pragma solidity 0.8.17;

import "@tetu_io/tetu-converter/contracts/interfaces/ITetuConverter.sol";

/// @notice Allow to share declaration of ConverterStrategyBaseState with libraries
interface IConverterStrategyBase {
  struct ConverterStrategyBaseState {
    /// @dev Amount of underlying assets invested to the pool.
    uint investedAssets;

    /// @dev Linked Tetu Converter
    ITetuConverter converter;

    /// @notice Percent of asset amount that can be not invested, it's allowed to just keep it on balance
    ///         decimals = {DENOMINATOR}
    /// @dev We need this threshold to avoid numerous conversions of small amounts
    uint reinvestThresholdPercent;

    /// @notice Current debt to the insurance.
    ///         It's increased when insurance covers any losses related to swapping and borrow-debts-paying.
    ///         It's not changed when insurance covers losses/receives profit that appeared after price changing.
    ///         The strategy covers this debt on each hardwork using the profit (rewards, fees)
    int debtToInsurance;

    /// @notice reserve space for future needs
    uint[50-1] __gap;
  }
}","keccak256":"0x0be4f2ba25d955dfa6c9f821ecb466c3ae78f025ad2a85d83d11e22d850047ea","license":"MIT"},"contracts/interfaces/IPairBasedDefaultStateProvider.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

/// @notice Provides access to getDefaultState() of a pair-based strategy
interface IPairBasedDefaultStateProvider {
  /// @notice Returns the current state of the contract
  /// @return addr [tokenA, tokenB, pool, profitHolder]
  /// @return tickData [tickSpacing, lowerTick, upperTick, rebalanceTickRange]
  /// @return nums [totalLiquidity, fuse-status-tokenA, fuse-status-tokenB, withdrawDone, 4 thresholds of token A, 4 thresholds of token B]
  /// @return boolValues [isStablePool, depositorSwapTokens]
  function getDefaultState() external view returns (
    address[] memory addr,
    int24[] memory tickData,
    uint[] memory nums,
    bool[] memory boolValues
  );
}","keccak256":"0x883b0f9e463485a57aa1baea9aafef64180362d336114a53f6cb8b7a94303d70","license":"MIT"},"contracts/interfaces/IPairBasedStrategyReaderAccess.sol":{"content":"// SPDX-License-Identifier: MIT
