dress,address,uint256,uint256)":{"params":{"amountIn_":"Meaning depends on {entryData_}.","entryData_":"Encoded entry kind and additional params if necessary (set of params depends on the kind)                   See TetuConverter\EntryKinds.sol\ENTRY_KIND_XXX constants for possible entry kinds                   0 or empty: Amount of collateral {amountIn_} is fixed, amount of borrow should be max possible."}},"prepareRewardsList(ITetuConverter,address[],address[],uint256[],mapping(address => uint256) storage)":{"details":"The post-processing is rewards conversion to the main asset","params":{"amountsOut":"Amounts of available rewards","rewardTokens_":"Amounts of rewards claimed from the internal pool","tokensOut":"List of available rewards - not zero amounts, reward tokens don't repeat","tokens_":"tokens received from {_depositorPoolAssets}"}},"recycle(address,uint256,address[],ITetuLiquidator,mapping(address => uint256) storage,mapping(address => uint256) storage,address[],uint256[])":{"params":{"baseAmounts_":"Base amounts for rewards tokens                     The base amounts allow to separate just received and previously received rewards.","liquidationThresholds_":"Liquidation thresholds for rewards tokens","rewardAmounts_":"Amounts of {rewardTokens_}; we assume, there are no zero amounts here","rewardTokens_":"Full list of reward tokens received from tetuConverter and depositor","tokens_":"tokens received from {_depositorPoolAssets}"},"returns":{"amountsToForward":"Amounts to be sent to forwarder","receivedAmounts":"Received amounts of the tokens         This array has +1 item at the end: received amount of the main asset                                            there was no possibility to use separate var for it, stack too deep","spentAmounts":"Spent amounts of the tokens"}},"swapToGivenAmount(uint256,address[],uint256,address,uint256[],ITetuConverter,ITetuLiquidator,uint256,uint256,mapping(address => uint256) storage)":{"params":{"indexTargetAsset_":"Index of target asset in tokens_ array","liquidationThresholdForTargetAsset_":"Liquidation thresholds for the target asset","overswap_":"Allow to swap more then required (i.e. 1_000 => +1%)                  to avoid additional swap if the swap return amount a bit less than we expected","targetAmount_":"Required amount of tokens[indexTheAsset_] that should be received by swap(s)","tokens_":"tokens received from {_depositorPoolAssets}","underlying_":"Index of underlying","withdrawnAmounts_":"Amounts withdrawn from the pool"},"returns":{"spentAmounts":"Any amounts spent during the swaps","withdrawnAmountsOut":"withdrawnAmounts + any amounts received during the swaps"}}},"version":1},"userdoc":{"events":{"ClosePosition(address,address,uint256,address,uint256,uint256)":{"notice":"Some borrow(s) was/were repaid"},"Liquidation(address,address,uint256,uint256,uint256)":{"notice":"A liquidation was made"},"OpenPosition(address,address,uint256,address,uint256,address)":{"notice":"A borrow was made"}},"kind":"user","methods":{"calcInvestedAssets(address[],uint256[],uint256,ITetuConverter,mapping(address => uint256) storage)":{"notice":"Calculate amount we will receive when we withdraw all from pool"},"closePosition(ITetuConverter,address,address,uint256)":{"notice":"Close the given position, pay {amountToRepay}, return collateral amount in result"},"convertAfterWithdraw(ITetuConverter,ITetuLiquidator,uint256,address[],uint256,uint256[])":{"notice":"Convert {p.amountsToConvert_} to the main asset"},"getAssetPriceFromConverter(ITetuConverter,address)":{"notice":"OTHER HELPERS"},"getCollaterals(uint256,address[],uint256[],uint256,uint256,IPriceOracle,mapping(address => uint256) storage)":{"notice":"For each {token_} calculate a part of {amount_} to be used as collateral according to the weights.         I.e. we have 300 USDC, we need to split it on 100 USDC, 100 USDT, 100 DAI         USDC is main asset, USDT and DAI should be borrowed. We check amounts of USDT and DAI on the balance         and return collaterals reduced on that amounts. For main asset, we return full amount always (100 USDC)."},"getLiquidityAmountRatio(uint256,mapping(address => uint256) storage,address,address[],uint256,ITetuConverter,uint256,uint256)":{"notice":"Get a ratio to calculate amount of liquidity that should be withdrawn from the pool to get {targetAmount_}               liquidityAmount = _depositorLiquidity() * {liquidityRatioOut} / 1e18         User needs to withdraw {targetAmount_} in main asset.         There are two kinds of available liquidity:         1) liquidity in the pool - {depositorLiquidity_}         2) Converted amounts on balance of the strategy - {baseAmounts_}         To withdraw {targetAmount_} we need         1) Reconvert converted amounts back to main asset         2) IF result amount is not necessary - withdraw some liquidity from the pool            and also convert it to the main asset."},"getTokenAmounts(ITetuConverter,address[],uint256,uint256[],uint256,mapping(address => uint256) storage)":{"notice":"Make borrow and save amounts of tokens available for deposit to tokenAmounts"},"liquidate(ITetuLiquidator,address,address,uint256,uint256,uint256)":{"notice":"Make liquidation if estimated amountOut exceeds the given threshold"},"openPosition(ITetuConverter,bytes,address,address,uint256,uint256)":{"notice":"Make one or several borrow necessary to supply/borrow required {amountIn_} according to {entryData_}         Max possible collateral should be approved before calling of this function."},"postWithdrawActions(uint256[],uint256,uint256,uint256,uint256[],address[],uint256,ITetuConverter,uint256,uint256[])":{"notice":"WITHDRAW HELPERS"},"prepareRewardsList(ITetuConverter,address[],address[],uint256[],mapping(address => uint256) storage)":{"notice":"Claim rewards from tetuConverter, generate result list of all available rewards and airdrops"},"recycle(address,uint256,address[],ITetuLiquidator,mapping(address => uint256) storage,mapping(address => uint256) storage,address[],uint256[])":{"notice":"Recycle the amounts: liquidate a part of each amount, send the other part to the forwarder. We have two kinds of rewards: 1) rewards in depositor's assets (the assets returned by _depositorPoolAssets) 2) any other rewards All received rewards are immediately "recycled". It means, they are divided on two parts: to forwarder, to compound   Compound-part of Rewards-2 can be liquidated   Compound part of Rewards-1 should be just added to baseAmounts All forwarder-parts are returned in amountsToForward and should be transferred to the forwarder."},"swapToGivenAmount(uint256,address[],uint256,address,uint256[],ITetuConverter,ITetuLiquidator,uint256,uint256,mapping(address => uint256) storage)":{"notice":"Swap available {amounts_} of {tokens_} to receive {targetAmount_} of {tokens[indexTheAsset_]}"}},"version":1}},"settings":{"compilationTarget":{"contracts/strategies/ConverterStrategyBaseLib.sol":"ConverterStrategyBaseLib"},"evmVersion":"london","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":150},"remappings":[]},"sources":{"@tetu_io/tetu-contracts-v2/contracts/interfaces/IController.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

interface IController {

  // --- DEPENDENCY ADDRESSES
  function governance() external view returns (address);

  function voter() external view returns (address);

  function liquidator() external view returns (address);

  function forwarder() external view returns (address);

  function investFund() external view returns (address);

  function veDistributor() external view returns (address);

  function platformVoter() external view returns (address);

  // --- VAULTS

  function vaults(uint id) external view returns (address);

  function vaultsList() external view returns (address[] memory);

  function vaultsListLength() external view returns (uint);

  function isValidVault(address _vault) external view returns (bool);

  // --- restrictions

  function isOperator(address _adr) external view returns (bool);


}
