pragma solidity 0.8.17;

/// @notice List of all errors generated by the application
///         Each error should have unique code TS-XXX and descriptive comment
library AppErrors {
  /// @notice Provided address should be not zero
  string public constant ZERO_ADDRESS = "TS-1 zero address";

  /// @notice A pair of the tokens cannot be found in the factory of uniswap pairs
  string public constant UNISWAP_PAIR_NOT_FOUND = "TS-2 pair not found";

  /// @notice Lengths not matched
  string public constant WRONG_LENGTHS = "TS-4 wrong lengths";

  /// @notice Unexpected zero balance
  string public constant ZERO_BALANCE = "TS-5 zero balance";

  string public constant ITEM_NOT_FOUND = "TS-6 not found";

  string public constant NOT_ENOUGH_BALANCE = "TS-7 not enough balance";

  /// @notice Price oracle returns zero price
  string public constant ZERO_PRICE = "TS-8 zero price";

  string public constant WRONG_VALUE = "TS-9 wrong value";

  /// @notice TetuConvertor wasn't able to make borrow, i.e. borrow-strategy wasn't found
  string public constant ZERO_AMOUNT_BORROWED = "TS-10 zero borrowed amount";

  string public constant WITHDRAW_TOO_MUCH = "TS-11 try to withdraw too much";

  string public constant UNKNOWN_ENTRY_KIND = "TS-12 unknown entry kind";

  string public constant ONLY_TETU_CONVERTER = "TS-13 only TetuConverter";

  string public constant WRONG_ASSET = "TS-14 wrong asset";

  string public constant NO_LIQUIDATION_ROUTE = "TS-15 No liquidation route";

  string public constant PRICE_IMPACT = "TS-16 price impact";

  /// @notice tetuConverter_.repay makes swap internally. It's not efficient and not allowed
  string public constant REPAY_MAKES_SWAP = "TS-17 can not convert back";

  string public constant NO_INVESTMENTS = "TS-18 no investments";

  string public constant INCORRECT_LENGTHS = "TS-19 lengths";

  /// @notice We expect increasing of the balance, but it was decreased
  string public constant BALANCE_DECREASE = "TS-20 balance decrease";

  /// @notice Prices changed and invested assets amount was increased on S, value of S is too high
  string public constant EARNED_AMOUNT_TOO_HIGH = "TS-21 earned too high";

  string public constant GOVERNANCE_ONLY = "TS-22 governance only";

  string public constant ZERO_VALUE = "TS-24 zero value";

  string public constant INCORRECT_SWAP_BY_AGG_PARAM = "TS-25 swap by agg";

  string public constant OVER_COLLATERAL_DETECTED = "TS-27 over-collateral";

  string public constant NOT_IMPLEMENTED = "TS-28 not implemented";

  /// @notice You are not allowed to make direct debt if a NOT-DUST reverse debt exists and visa verse.
  string public constant OPPOSITE_DEBT_EXISTS = "TS-29 opposite debt exists";

  string public constant INVALID_VALUE = "TS-30 invalid value";

  string public constant TOO_HIGH = "TS-32 too high value";

  /// @notice BorrowLib has recursive call, sub-calls are not allowed
  ///         This error can happen if allowed proportion is too small, i.e. 0.0004 : (1-0.0004)
  ///         Such situation can happen if amount to swap is almost equal to the amount of the token in the current tick,
  ///         so swap will move us close to the border between ticks.
  ///         It was decided, that it's ok to have revert in that case
  ///         We can change this behavior by changing BorrowLib.rebalanceRepayBorrow implementation:
  ///             if amount-to-repay passed to _repayDebt is too small to be used,
  ///             we should increase it min amount required to make repay successfully (amount must be > threshold)
  ///         Previously it was error NOT_ALLOWED = "TS23: not allowed", see issues SCB-777, SCB-818
  string public constant TOO_DEEP_RECURSION_BORROW_LIB = "TS-33 too deep recursion";
}
","keccak256":"0x1400c631697434c991de2bfadcac7a0164a87be41a2cb683ed7f4fc75798d3e8","license":"BUSL-1.1"},"contracts/libs/AppLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "@tetu_io/tetu-contracts-v2/contracts/interfaces/IERC20.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/IERC20Metadata.sol";
import "@tetu_io/tetu-contracts-v2/contracts/openzeppelin/SafeERC20.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/IPriceOracle.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/ITetuLiquidator.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/ITetuConverter.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/IConverterController.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/IController.sol";

/// @notice Common internal utils
library AppLib {
  using SafeERC20 for IERC20;

  /// @notice 1% gap to cover possible liquidation inefficiency
  /// @dev We assume that: conversion-result-calculated-by-prices - liquidation-result <= the-gap
  uint internal constant GAP_CONVERSION = 1_000;
  /// @dev Absolute value for any token
  uint internal constant DEFAULT_LIQUIDATION_THRESHOLD = 100_000;
  uint internal constant DENOMINATOR = 100_000;

  /// @notice Any amount less than the following is dust
  uint public constant DUST_AMOUNT_TOKENS = 100;

  /// @notice Unchecked increment for for-cycles
  function uncheckedInc(uint i) internal pure returns (uint) {
    unchecked {
      return i + 1;
    }
  }

  /// @notice Make infinite approve of {token} to {spender} if the approved amount is less than {amount}
  /// @dev Should NOT be used for third-party pools
  function approveIfNeeded(address token, uint amount, address spender) internal {
    if (IERC20(token).allowance(address(this), spender) < amount) {
      IERC20(token).safeApprove(spender, 0);
      // infinite approve, 2*255 is more gas efficient then type(uint).max
      IERC20(token).safeApprove(spender, 2 ** 255);
    }
  }

  function balance(address token) internal view returns (uint) {
    return IERC20(token).balanceOf(address(this));
  }

  /// @return prices Asset prices in USD, decimals 18
  /// @return decs 10**decimals
  function _getPricesAndDecs(IPriceOracle priceOracle, address[] memory tokens_, uint len) internal view returns (
    uint[] memory prices,
    uint[] memory decs
  ) {
    prices = new uint[](len);
    decs = new uint[](len);
    {
      for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
        decs[i] = 10 ** IERC20Metadata(tokens_[i]).decimals();
        prices[i] = priceOracle.getAssetPrice(tokens_[i]);
      }
    }
  }

  /// @notice Find index of the given {asset_} in array {tokens_}, return type(uint).max if not found
  function getAssetIndex(address[] memory tokens_, address asset_) internal pure returns (uint) {
    uint len = tokens_.length;
    for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
      if (tokens_[i] == asset_) {
        return i;
      }
    }
    return type(uint).max;
  }

  function _getLiquidator(address controller_) internal view returns (ITetuLiquidator) {
    return ITetuLiquidator(IController(controller_).liquidator());
  }

  function _getPriceOracle(ITetuConverter converter_) internal view returns (IPriceOracle) {
    return IPriceOracle(IConverterController(converter_.controller()).priceOracle());
  }

  /// @notice Calculate liquidation threshold, use default value if the threshold is not set
  ///         It's allowed to set any not-zero threshold, it this case default value is not used
  /// @dev This function should be applied to the threshold at the moment of the reading its value from the storage.
  ///      So, if we pass {mapping(address => uint) storage liquidationThresholds}, the threshold can be zero
  ///      bug if we pass {uint liquidationThreshold} to a function, the threshold should be not zero
  function _getLiquidationThreshold(uint threshold) internal pure returns (uint) {
    return threshold == 0
      ? AppLib.DEFAULT_LIQUIDATION_THRESHOLD
      : threshold;
  }

  /// @notice Return a-b OR zero if a < b
  function sub0(uint a, uint b) internal pure returns (uint) {
    return a > b ? a - b : 0;
  }
}
","keccak256":"0x9ed14dbb815e8db52864ff1002f877e3b496c9dcb2ac1dccc220c510da203022","license":"BUSL-1.1"},"contracts/libs/BorrowLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "@tetu_io/tetu-converter/contracts/interfaces/ITetuConverter.sol";
import "../strategies/ConverterStrategyBaseLib.sol";

/// @notice Library to make new borrow, extend/reduce exist borrows and repay to keep proper assets proportions
/// @dev Swap through liquidator is still allowed to be able to get required profitToCover, but this amount is small
library BorrowLib {
  /// @notice prop0 + prop1
  uint constant public SUM_PROPORTIONS = 1e18;

  /// @notice Function {_rebalanceAssets} cannot be called recursively more than twice.
  /// Normally one call is enough.
  /// Firstly repay(requiredAmount0) is called below. There are two possible results:
  /// 1) requiredCost0 <= cost0
  /// 2) v.directDebt == 0
  /// There is SCB-818: there are two debts (big and small), on the first cycle we get amount less than expected
  /// because of debt gap. So, we need second cycle.
  uint constant public MAX_DEEP_RECURSION = 2;

  //region -------------------------------------------------- Data types
  struct PricesDecs {
    /// @notice Asset prices in USD, decimals 18
    uint[] prices;
    /// @notice decs 10**decimals
    uint[] decs;
  }

  struct ConverterLiquidator {
    ITetuConverter converter;
    ITetuLiquidator liquidator;
  }

  struct RebalanceAssetsLocal {
    // ------- constant values
    address asset0;
    address asset1;
    /// @notice Proportion of {asset0}, > 0; proportion of {asset1} is SUM_PROPORTIONS - prop0
    uint prop0;
    /// @notice Min allowed amount of {asset0}-collateral, 0 - use default min value
    uint threshold0;
    /// @ntoice Min allowed amount of {asset1}-collateral, 0 - use default min value
    uint threshold1;

    PricesDecs pd;
    // ------- refreshable values

    // @notice Current balance of {asset0}
    uint amount0;
    // @notice Current balance of {asset1}
    uint amount1;

    /// @notice Borrowed amount of not-underlying
    uint directDebt;
    /// @notice Borrowed amount of underlying
    uint reverseDebt;

    uint addition0;
  }

  /// @notice Params required to borrow {assetB} under {assetA}
  struct RebalanceAssetsCore {
    ConverterLiquidator converterLiquidator;
    address assetA;
    address assetB;
    uint propA;
    uint propB;
    /// @notice {assetA} to {assetB} ratio; {amountB} * {alpha} => {amountA}, decimals 18
    uint alpha18;
    /// @notice Min allowed amount of {assetA}-collateral, 0 - use default min value
    uint thresholdA;

    uint addonA;
    uint addonB;

    /// @notice Index of {assetA} in {prices} and {decs}
    uint indexA;
    /// @notice Index of {assetB} in {prices} and {decs}
    uint indexB;
  }

  struct OpenPosition2Local {
    uint collateral;
    uint toBorrow;
    uint cc;
    uint cb;
    uint c0;
    uint cb2;
    uint ca0;
    uint gamma18;
    uint pa2;
    uint pb2;
    bytes entryData;
    uint alpha18;
  }

  struct MakeBorrowToDepositLocal {
    uint[] prices;
    uint[] decs;
    uint cost0;
    uint cost1;
    uint prop1;
    bytes entryData;
  }
  //endregion -------------------------------------------------- Data types

  //region -------------------------------------------------- External functions
  /// @notice Set balances of {asset0} and {asset1} in proportions {prop0}:{prop1} using borrow/repay (no swaps)
  /// @param prop0 Proportion of {asset0}, > 0. Proportion of {asset1} is calculates as 1e18 - prop0
  /// @param threshold0 Min allowed amount of {asset0}-collateral, 0 - use default min value
  /// @param threshold1 Min allowed amount of {asset1}-collateral, 0 - use default min value
  /// @param addition0 Additional amount A0 of {asset0}.
  ///                  Balance0 = A0 + B0
  ///                  We need following balances in results: B0 : Balance1 === {proportion}:{100_000-proportion}
  function rebalanceAssets(
    ITetuConverter converter_,
    ITetuLiquidator liquidator_,
    address asset0,
    address asset1,
    uint prop0,
    uint threshold0,
    uint threshold1,
    uint addition0
  ) external {
    // pool always have TWO assets, it's not allowed ot have only one asset
    // so, we assume that the proportions are in the range (0...1e18)
    require(prop0 != 0, AppErrors.ZERO_VALUE);
    require(prop0 < SUM_PROPORTIONS, AppErrors.TOO_HIGH);

    RebalanceAssetsLocal memory v;
    v.asset0 = asset0;
    v.asset1 = asset1;
    v.prop0 = prop0;
    v.threshold0 = threshold0;
    v.threshold1 = threshold1;
    v.addition0 = addition0;

    IPriceOracle priceOracle = AppLib._getPriceOracle(converter_);
    address[] memory tokens = new address[](2);
    tokens[0] = asset0;
    tokens[1] = asset1;
    (v.pd.prices, v.pd.decs) = AppLib._getPricesAndDecs(priceOracle, tokens, 2);

    _refreshRebalance(v, ConverterLiquidator(converter_, liquidator_), MAX_DEEP_RECURSION);
  }

  /// @notice Convert {amount_} of underlying to two amounts: A0 (underlying) and A1 (not-underlying)
  ///         Result proportions of A0 and A1 should match to {prop0} : 1e18-{prop0}
  ///         The function is able to make new borrowing and/or close exist debts.
  /// @param amount_ Amount of underlying that is going to be deposited
  ///                We assume here, that current balance >= the {amount_}
  /// @param tokens_ [Underlying, not underlying]
  /// @param thresholds_ Thresholds for the given {tokens_}. Debts with amount-to-repay < threshold are ignored.
  /// @param prop0 Required proportion of underlying, > 0. Proportion of not-underlying is calculates as 1e18 - {prop0}
  /// @return tokenAmounts Result amounts [A0 (underlying), A1 (not-underlying)]
  function prepareToDeposit(
    ITetuConverter converter_,
    uint amount_,
    address[2] memory tokens_,
    uint[2] memory thresholds_,
    uint prop0
  ) external returns (
    uint[] memory tokenAmounts
  ) {
    uint[2] memory amountsToDeposit;
    uint[2] memory balances = [
      AppLib.sub0(AppLib.balance(tokens_[0]), amount_), // We assume here, that current balance >= the {amount_}
      AppLib.balance(tokens_[1])
    ];

    // we assume here, that either direct OR reverse debts (amount > threshold) are possible but not both at the same time
    (uint debtReverse, ) = converter_.getDebtAmountCurrent(address(this), tokens_[1], tokens_[0], true);
    if (debtReverse > thresholds_[0]) {
      // case 1: reverse debt exists
      // case 1.1: amount to deposit exceeds exist debt.
      //    Close the debt completely and than make either new direct OR reverse debt
      // case 1.2: amount to deposit is less than the exist debt.
      //    Close the debt partially and make new reverse debt
      uint amountToRepay = amount_ > debtReverse ? debtReverse : amount_;
      ConverterStrategyBaseLib.closePosition(converter_, tokens_[1], tokens_[0], amountToRepay);
      amountsToDeposit = [
        AppLib.sub0(AppLib.balance(tokens_[0]), balances[0]),
        AppLib.sub0(AppLib.balance(tokens_[1]), balances[1])
      ];
    } else {
      // case 2: no debts OR direct debt exists
      amountsToDeposit = [amount_, 0];
    }

    _makeBorrowToDeposit(converter_, amountsToDeposit, tokens_, thresholds_, prop0);

    tokenAmounts = new uint[](2);
    tokenAmounts[0] = AppLib.sub0(AppLib.balance(tokens_[0]), balances[0]);
    tokenAmounts[1] = AppLib.sub0(AppLib.balance(tokens_[1]), balances[1]);
  }
  //endregion -------------------------------------------------- External functions

  //region -------------------------------------------------- Implementation of prepareToDeposit
  /// @notice Make a direct or reverse borrow to make amounts_ fit to the given proportions.
  /// If one of available amounts is zero, we just need to make a borrow using second amount as amountIn.
  /// Otherwise, we need to calculate amountIn at first.
  /// @dev The purpose is to get the amounts in proper proportions: A:B = prop0:prop1.
  /// Suppose, amounts_[1] is not enough:
  ///   [A1, B1] => [A2 + A3, B1], A2:B1 = prop0:prop1, A3 is amountIn for new borrow.
  /// Suppose, amounts_[0] is not enough:
  ///   [A1, B1] => [A1, B2 + B3], A1:B2 = prop0:prop1, B3 is amountIn for new borrow.
  /// @param amounts_ Available amounts
  /// @param tokens_ [Underlying, not underlying]
  /// @param thresholds_ Thresholds for the given {tokens_}. Debts with amount-to-repay < threshold are ignored.
  /// @param prop0 Required proportion of underlying, > 0. Proportion of not-underlying is calculates as 1e18 - {prop0}
  function _makeBorrowToDeposit(
    ITetuConverter converter_,
    uint[2] memory amounts_,
    address[2] memory tokens_,
    uint[2] memory thresholds_,
    uint prop0
  ) internal {
    MakeBorrowToDepositLocal memory v;

    {
      IPriceOracle priceOracle = AppLib._getPriceOracle(converter_);
      address[] memory tokens = new address[](2);
      tokens[0] = tokens_[0];
      tokens[1] = tokens_[1];
      (v.prices, v.decs) = AppLib._getPricesAndDecs(priceOracle, tokens, 2);
    }

    v.cost0 = amounts_[0] * v.prices[0] / v.decs[0];
    v.cost1 = amounts_[1] * v.prices[1] / v.decs[1];
    // we need: cost0/cost1 = prop0/prop1, and so cost0 * prop1 = cost1 * prop0
    v.prop1 = SUM_PROPORTIONS - prop0;

    if (v.cost0 * v.prop1 > v.cost1 * prop0) {
      // we need to make direct borrow
      uint cost0for1 = v.cost1 * prop0 / v.prop1; // a part of cost0 that is matched to cost1
      uint amountIn = (v.cost0 - cost0for1) * v.decs[0] / v.prices[0];

      AppLib.approveIfNeeded(tokens_[0], amountIn, address(converter_));
      v.entryData = abi.encode(1, prop0, v.prop1); // ENTRY_KIND_EXACT_PROPORTION_1
      ConverterStrategyBaseLib.openPosition(converter_, v.entryData, tokens_[0], tokens_[1], amountIn, thresholds_[0]);
    } else if (v.cost0 * v.prop1 < v.cost1 * prop0) {
      // we need to make reverse borrow
      uint cost1for0 = v.cost0 * v.prop1 / prop0; // a part of cost1 that is matched to cost0
      uint amountIn = (v.cost1 - cost1for0) * v.decs[1] / v.prices[1];

      AppLib.approveIfNeeded(tokens_[1], amountIn, address(converter_));
      v.entryData = abi.encode(1, v.prop1, prop0); // ENTRY_KIND_EXACT_PROPORTION_1
      ConverterStrategyBaseLib.openPosition(converter_, v.entryData, tokens_[1], tokens_[0], amountIn, thresholds_[1]);
    }
  }

  //endregion -------------------------------------------------- Implementation of prepareToDeposit

  //region -------------------------------------------------- Internal helper functions

  /// @notice refresh state in {v} and call _rebalanceAssets()
  function _refreshRebalance(
    RebalanceAssetsLocal memory v,
    ConverterLiquidator memory converterLiquidator,
    uint repayAllowed
  ) internal {
    v.amount0 = IERC20(v.asset0).balanceOf(address(this));
    v.amount1 = IERC20(v.asset1).balanceOf(address(this));

    (v.directDebt, ) = converterLiquidator.converter.getDebtAmountCurrent(address(this), v.asset0, v.asset1, true);
    (v.reverseDebt, ) = converterLiquidator.converter.getDebtAmountCurrent(address(this), v.asset1, v.asset0, true);

    _rebalanceAssets(v, converterLiquidator, repayAllowed);
  }

  /// @param repayAllowed Protection against recursion
  ///                     Assets can be rebalanced in two ways:
  ///                     1) openPosition
  ///                     2) repay + openPosition
  ///                     Only one repay is allowed.
  function _rebalanceAssets(
    RebalanceAssetsLocal memory v,
    ConverterLiquidator memory converterLiquidator,
    uint repayAllowed
  ) internal {
    uint cost0 = v.amount0 * v.pd.prices[0] / v.pd.decs[0];
    uint cost1 = v.amount1 * v.pd.prices[1] / v.pd.decs[1];
    uint costAddition0 = v.addition0 * v.pd.prices[0] / v.pd.decs[0];

    if (cost0 + cost1 > costAddition0) {
      uint totalCost = cost0 + cost1 - costAddition0;

      uint requiredCost0 = totalCost * v.prop0 / SUM_PROPORTIONS + costAddition0;
      uint requiredCost1 = totalCost * (SUM_PROPORTIONS - v.prop0) / SUM_PROPORTIONS;

      if (requiredCost0 > cost0) {
        // we need to increase amount of asset 0 and decrease amount of asset 1, so we need to borrow asset 0 (reverse)
        RebalanceAssetsCore memory c10 = RebalanceAssetsCore({
          converterLiquidator: converterLiquidator,
          assetA: v.asset1,
          assetB: v.asset0,
          propA: SUM_PROPORTIONS - v.prop0,
          propB: v.prop0,
          alpha18: 1e18 * v.pd.prices[0] * v.pd.decs[1] / v.pd.prices[1] / v.pd.decs[0],
          thresholdA: v.threshold1,
          addonA: 0,
          addonB: v.addition0,
          indexA: 1,
          indexB: 0
        });

        if (v.directDebt >= AppLib.DUST_AMOUNT_TOKENS) {
          require(repayAllowed != 0, AppErrors.TOO_DEEP_RECURSION_BORROW_LIB);

          // repay of v.asset1 is required
          uint requiredAmount0 = (requiredCost0 - cost0) * v.pd.decs[0] / v.pd.prices[0];
          rebalanceRepayBorrow(v, c10, requiredAmount0, v.directDebt, repayAllowed);
        } else {
          // new (or additional) borrow of asset 0 under asset 1 is required
          openPosition(c10, v.pd, v.amount1, v.amount0);
        }
      } else if (requiredCost0 < cost0) {
        RebalanceAssetsCore memory c01 = RebalanceAssetsCore({
          converterLiquidator: converterLiquidator,
          assetA: v.asset0,
          assetB: v.asset1,
          propA: v.prop0,
          propB: SUM_PROPORTIONS - v.prop0,
          alpha18: 1e18 * v.pd.prices[1] * v.pd.decs[0] / v.pd.prices[0] / v.pd.decs[1],
          thresholdA: v.threshold0,
          addonA: v.addition0,
          addonB: 0,
          indexA: 0,
          indexB: 1
        });
        // we need to decrease amount of asset 0 and increase amount of asset 1, so we need to borrow asset 1 (direct)
        if (v.reverseDebt >= AppLib.DUST_AMOUNT_TOKENS) {
          require(repayAllowed != 0, AppErrors.TOO_DEEP_RECURSION_BORROW_LIB);

          // repay of v.asset0 is required
          // requiredCost0 < cost0 => requiredCost1 > cost1
          uint requiredAmount1 = (requiredCost1 - cost1) * v.pd.decs[1] / v.pd.prices[1];
          rebalanceRepayBorrow(v, c01, requiredAmount1, v.reverseDebt, repayAllowed);
        } else {
          // new or additional borrow of asset 1 under asset 0 is required
          openPosition(c01, v.pd, v.amount0, v.amount1);
        }
      }
    } else {
      // if costAddition0 exceeds cost0 + cost1, all amounts should be converted to asset 0
      // for simplicity, we don't make any swaps or borrows (amount addition0 is assumed to be small)
      // and just leave balances as is
      // as result, profit-to-cover will be reduced from costAddition0 to v.amount0
    }
  }

  /// @notice Repay {amountDebtA} fully or partially to get at least {requiredAmountB} of collateral
  ///         then try to rebalance once more
  /// @param requiredAmountB Amount of collateral that we need to receive after repay
  /// @param amountDebtA Total amount that is required to pay to close the debt
  function rebalanceRepayBorrow(
    RebalanceAssetsLocal memory v,
    RebalanceAssetsCore memory c,
    uint requiredAmountB,
    uint amountDebtA,
    uint repayAllowed
  ) internal {
    // repayAllowed cannot be zero here because of requires in _rebalanceAssets, but it's safer to check it once more
    require(repayAllowed != 0, AppErrors.TOO_DEEP_RECURSION_BORROW_LIB);

    // we need to get {requiredAmountB}
    // we don't know exact amount to repay
    // but we are sure that amount {requiredAmountB ===> requiredAmountA} would be more than required
    uint capRequiredAmountA = requiredAmountB * c.alpha18 / 1e18;
    uint amountToRepay = Math.min(capRequiredAmountA, amountDebtA);
    if (amountToRepay >= AppLib.DUST_AMOUNT_TOKENS) {
      ConverterStrategyBaseLib._repayDebt(c.converterLiquidator.converter, c.assetB, c.assetA, amountToRepay);
      _refreshRebalance(v, c.converterLiquidator, repayAllowed - 1);
    } // else the assets are already in proper proportions
  }

  //endregion -------------------------------------------------- Internal helper functions

  //region -------------------------------------------------- Open position
  /// @notice borrow asset B under asset A. Result balances should be A0 + A1, B0 + B1
  ///         Where (A1 : B1) == (propA : propB), A0 and B0 are equal to {c.addonA} and {c.addonB}
  /// @param balanceA_ Current balance of the collateral
  /// @param balanceB_ Current balance of the borrow asset
  function openPosition(
    RebalanceAssetsCore memory c,
    PricesDecs memory pd,
    uint balanceA_,
    uint balanceB_
  ) internal returns (
    uint collateralAmountOut,
    uint borrowedAmountOut
  ) {
    // if there are two not-zero addons, the caller should reduce balances before the call
    require(c.addonA == 0 || c.addonB == 0, AppErrors.INVALID_VALUE);

    // we are going to borrow B under A
    if (c.addonB != 0) {
      // B is underlying, so we are going to borrow underlying
      if (balanceB_ >= c.addonB) {
        // simple case - we already have required addon on the balance. Just keep it unused
        return _openPosition(c, balanceA_, balanceB_ - c.addonB);
      } else {
        // we need to get 1) (c.addonB + balanceB_) amount, so we will have required c.addonB
        //                2) leftovers of A and B should be allocated in required proportions
        // it's too hard to calculate correctly required to borrow amount in this case without changing TetuConverter
        // but we can assume here, that amount (c.addonB - balanceB_) is pretty small (it's profitToCover)
        // so, we can swap this required amount through liquidator at first
        // then use _openPosition to re-allocated rest amounts to proper proportions
        (uint decA,) = _makeLittleSwap(c, pd, balanceA_, c.addonB - balanceB_);
        return _openPosition(c, balanceA_ - decA, balanceB_);
      }
    } else if (c.addonA != 0) {
      // A is underlying, we need to put aside c.addonA and allocate leftovers in right proportions.
      // we are going to borrow B under asset A, so the case (balanceA_ < c.addonA) is not valid here
      require(balanceA_ >= c.addonA, AppErrors.NOT_ENOUGH_BALANCE);
      return _openPosition(c, balanceA_ - c.addonA, balanceB_);
    } else {
      // simple logic, no addons
      return _openPosition(c, balanceA_, balanceB_);
    }
  }

  /// @notice borrow asset B under asset A, result balances should have proportions: (propA : propB)
  function _openPosition(RebalanceAssetsCore memory c, uint balanceA_, uint balanceB_) internal returns (
    uint collateralAmountOut,
    uint borrowedAmountOut
  ) {
    uint untouchedAmountA;
    bytes memory entryData = abi.encode(1, c.propA, c.propB);

    if (balanceB_ != 0) {
      // we are going to use {balanceA_} as collateral
      // but there is some amount on {balanceB_}, so we need to keep corresponded part of {balanceA_} untouched
      untouchedAmountA = balanceB_ * c.alpha18 * c.propA / c.propB / 1e18;

      // we are going to borrow B under A, so balance A must be greater then balance B
      // otherwise the function is called incorrectly - probably we need to borrow A under B
      require(untouchedAmountA <= balanceA_, AppErrors.WRONG_VALUE);
    }

    AppLib.approveIfNeeded(c.assetA, balanceA_ - untouchedAmountA, address(c.converterLiquidator.converter));

    return ConverterStrategyBaseLib.openPosition(
      c.converterLiquidator.converter,
      entryData,
      c.assetA,
      c.assetB,
      balanceA_ - untouchedAmountA,
      c.thresholdA
    );
  }

  //endregion -------------------------------------------------- Open position

  //region -------------------------------------------------- Little swap
  /// @notice Swap min amount of A to get {requiredAmountB}
  /// @return spentAmountIn how much the balance A has decreased
  /// @return receivedAmountOut how much the balance B has increased
  function _makeLittleSwap(
    RebalanceAssetsCore memory c,
    PricesDecs memory pd,
    uint balanceA_,
    uint requiredAmountB
  ) internal returns (
    uint spentAmountIn,
    uint receivedAmountOut
  ) {
    uint amountInA = requiredAmountB * pd.prices[c.indexB] * pd.decs[c.indexA] / pd.prices[c.indexA] / pd.decs[c.indexB];
    // we can have some loss because of slippage
    // so, let's increase input amount a bit
    amountInA = amountInA * (100_000 + ConverterStrategyBaseLib._ASSET_LIQUIDATION_SLIPPAGE) / 100_000;

    // in practice the addition is required to pay ProfitToCover
    // we assume, that total addition amount is small enough, much smaller then the total balance
    // otherwise something is wrong: we are going to pay ProfitToCover, but we don't have enough amount on the balances.
    require(balanceA_ > amountInA, AppErrors.NOT_ENOUGH_BALANCE);

    (spentAmountIn, receivedAmountOut) = ConverterStrategyBaseLib.liquidate(
      c.converterLiquidator.converter,
      c.converterLiquidator.liquidator,
      c.assetA,
      c.assetB,
      amountInA,
      ConverterStrategyBaseLib._ASSET_LIQUIDATION_SLIPPAGE,
      c.thresholdA,
      false
    );
  }

  //endregion -------------------------------------------------- Little swap

}
","keccak256":"0x5a94be3da8739c31b91b0e4c6ca7860e96d052ef2d1975b63983e33eed33a8a8","license":"BUSL-1.1"},"contracts/libs/ConverterEntryKinds.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

/// @notice Utils and constants related to entryKind param of ITetuConverter.findBorrowStrategy
library ConverterEntryKinds {
  /// @notice Amount of collateral is fixed. Amount of borrow should be max possible.
  uint constant public ENTRY_KIND_EXACT_COLLATERAL_IN_FOR_MAX_BORROW_OUT_0 = 0;

  /// @notice Split provided source amount S on two parts: C1 and C2 (C1 + C2 = S)
  ///         C2 should be used as collateral to make a borrow B.
  ///         Results amounts of C1 and B (both in terms of USD) must be in the given proportion
  uint constant public ENTRY_KIND_EXACT_PROPORTION_1 = 1;

  /// @notice Borrow given amount using min possible collateral
  uint constant public ENTRY_KIND_EXACT_BORROW_OUT_FOR_MIN_COLLATERAL_IN_2 = 2;

  /// @notice Decode entryData, extract first uint - entry kind
  ///         Valid values of entry kinds are given by ENTRY_KIND_XXX constants above
  function getEntryKind(bytes memory entryData_) internal pure returns (uint) {
    if (entryData_.length == 0) {
      return ENTRY_KIND_EXACT_COLLATERAL_IN_FOR_MAX_BORROW_OUT_0;
    }
    return abi.decode(entryData_, (uint));
  }
}
","keccak256":"0x4f4332c8be1be5fd85fef7c06795fc19957b35a4f2e3735fdd89c0906ddc923b","license":"BUSL-1.1"},"contracts/libs/IterationPlanLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "@tetu_io/tetu-contracts-v2/contracts/interfaces/IERC20.sol";
import "@tetu_io/tetu-contracts-v2/contracts/openzeppelin/Math.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/ITetuConverter.sol";
import "./AppErrors.sol";
import "./AppLib.sol";

/// @notice Support of withdraw iteration plans
library IterationPlanLib {

//region ------------------------------------------------ Constants
  /// @notice Swap collateral asset to get required amount-to-repay, then repay and get more collateral back.
  ///         It tries to minimizes count of repay-operations.
  ///         If there are no debts, swap leftovers to get required proportions of the asset.
  ///         This mode is intended i.e. for "withdraw all"
  ///         (uint256, uint256) - (entry kind, propNotUnderlying18)
  /// propNotUnderlying18 Required proportion of not-underlying for the final swap of leftovers, [0...1e18].
  ///                     The assets should be swapped to get following result proportions:
  ///                     not-underlying : underlying === propNotUnderlying18 : (1e18 - propNotUnderlying18)
  ///                     Pass type(uint).max to read proportions from the pool.
  uint constant public PLAN_SWAP_REPAY = 0;

  /// @notice Repay available amount-to-repay, swap all or part of collateral to borrowed-asset, make one repay if needed.
  ///         Swap + second repay tries to make asset balances to proportions required by the pool.
  ///         Proportions are read from pool through IPoolProportionsProvider(this) and re-read after swapping.
  ///         This mode is intended i.e. for rebalancing debts using single iteration.
  ///         (uint256, uint256, uint256) - (entry kind, propNotUnderlying18, required-amount-to-reduce-the-debt)
  /// propNotUnderlying18 Required proportion of not-underlying for the final swap of leftovers, [0...1e18].
  ///                     The assets should be swapped to get following result proportions:
  ///                     not-underlying : underlying === propNotUnderlying18 : (1e18 - propNotUnderlying18)
  ///                     Pass type(uint).max to read proportions from the pool.
  uint constant public PLAN_REPAY_SWAP_REPAY = 1;

  /// @notice Swap leftovers to required proportions, don't repay any debts
  ///         (uint256, uint256) - (entry kind, propNotUnderlying18)
  /// propNotUnderlying18 Required proportion of not-underlying for the final swap of leftovers, [0...1e18].
  ///                     The assets should be swapped to get following result proportions:
  ///                     not-underlying : underlying === propNotUnderlying18 : (1e18 - propNotUnderlying18)
  ///                     Pass type(uint).max to read proportions from the pool.
  uint constant public PLAN_SWAP_ONLY = 2;
//endregion ------------------------------------------------ Constants

//region ------------------------------------------------ Data types
  /// @notice Set of parameters required to liquidation through aggregators
  struct SwapRepayPlanParams {
    ITetuConverter converter;
    ITetuLiquidator liquidator;

    /// @notice Assets used by depositor stored as following way: [underlying, not-underlying]
    address[] tokens;

    /// @notice Liquidation thresholds for the {tokens}
    uint[] liquidationThresholds;

    /// @notice Cost of $1 in terms of the assets, decimals 18
    uint[] prices;
    /// @notice 10**decimal for the assets
    uint[] decs;

    /// @notice Amounts that will be received on balance before execution of the plan.
    uint[] balanceAdditions;

    /// @notice Plan kind extracted from entry data, see {IterationPlanKinds}
    uint planKind;

    /// @notice Required proportion of not-underlying for the final swap of leftovers, [0...1e18].
    ///         The leftovers should be swapped to get following result proportions of the assets:
    ///         not-underlying : underlying === propNotUnderlying18 : 1e18 - propNotUnderlying18
    uint propNotUnderlying18;

    /// @notice proportions should be taken from the pool and re-read from the pool after each swap
    bool usePoolProportions;

    /// @notice "required-amount-to-reduce-debt" in the case of REPAY-SWAP-REPAY, zero in other cases
    uint entryDataParam;
  }

  struct GetIterationPlanLocal {
    /// @notice Underlying balance
    uint assetBalance;
    /// @notice Not-underlying balance
    uint tokenBalance;

    uint totalDebt;
    uint totalCollateral;

    uint debtReverse;
    uint collateralReverse;

    address asset;
    address token;

    bool swapLeftoversNeeded;
  }

  struct EstimateSwapAmountForRepaySwapRepayLocal {
    uint x;
    uint y;
    uint bA1;
    uint bB1;
    uint alpha;
    uint swapRatio;
    uint aB3;
    uint cA1;
    uint cB1;
    uint aA2;
  }
//endregion ------------------------------------------------ Data types

  /// @notice Decode entryData, extract first uint - entry kind
  ///         Valid values of entry kinds are given by ENTRY_KIND_XXX constants above
  function getEntryKind(bytes memory entryData_) internal pure returns (uint) {
    if (entryData_.length == 0) {
      return PLAN_SWAP_REPAY;
    }
    return abi.decode(entryData_, (uint));
  }

//region ------------------------------------------------ Build plan
  /// @notice Build plan to make single iteration of withdraw according to the selected plan
  ///         The goal is to withdraw {requestedAmount} and receive {asset}:{token} in proper proportions on the balance
  /// @param converterLiquidator [TetuConverter, TetuLiquidator]
  /// @param tokens List of the pool tokens. One of them is underlying and one of then is not-underlying
  ///               that we are going to withdraw
  /// @param liquidationThresholds Liquidation thresholds for the {tokens}. If amount is less then the threshold,
  ///                              we cannot swap it.
  /// @param prices Prices of the {tokens}, decimals 18, [$/token]
  /// @param decs 10**decimal for each token of the {tokens}
  /// @param balanceAdditions Amounts that will be added to the current balances of the {tokens}
  ///                         to the moment of the plan execution
  /// @param packedData Several values packed to fixed-size array (to reduce number of params)
  ///    0: usePoolProportions: 1 - read proportions from the pool through IPoolProportionsProvider(this)
  ///    1: planKind: selected plan, one of PLAN_XXX
  ///    2: propNotUnderlying18: value of not-underlying proportion [0..1e18] if usePoolProportions == 0
  ///    3: requestedBalance: total amount that should be withdrawn, it can be type(uint).max
  ///    4: indexAsset: index of the underlying in {tokens} array
  ///    5: indexToken: index of the token in {tokens} array. We are going to withdraw the token and convert it to the asset
  ///    6: entryDataParam: required-amount-to-reduce-debt in REPAY-SWAP-REPAY case; zero in other cases
  function buildIterationPlan(
    address[2] memory converterLiquidator,
    address[] memory tokens,
    uint[] memory liquidationThresholds,
    uint[] memory prices,
    uint[] memory decs,
    uint[] memory balanceAdditions,
    uint[7] memory packedData
  ) external returns (
    uint indexToSwapPlus1,
    uint amountToSwap,
    uint indexToRepayPlus1
  ) {
    return _buildIterationPlan(
      SwapRepayPlanParams({
        converter: ITetuConverter(converterLiquidator[0]),
        liquidator: ITetuLiquidator(converterLiquidator[1]),
        tokens: tokens,
        liquidationThresholds: liquidationThresholds,
        prices: prices,
        decs: decs,
        balanceAdditions: balanceAdditions,
        planKind: packedData[1],
        propNotUnderlying18: packedData[2],
        usePoolProportions: packedData[0] != 0,
        entryDataParam: packedData[6]
      }),
      packedData[3],
      packedData[4],
      packedData[5]
    );
  }

  /// @notice Generate plan for next withdraw iteration. We can do only one swap per iteration.
  ///         In general, we cam make 1) single swap (direct or reverse) and 2) repay
  ///         Swap is required to get required repay-amount OR to swap leftovers on final iteration.
  /// @param requestedBalance Amount of underlying that we need to have on balance after executing the plan.
  /// @param indexAsset Index of the underlying in {p.tokens} array
  /// @param indexToken Index of the not-underlying in {p.tokens} array
  /// @return indexToSwapPlus1 1-based index of the token to be swapped; 0 means swap is not required.
  /// @return amountToSwap Amount to be swapped. 0 - no swap
  /// @return indexToRepayPlus1 1-based index of the token that should be used to repay borrow in converter.
  ///                            0 - no repay is required - it means that this is a last step with swapping leftovers.
  function _buildIterationPlan(
    SwapRepayPlanParams memory p,
    uint requestedBalance,
    uint indexAsset,
    uint indexToken
  ) internal returns (
    uint indexToSwapPlus1,
    uint amountToSwap,
    uint indexToRepayPlus1
  ) {
    GetIterationPlanLocal memory v;
    v.asset = p.tokens[indexAsset];
    v.token = p.tokens[indexToken];

    v.assetBalance = IERC20(v.asset).balanceOf(address(this)) + p.balanceAdditions[indexAsset];
    v.tokenBalance = IERC20(p.tokens[indexToken]).balanceOf(address(this)) + p.balanceAdditions[indexToken];

    if (p.planKind == IterationPlanLib.PLAN_SWAP_ONLY) {
      v.swapLeftoversNeeded = true;
    } else {
      uint requestedAmount = requestedBalance == type(uint).max
        ? type(uint).max
        : AppLib.sub0(requestedBalance, v.assetBalance);

      if (requestedAmount < p.liquidationThresholds[indexAsset]) {
        // we don't need to repay any debts anymore, but we should swap leftovers
        v.swapLeftoversNeeded = true;
      } else {
        // we need to increase balance on the following amount: requestedAmount - v.balance;
        // we can have two possible borrows:
        // 1) direct (p.tokens[INDEX_ASSET] => tokens[i]) and 2) reverse (tokens[i] => p.tokens[INDEX_ASSET])
        // normally we can have only one of them, not both..
        // but better to take into account possibility to have two debts simultaneously

        // reverse debt
        (v.debtReverse, v.collateralReverse) = p.converter.getDebtAmountCurrent(address(this), v.token, v.asset, true);
        if (v.debtReverse < AppLib.DUST_AMOUNT_TOKENS) { // there is reverse debt or the reverse debt is dust debt
          // direct debt
          (v.totalDebt, v.totalCollateral) = p.converter.getDebtAmountCurrent(address(this), v.asset, v.token, true);

          if (v.totalDebt < AppLib.DUST_AMOUNT_TOKENS) { // there is direct debt or the direct debt is dust debt
            // This is final iteration - we need to swap leftovers and get amounts on balance in proper proportions.
            // The leftovers should be swapped to get following result proportions of the assets:
            //      underlying : not-underlying === 1e18 - propNotUnderlying18 : propNotUnderlying18
            v.swapLeftoversNeeded = true;
          } else {
            // repay direct debt
            if (p.planKind == IterationPlanLib.PLAN_REPAY_SWAP_REPAY) {
              (indexToSwapPlus1, amountToSwap, indexToRepayPlus1) = _buildPlanRepaySwapRepay(
                p,
                [v.assetBalance, v.tokenBalance],
                [indexAsset, indexToken],
                p.propNotUnderlying18,
                [v.totalCollateral, v.totalDebt],
                p.entryDataParam
              );
            } else {
              (indexToSwapPlus1, amountToSwap, indexToRepayPlus1) = _buildPlanForSellAndRepay(
                requestedAmount,
                p,
                v.totalCollateral,
                v.totalDebt,
                indexAsset,
                indexToken,
                v.assetBalance,
                v.tokenBalance
              );
            }
          }
        } else {
          // repay reverse debt
          if (p.planKind == IterationPlanLib.PLAN_REPAY_SWAP_REPAY) {
            (indexToSwapPlus1, amountToSwap, indexToRepayPlus1) = _buildPlanRepaySwapRepay(
              p,
              [v.tokenBalance, v.assetBalance],
              [indexToken, indexAsset],
              1e18 - p.propNotUnderlying18,
              [v.collateralReverse, v.debtReverse],
              p.entryDataParam
            );
          } else {
            (indexToSwapPlus1, amountToSwap, indexToRepayPlus1) = _buildPlanForSellAndRepay(
              requestedAmount == type(uint).max
                ? type(uint).max
                : requestedAmount * p.prices[indexAsset] * p.decs[indexToken] / p.prices[indexToken] / p.decs[indexAsset],
              p,
              v.collateralReverse,
              v.debtReverse,
              indexToken,
              indexAsset,
              v.tokenBalance,
              v.assetBalance
            );
          }
        }
      }
    }

    if (v.swapLeftoversNeeded) {
      (indexToSwapPlus1, amountToSwap) = _buildPlanForLeftovers(p, v.assetBalance, v.tokenBalance, indexAsset, indexToken, p.propNotUnderlying18);
    }

    return (indexToSwapPlus1, amountToSwap, indexToRepayPlus1);
  }

  /// @notice Repay B, get collateral A, then swap A => B, [make one more repay B] => get A:B in required proportions
  /// @param balancesAB [balanceA, balanceB]
  /// @param idxAB [indexA, indexB]
  /// @param totalAB [totalCollateralA, totalBorrowB]
  /// @param requiredAmountToReduceDebt If not zero: we are going to make repay-swap-repay to reduce total
  ///        debt on the given amount. So, if possible it worth to make swap in such a way as to reduce
  ///        the amount of debt by the given amount.
  function _buildPlanRepaySwapRepay(
    SwapRepayPlanParams memory p,
    uint[2] memory balancesAB,
    uint[2] memory idxAB,
    uint propB,
    uint[2] memory totalAB,
    uint requiredAmountToReduceDebt
  ) internal returns (
    uint indexToSwapPlus1,
    uint amountToSwap,
    uint indexToRepayPlus1
  ) {
    // use all available tokenB to repay debt and receive as much as possible tokenA
    uint amountToRepay = Math.min(balancesAB[1], totalAB[1]);

    uint collateralAmount;
    if (amountToRepay >= AppLib.DUST_AMOUNT_TOKENS) {
      uint swappedAmountOut;
      //
      (collateralAmount, swappedAmountOut) = p.converter.quoteRepay(address(this), p.tokens[idxAB[0]], p.tokens[idxAB[1]], amountToRepay);
      if (collateralAmount > swappedAmountOut) { // SCB-789
        collateralAmount -= swappedAmountOut;
      }
    } else {
      amountToRepay = 0;
    }

    // swap A to B: full or partial
    amountToSwap = estimateSwapAmountForRepaySwapRepay(
      p,
      balancesAB[0],
      balancesAB[1],
      idxAB[0],
      idxAB[1],
      propB,
      totalAB[0],
      totalAB[1],
      collateralAmount,
      amountToRepay
    );

    if (requiredAmountToReduceDebt != 0) {
      // probably it worth to increase amount to swap?
      uint requiredAmountToSwap = requiredAmountToReduceDebt * p.prices[idxAB[1]] * p.decs[idxAB[0]] / p.prices[idxAB[0]] / p.decs[idxAB[1]];
      amountToSwap = Math.max(amountToSwap, requiredAmountToSwap);
      amountToSwap = Math.min(amountToSwap, balancesAB[0] + collateralAmount);
    }

    return (idxAB[0] + 1, amountToSwap, idxAB[1] + 1);
  }

  /// @notice Estimate swap amount for iteration "repay-swap-repay"
  ///         The iteration should give us amounts of assets in required proportions.
  ///         There are two cases here: full swap and partial swap. Second repay is not required if the swap is partial.
  /// @param collateralA Estimated value of collateral A received after repay balanceB
  /// @return amount of token A to be swapped
  function estimateSwapAmountForRepaySwapRepay(
    SwapRepayPlanParams memory p,
    uint balanceA,
    uint balanceB,
    uint indexA,
    uint indexB,
    uint propB,
    uint totalCollateralA,
    uint totalBorrowB,
    uint collateralA,
    uint amountToRepayB
  ) internal pure returns(uint) {
    // N - number of the state
    // bAN, bBN - balances of A and B; aAN, aBN - amounts of A and B; cAN, cBN - collateral/borrow amounts of A/B
    // alpha ~ cAN/cBN - estimated ratio of collateral/borrow
    // s = swap ratio, aA is swapped to aB, so aA = s * aB
    // g = split ratio, bA1 is divided on two parts: bA1 * gamma, bA1 * (1 - gamma). First part is swapped.
    // X = proportion of A, Y = proportion of B

    // Formulas
    // aB3 = (x * bB2 - y * bA2) / (alpha * y + x)
    // gamma = (y * bA1 - x * bB1) / (bA1 * (x * s + y))

    // There are following stages:
    // 0. init (we have at least not zero amount of B and not zero debt of B)
    // 1. repay 1 (repay all available amount of B OR all available debt)
    // 2. swap (swap A fully or partially to B)
    // 3. repay 2 (optional: we need this stage if full swap produces amount of B that is <= available debt)
    // 4. final (we have assets in right proportion on the balance)
    EstimateSwapAmountForRepaySwapRepayLocal memory v;
    v.x = 1e18 - propB;
    v.y = propB;

// 1. repay 1
    // convert amounts A, amounts B to cost A, cost B in USD
    v.bA1 = (balanceA + collateralA) * p.prices[indexA] / p.decs[indexA];
    v.bB1 = (balanceB - amountToRepayB) * p.prices[indexB] / p.decs[indexB];
    v.cB1 = (totalBorrowB - amountToRepayB) * p.prices[indexB] / p.decs[indexB];
    v.alpha = 1e18 * totalCollateralA * p.prices[indexA] * p.decs[indexB]
      / p.decs[indexA] / p.prices[indexB] / totalBorrowB; // (!) approx estimation

// 2. full swap
    v.aA2 = v.bA1;
    v.swapRatio = 1e18; // we assume swap ratio 1:1

// 3. repay 2
    // aB3 = (x * bB2 - Y * bA2) / (alpha * y + x)
    v.aB3 = (
      v.x * (v.bB1 + v.aA2 * v.swapRatio / 1e18)    // bB2 = v.bB1 + v.aA2 * v.s / 1e18
      - v.y * (v.bA1 - v.aA2)                       // bA2 = v.bA1 - v.aA2;
    ) / (v.y * v.alpha / 1e18 + v.x);

    if (v.aB3 > v.cB1) {
      // there is not enough debt to make second repay
      // we need to make partial swap and receive assets in right proportions in result
      // v.gamma = 1e18 * (v.y * v.bA1 - v.x * v.bB1) / (v.bA1 * (v.x * v.s / 1e18 + v.y));
      v.aA2 = v.bA1 * (v.y * v.bA1 - v.x * v.bB1) / (v.bA1 * (v.x * v.swapRatio / 1e18 + v.y));
    }

    return v.aA2 * p.decs[indexA] / p.prices[indexA];
  }

  /// @notice Prepare a plan to swap leftovers to required proportion
  /// @param balanceA Balance of token A, i.e. underlying
  /// @param balanceB Balance of token B, i.e. not-underlying
  /// @param indexA Index of the token A, i.e. underlying, in {p.prices} and {p.decs}
  /// @param indexB Index of the token B, i.e. not-underlying, in {p.prices} and {p.decs}
  /// @param propB Required proportion of TokenB [0..1e18]. Proportion of token A is (1e18-propB)
  /// @return indexTokenToSwapPlus1 Index of the token to be swapped. 0 - no swap is required
  /// @return amountToSwap Amount to be swapped. 0 - no swap is required
  function _buildPlanForLeftovers(
    SwapRepayPlanParams memory p,
    uint balanceA,
    uint balanceB,
    uint indexA,
    uint indexB,
    uint propB
  ) internal pure returns (
    uint indexTokenToSwapPlus1,
    uint amountToSwap
  ) {
    (uint targetA, uint targetB) = _getTargetAmounts(p.prices, p.decs, balanceA, balanceB, propB, indexA, indexB);
    if (balanceA < targetA) {
      // we need to swap not-underlying to underlying
      if (balanceB - targetB > p.liquidationThresholds[indexB]) {
        amountToSwap = balanceB - targetB;
        indexTokenToSwapPlus1 = indexB + 1;
      }
    } else {
      // we need to swap underlying to not-underlying
      if (balanceA - targetA > p.liquidationThresholds[indexA]) {
        amountToSwap = balanceA - targetA;
        indexTokenToSwapPlus1 = indexA + 1;
      }
    }
    return (indexTokenToSwapPlus1, amountToSwap);
  }

  /// @notice Prepare a plan to swap some amount of collateral to get required repay-amount and make repaying
  ///         1) Sell collateral-asset to get missed amount-to-repay 2) make repay and get more collateral back
  /// @param requestedAmount We need to increase balance (of collateral asset) on this amount.
  /// @param totalCollateral Total amount of collateral used in the borrow
  /// @param totalDebt Total amount of debt that should be repaid to receive {totalCollateral}
  /// @param indexCollateral Index of collateral asset in {p.prices}, {p.decs}
  /// @param indexBorrow Index of borrow asset in {p.prices}, {p.decs}
  /// @param balanceCollateral Current balance of the collateral asset
  /// @param balanceBorrow Current balance of the borrowed asset
  /// @param indexTokenToSwapPlus1 1-based index of the token to be swapped. Swap of amount of collateral asset can be required
  ///                              to receive missed amount-to-repay. 0 - no swap is required
  /// @param amountToSwap Amount to be swapped. 0 - no swap is required
  /// @param indexRepayTokenPlus1 1-based index of the token to be repaied. 0 - no repaying is required
  function _buildPlanForSellAndRepay(
    uint requestedAmount,
    SwapRepayPlanParams memory p,
    uint totalCollateral,
    uint totalDebt,
    uint indexCollateral,
    uint indexBorrow,
    uint balanceCollateral,
    uint balanceBorrow
  ) internal pure returns (
    uint indexTokenToSwapPlus1,
    uint amountToSwap,
    uint indexRepayTokenPlus1
  ) {
    // what amount of collateral we should sell to get required amount-to-pay to pay the debt
    uint toSell = _getAmountToSell(
      requestedAmount,
      totalDebt,
      totalCollateral,
      p.prices,
      p.decs,
      indexCollateral,
      indexBorrow,
      balanceBorrow
    );

    // convert {toSell} amount of underlying to token
    if (toSell != 0 && balanceCollateral != 0) {
      toSell = Math.min(toSell, balanceCollateral);
      uint threshold = p.liquidationThresholds[indexCollateral];
      if (toSell > threshold) {
        amountToSwap = toSell;
        indexTokenToSwapPlus1 = indexCollateral + 1;
      } else {
        // we need to sell amount less than the threshold, it's not allowed
        // but it's dangerous to just ignore the selling because there is a chance to have error 35
        // (There is a debt $3.29, we make repay $3.27 => error 35)
        // it would be safer to sell a bit more amount if it's possible
        if (balanceCollateral >= threshold + 1) {
          amountToSwap = threshold + 1;
          indexTokenToSwapPlus1 = indexCollateral + 1;
        }
      }
    }

    return (indexTokenToSwapPlus1, amountToSwap, indexBorrow + 1);
  }

  /// @notice Calculate what balances of underlying and not-underlying we need to fit {propNotUnderlying18}
  /// @param prices Prices of underlying and not underlying
  /// @param decs 10**decimals for underlying and not underlying
  /// @param assetBalance Current balance of underlying
  /// @param tokenBalance Current balance of not-underlying
  /// @param propNotUnderlying18 Required proportion of not-underlying [0..1e18]
  ///                            Proportion of underlying would be (1e18 - propNotUnderlying18)
  /// @param targetAssets What result balance of underlying is required to fit to required proportions
  /// @param targetTokens What result balance of not-underlying is required to fit to required proportions
  function _getTargetAmounts(
    uint[] memory prices,
    uint[] memory decs,
    uint assetBalance,
    uint tokenBalance,
    uint propNotUnderlying18,
    uint indexAsset,
    uint indexToken
  ) internal pure returns (
    uint targetAssets,
    uint targetTokens
  ) {
    uint costAssets = assetBalance * prices[indexAsset] / decs[indexAsset];
    uint costTokens = tokenBalance * prices[indexToken] / decs[indexToken];
    targetTokens = propNotUnderlying18 == 0
      ? 0
      : ((costAssets + costTokens) * propNotUnderlying18 / 1e18);
    targetAssets = ((costAssets + costTokens) - targetTokens) * decs[indexAsset] / prices[indexAsset];
    targetTokens = targetTokens * decs[indexToken] / prices[indexToken];
  }

  /// @notice What amount of collateral should be sold to pay the debt and receive {requestedAmount}
  /// @dev It doesn't allow to sell more than the amount of total debt in the borrow
  /// @param requestedAmount We need to increase balance (of collateral asset) on this amount
  /// @param totalDebt Total debt of the borrow in terms of borrow asset
  /// @param totalCollateral Total collateral of the borrow in terms of collateral asset
  /// @param prices Cost of $1 in terms of the asset, decimals 18
  /// @param decs 10**decimals for each asset
  /// @param indexCollateral Index of the collateral asset in {prices} and {decs}
  /// @param indexBorrowAsset Index of the borrow asset in {prices} and {decs}
  /// @param balanceBorrowAsset Available balance of the borrow asset, it will be used to cover the debt
  /// @return amountOut Amount of collateral-asset that should be sold
  function _getAmountToSell(
    uint requestedAmount,
    uint totalDebt,
    uint totalCollateral,
    uint[] memory prices,
    uint[] memory decs,
    uint indexCollateral,
    uint indexBorrowAsset,
    uint balanceBorrowAsset
  ) internal pure returns (
    uint amountOut
  ) {
    if (totalDebt != 0) {
      if (balanceBorrowAsset != 0) {
        // there is some borrow asset on balance
        // it will be used to cover the debt
        // let's reduce the size of totalDebt/Collateral to exclude balanceBorrowAsset
        uint sub = Math.min(balanceBorrowAsset, totalDebt);
        totalCollateral -= totalCollateral * sub / totalDebt;
        totalDebt -= sub;
      }

      // for definiteness: usdc - collateral asset, dai - borrow asset
      // Pc = price of the USDC, Pb = price of the DAI, alpha = Pc / Pb [DAI / USDC]
      // S [USDC] - amount to sell, R [DAI] = alpha * S - amount to repay
      // After repaying R we get: alpha * S * C / R
      // Balance should be increased on: requestedAmount = alpha * S * C / R - S
      // So, we should sell: S = requestedAmount / (alpha * C / R - 1))
      // We can lost some amount on liquidation of S => R, so we need to use some gap = {GAP_AMOUNT_TO_SELL}
      // Same formula: S * h = S + requestedAmount, where h = health factor => s = requestedAmount / (h - 1)
      // h = alpha * C / R
      uint alpha18 = prices[indexCollateral] * decs[indexBorrowAsset] * 1e18
        / prices[indexBorrowAsset] / decs[indexCollateral];

      // if totalCollateral is zero (liquidation happens) we will have zero amount (the debt shouldn't be paid)
      amountOut = totalDebt != 0 && alpha18 * totalCollateral / totalDebt > 1e18
        ? Math.min(requestedAmount, totalCollateral) * 1e18 / (alpha18 * totalCollateral / totalDebt - 1e18)
        : 0;

      if (amountOut != 0) {
        // we shouldn't try to sell amount greater than amount of totalDebt in terms of collateral asset
        // but we always asks +1% because liquidation results can be different a bit from expected
        amountOut = (AppLib.GAP_CONVERSION + AppLib.DENOMINATOR) * Math.min(amountOut, totalDebt * 1e18 / alpha18) / AppLib.DENOMINATOR;
      }
    }

    return amountOut;
  }
//endregion ------------------------------------------------ Build plan
}
","keccak256":"0x7f4bcb29e74bf70a8a6e786635b689bb7457a8dc41d0ce42c63bd4e5c34616ef","license":"BUSL-1.1"},"contracts/libs/TokenAmountsLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./AppErrors.sol";

/// @title Library for clearing / joining token addresses & amounts arrays
/// @author bogdoslav
library TokenAmountsLib {
  /// @notice Version of the contract
  /// @dev Should be incremented when contract changed
  string internal constant TOKEN_AMOUNTS_LIB_VERSION = "1.0.1";

  function uncheckedInc(uint i) internal pure returns (uint) {
    unchecked {
      return i + 1;
    }
  }

  function filterZeroAmounts(
    address[] memory tokens,
    uint[] memory amounts
  ) internal pure returns (
    address[] memory t,
    uint[] memory a
  ) {
    require(tokens.length == amounts.length, AppErrors.INCORRECT_LENGTHS);
    uint len2 = 0;
    uint len = tokens.length;
    for (uint i = 0; i < len; i++) {
      if (amounts[i] != 0) len2++;
    }

    t = new address[](len2);
    a = new uint[](len2);

    uint j = 0;
    for (uint i = 0; i < len; i++) {
      uint amount = amounts[i];
      if (amount != 0) {
        t[j] = tokens[i];
        a[j] = amount;
        j++;
      }
    }
  }

  /// @notice unites three arrays to single array without duplicates, amounts are sum, zero amounts are allowed
  function combineArrays(
    address[] memory tokens0,
    uint[] memory amounts0,
    address[] memory tokens1,
    uint[] memory amounts1,
    address[] memory tokens2,
    uint[] memory amounts2
  ) internal pure returns (
    address[] memory allTokens,
    uint[] memory allAmounts
  ) {
    uint[] memory lens = new uint[](3);
    lens[0] = tokens0.length;
    lens[1] = tokens1.length;
    lens[2] = tokens2.length;

    require(
      lens[0] == amounts0.length && lens[1] == amounts1.length && lens[2] == amounts2.length,
      AppErrors.INCORRECT_LENGTHS
    );

    uint maxLength = lens[0] + lens[1] + lens[2];
    address[] memory tokensOut = new address[](maxLength);
    uint[] memory amountsOut = new uint[](maxLength);
    uint unitedLength;

    for (uint step; step < 3; ++step) {
      uint[] memory amounts = step == 0
        ? amounts0
        : (step == 1
          ? amounts1
          : amounts2);
      address[] memory tokens = step == 0
        ? tokens0
        : (step == 1
          ? tokens1
          : tokens2);
      for (uint i1 = 0; i1 < lens[step]; i1++) {
        uint amount1 = amounts[i1];
        address token1 = tokens[i1];
        bool united = false;

        for (uint i = 0; i < unitedLength; i++) {
          if (token1 == tokensOut[i]) {
            amountsOut[i] += amount1;
            united = true;
            break;
          }
        }

        if (!united) {
          tokensOut[unitedLength] = token1;
          amountsOut[unitedLength] = amount1;
          unitedLength++;
        }
      }
    }

    // copy united tokens to result array
    allTokens = new address[](unitedLength);
    allAmounts = new uint[](unitedLength);
    for (uint i; i < unitedLength; i++) {
      allTokens[i] = tokensOut[i];
      allAmounts[i] = amountsOut[i];
    }

  }
}
","keccak256":"0xb3adb8a53441362b47b3bf5c0c7181f7c1652de7dde3df4fb765e8484447d074","license":"BUSL-1.1"},"contracts/strategies/ConverterStrategyBaseLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "@tetu_io/tetu-contracts-v2/contracts/interfaces/ITetuLiquidator.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/IForwarder.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/ITetuVaultV2.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/ISplitter.sol";
import "@tetu_io/tetu-contracts-v2/contracts/strategy/StrategyLib2.sol";
import "@tetu_io/tetu-contracts-v2/contracts/openzeppelin/Math.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/IConverterController.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/IPriceOracle.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/ITetuConverter.sol";
import "../libs/AppErrors.sol";
import "../libs/AppLib.sol";
import "../libs/TokenAmountsLib.sol";
import "../libs/ConverterEntryKinds.sol";
import "../libs/IterationPlanLib.sol";
import "../interfaces/IConverterStrategyBase.sol";

library ConverterStrategyBaseLib {
  using SafeERC20 for IERC20;

//region--------------------------------------------------- Data types

  /// @notice Local vars for {_recycle}, workaround for stack too deep
  struct RecycleLocalParams {
    /// @notice Compound amount + Performance amount
    uint amountCP;
    /// @notice Amount to compound
    uint amountC;
    /// @notice Amount to send to performance and insurance
    uint amountP;
    /// @notice Amount to forwarder + amount to compound
    uint amountFC;
    address rewardToken;
    uint len;
    uint receivedAmountOut;
  }

  struct OpenPositionLocal {
    uint entryKind;
    address[] converters;
    uint[] collateralsRequired;
    uint[] amountsToBorrow;
    uint collateral;
    uint amountToBorrow;
  }

  struct OpenPositionEntryKind1Local {
    address[] converters;
    uint[] collateralsRequired;
    uint[] amountsToBorrow;
    uint collateral;
    uint amountToBorrow;
    uint c1;
    uint c3;
    uint alpha;
  }

  struct SwapToGetAmountLocal {
    uint len;
    uint[] prices;
    uint[] decs;
  }

  struct ConvertAfterWithdrawLocal {
    address asset;
    uint spent;
    uint received;
    uint balance;
    uint balanceBefore;
    uint len;
  }

  struct SwapToGivenAmountInputParams {
    ITetuConverter converter;
    ITetuLiquidator liquidator;
    uint targetAmount;
    address[] tokens;
    uint[] amounts;
    /// @notice liquidationThresholds for the {tokens}
    uint[] liquidationThresholds;
    uint indexTargetAsset;
    address underlying;
    /// @notice Allow to swap more then required (i.e. 1_000 => +1%)
    ///         to avoid additional swap if the swap return amount a bit less than we expected
    uint overswap;
  }

  struct SwapToGivenAmountLocal {
    uint len;
    uint[] availableAmounts;
    uint i;
  }

  struct CloseDebtsForRequiredAmountLocal {
    address asset;
    uint balanceAsset;
    uint balanceToken;

    uint newBalanceAsset;
    uint newBalanceToken;

    uint idxToSwap1;
    uint amountToSwap;
    uint idxToRepay1;

    /// @notice Cost of $1 in terms of the assets, decimals 18
    uint[] prices;
    /// @notice 10**decimal for the assets
    uint[] decs;

    /// @notice Amounts that will be received on balance before execution of the plan.
    uint[] balanceAdditions;

    /// @notice Required proportion of not-underlying for the final swap of leftovers, [0...1e18].
    ///         The leftovers should be swapped to get following result proportions of the assets:
    ///         not-underlying : underlying === propNotUnderlying18 : 1e18 - propNotUnderlying18
    uint propNotUnderlying18;

    /// @notice proportions should be taken from the pool and re-read from the pool after each swap
    bool usePoolProportions;

    bool exitLoop;
  }

  struct DataSetLocal {
    ITetuConverter converter;
    ITetuLiquidator liquidator;
    /// @notice Tokens received from {_depositorPoolAssets}
    address[] tokens;
    /// @notice Index of the main asset in {tokens}
    uint indexAsset;
    /// @notice Length of {tokens}
    uint len;
  }

  struct RecycleLocal {
    address asset;
    uint compoundRatio;
    uint performanceFee;
    /// @notice // total amount for the performance receiver and insurance
    uint amountPerf;
    uint toPerf;
    uint toInsurance;
    uint[] amountsToForward;
    uint[] thresholds;
    int debtToInsuranceCurrent;
    int debtToInsuranceUpdated;
    address splitter;
  }

  /// @notice Input params for _recycle
  struct RecycleParams {
    ITetuConverter converter;
    ITetuLiquidator liquidator;
    address splitter;

    /// @notice Underlying asset
    address asset;
    /// @notice Compound ration in the range [0...COMPOUND_DENOMINATOR]
    uint compoundRatio;
    /// @notice tokens received from {_depositorPoolAssets}
    address[] tokens;
    /// @notice Liquidation thresholds for rewards tokens
    uint[] thresholds;
    /// @notice Full list of reward tokens received from tetuConverter and depositor
    address[] rewardTokens;
    /// @notice Amounts of {rewardTokens_}; we assume, there are no zero amounts here
    uint[] rewardAmounts;
    /// @notice Performance fee in the range [0...FEE_DENOMINATOR]
    uint performanceFee;
    /// @notice Current debt to the insurance [in underlying]
    int debtToInsurance;
    /// @notice Liquidation threshold for the {asset}
    uint assetThreshold;
  }
//endregion--------------------------------------------------- Data types

//region--------------------------------------------------- Constants

  /// @notice approx one month for average block time 2 sec
  uint internal constant _LOAN_PERIOD_IN_BLOCKS = 30 days / 2;
  uint internal constant _REWARD_LIQUIDATION_SLIPPAGE = 5_000; // 5%
  uint internal constant COMPOUND_DENOMINATOR = 100_000;
  uint internal constant _ASSET_LIQUIDATION_SLIPPAGE = 300;
  uint internal constant PRICE_IMPACT_TOLERANCE = 300;
  /// @notice borrow/collateral amount cannot be less than given number of tokens
  uint internal constant DEFAULT_OPEN_POSITION_AMOUNT_IN_THRESHOLD = 10;
  /// @notice Allow to swap more then required (i.e. 1_000 => +1%) inside {swapToGivenAmount}
  ///         to avoid additional swap if the swap will return amount a bit less than we expected
  uint internal constant OVERSWAP = PRICE_IMPACT_TOLERANCE + _ASSET_LIQUIDATION_SLIPPAGE;
  /// @notice During SWAP-REPAY cycle we can receive requested amount after SWAP, so, following REPAY will be skipped.
  ///         But we should prevent situation "zero balance, not zero debts".
  ///         So, it worth to request amount higher (on the given gap) than it's really requested.
  uint internal constant REQUESTED_BALANCE_GAP = 5_000; // 5%
//endregion--------------------------------------------------- Constants

//region--------------------------------------------------- Events
  /// @notice A borrow was made
  event OpenPosition(
    address converter,
    address collateralAsset,
    uint collateralAmount,
    address borrowAsset,
    uint borrowedAmount,
    address recepient
  );

  /// @notice Some borrow(s) was/were repaid
  event ClosePosition(
    address collateralAsset,
    address borrowAsset,
    uint amountRepay,
    address recepient,
    uint returnedAssetAmountOut,
    uint returnedBorrowAmountOut
  );

  /// @notice A liquidation was made
  event Liquidation(
    address tokenIn,
    address tokenOut,
    uint amountIn,
    uint spentAmountIn,
    uint receivedAmountOut
  );

  event ReturnAssetToConverter(address asset, uint amount);

  /// @notice Recycle was made
  /// @param rewardTokens Full list of reward tokens received from tetuConverter and depositor
  /// @param amountsToForward Amounts to be sent to forwarder
  event Recycle(
    address[] rewardTokens,
    uint[] amountsToForward,
    uint toPerf,
    uint toInsurance
  );

  /// @notice Debt to insurance was paid by rewards
  /// @param debtToInsuranceBefore Initial amount of debts to the insurance, in underlying
  /// @param debtToInsuranceBefore Final amount of debts to the insurance, in underlying
  event OnPayDebtToInsurance(
    int debtToInsuranceBefore,
    int debtToInsuraneAfter
  );

  /// @notice Debt to insurance was paid by a reward token
  /// @param debtToCover Initial amount of debt that should be covered, in underlying
  /// @param debtLeftovers Final amount of debt that should be covered, in underlying
  /// It can be negative if we paid more than required
  event OnCoverDebtToInsurance(
    address rewardToken,
    uint rewardAmount,
    uint debtToCover,
    int debtLeftovers
  );
//endregion---------------------------------------------------  Events

//region--------------------------------------------------- Borrow and close positions

  /// @notice Make one or several borrow necessary to supply/borrow required {amountIn_} according to {entryData_}
  ///         Max possible collateral should be approved before calling of this function.
  /// @param entryData_ Encoded entry kind and additional params if necessary (set of params depends on the kind)
  ///                   See TetuConverter\EntryKinds.sol\ENTRY_KIND_XXX constants for possible entry kinds
  ///                   0 or empty: Amount of collateral {amountIn_} is fixed, amount of borrow should be max possible.
  /// @param amountIn_ Meaning depends on {entryData_}.
  function openPosition(
    ITetuConverter tetuConverter_,
    bytes memory entryData_,
    address collateralAsset_,
    address borrowAsset_,
    uint amountIn_,
    uint thresholdAmountIn_
  ) external returns (
    uint collateralAmountOut,
    uint borrowedAmountOut
  ) {
    return _openPosition(tetuConverter_, entryData_, collateralAsset_, borrowAsset_, amountIn_, thresholdAmountIn_);
  }

  /// @notice Make one or several borrow necessary to supply/borrow required {amountIn_} according to {entryData_}
  ///         Max possible collateral should be approved before calling of this function.
  /// @param entryData_ Encoded entry kind and additional params if necessary (set of params depends on the kind)
  ///                   See TetuConverter\EntryKinds.sol\ENTRY_KIND_XXX constants for possible entry kinds
  ///                   0 or empty: Amount of collateral {amountIn_} is fixed, amount of borrow should be max possible.
  /// @param amountIn_ Meaning depends on {entryData_}.
  /// @param thresholdAmountIn_ Min value of amountIn allowed for the second and subsequent conversions.
  ///        0 - use default min value
  ///        If amountIn becomes too low, no additional borrows are possible, so
  ///        the rest amountIn is just added to collateral/borrow amount of previous conversion.
  function _openPosition(
    ITetuConverter tetuConverter_,
    bytes memory entryData_,
    address collateralAsset_,
    address borrowAsset_,
    uint amountIn_,
    uint thresholdAmountIn_
  ) internal returns (
    uint collateralAmountOut,
    uint borrowedAmountOut
  ) {
    if (thresholdAmountIn_ == 0) {
      // zero threshold is not allowed because round-issues are possible, see openPosition.dust test
      // we assume here, that it's useless to borrow amount using collateral/borrow amount
      // less than given number of tokens (event for BTC)
      thresholdAmountIn_ = DEFAULT_OPEN_POSITION_AMOUNT_IN_THRESHOLD;
    }
    if (amountIn_ <= thresholdAmountIn_) {
      return (0, 0);
    }

    OpenPositionLocal memory vars;
    // we assume here, that max possible collateral amount is already approved (as it's required by TetuConverter)
    vars.entryKind = ConverterEntryKinds.getEntryKind(entryData_);
    if (vars.entryKind == ConverterEntryKinds.ENTRY_KIND_EXACT_PROPORTION_1) {
      return openPositionEntryKind1(
        tetuConverter_,
        entryData_,
        collateralAsset_,
        borrowAsset_,
        amountIn_,
        thresholdAmountIn_
      );
    } else {
      (vars.converters, vars.collateralsRequired, vars.amountsToBorrow,) = tetuConverter_.findBorrowStrategies(
        entryData_,
        collateralAsset_,
        amountIn_,
        borrowAsset_,
        _LOAN_PERIOD_IN_BLOCKS
      );

      uint len = vars.converters.length;
      if (len > 0) {
        for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
          // we need to approve collateralAmount before the borrow-call but it's already approved, see above comments
          vars.collateral;
          vars.amountToBorrow;
          if (vars.entryKind == ConverterEntryKinds.ENTRY_KIND_EXACT_COLLATERAL_IN_FOR_MAX_BORROW_OUT_0) {
            // we have exact amount of total collateral amount
            // Case ENTRY_KIND_EXACT_PROPORTION_1 is here too because we consider first platform only
            vars.collateral = amountIn_ < vars.collateralsRequired[i]
              ? amountIn_
              : vars.collateralsRequired[i];
            vars.amountToBorrow = amountIn_ < vars.collateralsRequired[i]
              ? vars.amountsToBorrow[i] * amountIn_ / vars.collateralsRequired[i]
              : vars.amountsToBorrow[i];
            amountIn_ -= vars.collateral;
          } else {
            // assume here that entryKind == EntryKinds.ENTRY_KIND_EXACT_BORROW_OUT_FOR_MIN_COLLATERAL_IN_2
            // we have exact amount of total amount-to-borrow
            vars.amountToBorrow = amountIn_ < vars.amountsToBorrow[i]
              ? amountIn_
              : vars.amountsToBorrow[i];
            vars.collateral = amountIn_ < vars.amountsToBorrow[i]
              ? vars.collateralsRequired[i] * amountIn_ / vars.amountsToBorrow[i]
              : vars.collateralsRequired[i];
            amountIn_ -= vars.amountToBorrow;
          }

          if (amountIn_ < thresholdAmountIn_ && amountIn_ != 0) {
            // dust amount is left, just leave it unused
            // we cannot add it to collateral/borrow amounts - there is a risk to exceed max allowed amounts
            amountIn_ = 0;
          }

          if (vars.amountToBorrow != 0) {
            borrowedAmountOut += tetuConverter_.borrow(
              vars.converters[i],
              collateralAsset_,
              vars.collateral,
              borrowAsset_,
              vars.amountToBorrow,
              address(this)
            );
            collateralAmountOut += vars.collateral;
            emit OpenPosition(
              vars.converters[i],
              collateralAsset_,
              vars.collateral,
              borrowAsset_,
              vars.amountToBorrow,
              address(this)
            );
          }

          if (amountIn_ == 0) break;
        }
      }

      return (collateralAmountOut, borrowedAmountOut);
    }
  }

  /// @notice Open position using entry kind 1 - split provided amount on two parts according provided proportions
  /// @param amountIn_ Amount of collateral to be divided on parts. We assume {amountIn_} > 0
  /// @param collateralThreshold_ Min allowed collateral amount to be used for new borrow, > 0
  /// @return collateralAmountOut Total collateral used to borrow {borrowedAmountOut}
  /// @return borrowedAmountOut Total borrowed amount
  function openPositionEntryKind1(
    ITetuConverter tetuConverter_,
    bytes memory entryData_,
    address collateralAsset_,
    address borrowAsset_,
    uint amountIn_,
    uint collateralThreshold_
  ) internal returns (
    uint collateralAmountOut,
    uint borrowedAmountOut
  ) {
    OpenPositionEntryKind1Local memory vars;
    (vars.converters, vars.collateralsRequired, vars.amountsToBorrow,) = tetuConverter_.findBorrowStrategies(
      entryData_,
      collateralAsset_,
      amountIn_,
      borrowAsset_,
      _LOAN_PERIOD_IN_BLOCKS
    );

    uint len = vars.converters.length;
    if (len > 0) {
      // we should split amountIn on two amounts with proportions x:y
      (, uint x, uint y) = abi.decode(entryData_, (uint, uint, uint));
      // calculate prices conversion ratio using price oracle, decimals 18
      // i.e. alpha = 1e18 * 75e6 usdc / 25e18 matic = 3e6 usdc/matic
      vars.alpha = _getCollateralToBorrowRatio(tetuConverter_, collateralAsset_, borrowAsset_);

      for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
        // the lending platform allows to convert {collateralsRequired[i]} to {amountsToBorrow[i]}
        // and give us required proportions in result
        // C = C1 + C2, C2 => B2, B2 * alpha = C3, C1/C3 must be equal to x/y
        // C1 is collateral amount left untouched (x)
        // C2 is collateral amount converted to B2 (y)
        // but if lending platform doesn't have enough liquidity
        // it reduces {collateralsRequired[i]} and {amountsToBorrow[i]} proportionally to fit the limits
        // as result, remaining C1 will be too big after conversion and we need to make another borrow
        vars.c3 = vars.alpha * vars.amountsToBorrow[i] / 1e18;
        vars.c1 = x * vars.c3 / y;

        // we doesn't calculate an intermediate ratio cR/(cR+c1) to avoid lost of precision
        if ((vars.collateralsRequired[i] + vars.c1) > amountIn_) {
          vars.collateral = vars.collateralsRequired[i] * amountIn_ / (vars.collateralsRequired[i] + vars.c1);
          vars.amountToBorrow = vars.amountsToBorrow[i] * amountIn_ / (vars.collateralsRequired[i] + vars.c1);
        } else {
          vars.collateral = vars.collateralsRequired[i];
          vars.amountToBorrow = vars.amountsToBorrow[i];
        }

        // skip any attempts to borrow zero amount or use too little collateral
        if (vars.collateral < collateralThreshold_ || vars.amountToBorrow == 0) {
          if (vars.collateralsRequired[i] + vars.c1 + collateralThreshold_ > amountIn_) {
            // The lending platform has enough resources to make the borrow but amount of the borrow is too low
            // Skip the borrow, leave leftover of collateral untouched
            break;
          } else {
            // The lending platform doesn't have enough resources to make the borrow.
            // We should try to make borrow on the next platform (if any)
            continue;
          }
        }

        require(
          tetuConverter_.borrow(
            vars.converters[i],
            collateralAsset_,
            vars.collateral,
            borrowAsset_,
            vars.amountToBorrow,
            address(this)
          ) == vars.amountToBorrow,
          StrategyLib2.WRONG_VALUE
        );
        emit OpenPosition(
          vars.converters[i],
          collateralAsset_,
          vars.collateral,
          borrowAsset_,
          vars.amountToBorrow,
          address(this)
        );

        borrowedAmountOut += vars.amountToBorrow;
        collateralAmountOut += vars.collateral;

        // calculate amount to be borrowed in the next converter
        vars.c3 = vars.alpha * vars.amountToBorrow / 1e18;
        vars.c1 = x * vars.c3 / y;
        amountIn_ = (amountIn_ > vars.c1 + vars.collateral)
          ? amountIn_ - (vars.c1 + vars.collateral)
          : 0;

        // protection against dust amounts, see "openPosition.dust", just leave dust amount unused
        // we CAN NOT add it to collateral/borrow amounts - there is a risk to exceed max allowed amounts
        // we assume here, that collateralThreshold_ != 0, so check amountIn_ != 0 is not required
        if (amountIn_ < collateralThreshold_) break;
      }
    }

    return (collateralAmountOut, borrowedAmountOut);
  }

  /// @notice Get ratio18 = collateral / borrow
  function _getCollateralToBorrowRatio(
    ITetuConverter converter_,
    address collateralAsset_,
    address borrowAsset_
  ) internal view returns (uint){
    IPriceOracle priceOracle = AppLib._getPriceOracle(converter_);
    uint priceCollateral = priceOracle.getAssetPrice(collateralAsset_);
    uint priceBorrow = priceOracle.getAssetPrice(borrowAsset_);
    return 1e18 * priceBorrow * 10 ** IERC20Metadata(collateralAsset_).decimals()
    / priceCollateral / 10 ** IERC20Metadata(borrowAsset_).decimals();
  }

  /// @notice Close the given position, pay {amountToRepay}, return collateral amount in result
  ///         It doesn't repay more than the actual amount of the debt, so it can use less amount than {amountToRepay}
  /// @param amountToRepay Amount to repay in terms of {borrowAsset}
  /// @return returnedAssetAmountOut Amount of collateral received back after repaying
  /// @return repaidAmountOut Amount that was actually repaid
  function _closePosition(
    ITetuConverter converter_,
    address collateralAsset,
    address borrowAsset,
    uint amountToRepay
  ) internal returns (
    uint returnedAssetAmountOut,
    uint repaidAmountOut
  ) {

    uint balanceBefore = IERC20(borrowAsset).balanceOf(address(this));

    // We shouldn't try to pay more than we actually need to repay
    // The leftover will be swapped inside TetuConverter, it's inefficient.
    // Let's limit amountToRepay by needToRepay-amount
    (uint needToRepay,) = converter_.getDebtAmountCurrent(address(this), collateralAsset, borrowAsset, true);
    uint amountRepay = Math.min(amountToRepay < needToRepay ? amountToRepay : needToRepay, balanceBefore);

    return _closePositionExact(converter_, collateralAsset, borrowAsset, amountRepay, balanceBefore);
  }

  /// @notice Close the given position, pay {amountRepay} exactly and ensure that all amount was accepted,
  /// @param amountRepay Amount to repay in terms of {borrowAsset}
  /// @param balanceBorrowAsset Current balance of the borrow asset
  /// @return collateralOut Amount of collateral received back after repaying
  /// @return repaidAmountOut Amount that was actually repaid
  function _closePositionExact(
    ITetuConverter converter_,
    address collateralAsset,
    address borrowAsset,
    uint amountRepay,
    uint balanceBorrowAsset
  ) internal returns (
    uint collateralOut,
    uint repaidAmountOut
  ) {
    if (amountRepay >= AppLib.DUST_AMOUNT_TOKENS) {
      // Make full/partial repayment
      IERC20(borrowAsset).safeTransfer(address(converter_), amountRepay);

      uint notUsedAmount;
      (collateralOut, notUsedAmount,,) = converter_.repay(collateralAsset, borrowAsset, amountRepay, address(this));

      emit ClosePosition(collateralAsset, borrowAsset, amountRepay, address(this), collateralOut, notUsedAmount);
      uint balanceAfter = IERC20(borrowAsset).balanceOf(address(this));

      // we cannot use amountRepay here because AAVE pool adapter is able to send tiny amount back (debt-gap)
      repaidAmountOut = balanceBorrowAsset > balanceAfter
        ? balanceBorrowAsset - balanceAfter
        : 0;
      require(notUsedAmount == 0, StrategyLib2.WRONG_VALUE);
    }

    return (collateralOut, repaidAmountOut);
  }

  /// @notice Close the given position, pay {amountToRepay}, return collateral amount in result
  /// @param amountToRepay Amount to repay in terms of {borrowAsset}
  /// @return returnedAssetAmountOut Amount of collateral received back after repaying
  /// @return repaidAmountOut Amount that was actually repaid
  function closePosition(
    ITetuConverter tetuConverter_,
    address collateralAsset,
    address borrowAsset,
    uint amountToRepay
  ) external returns (
    uint returnedAssetAmountOut,
    uint repaidAmountOut
  ) {
    return _closePosition(tetuConverter_, collateralAsset, borrowAsset, amountToRepay);
  }
//endregion--------------------------------------------------- Borrow and close positions

//region--------------------------------------------------- Liquidation

  /// @notice Make liquidation if estimated amountOut exceeds the given threshold
  /// @param liquidationThresholdForTokenIn_ Liquidation threshold for {amountIn_}
  /// @param skipValidation Don't check correctness of conversion using TetuConverter's oracle (i.e. for reward tokens)
  /// @return spentAmountIn Amount of {tokenIn} has been consumed by the liquidator
  /// @return receivedAmountOut Amount of {tokenOut_} has been returned by the liquidator
  function liquidate(
    ITetuConverter converter,
    ITetuLiquidator liquidator_,
    address tokenIn_,
    address tokenOut_,
    uint amountIn_,
    uint slippage_,
    uint liquidationThresholdForTokenIn_,
    bool skipValidation
  ) external returns (
    uint spentAmountIn,
    uint receivedAmountOut
  ) {
    return _liquidate(converter, liquidator_, tokenIn_, tokenOut_, amountIn_, slippage_, liquidationThresholdForTokenIn_, skipValidation);
  }

  /// @notice Make liquidation if estimated amountOut exceeds the given threshold
  /// @param liquidationThresholdForTokenIn_ Liquidation threshold for {amountIn_}
  /// @param skipValidation Don't check correctness of conversion using TetuConverter's oracle (i.e. for reward tokens)
  /// @return spentAmountIn Amount of {tokenIn} has been consumed by the liquidator (== 0 | amountIn_)
  /// @return receivedAmountOut Amount of {tokenOut_} has been returned by the liquidator
  function _liquidate(
    ITetuConverter converter_,
    ITetuLiquidator liquidator_,
    address tokenIn_,
    address tokenOut_,
    uint amountIn_,
    uint slippage_,
    uint liquidationThresholdForTokenIn_,
    bool skipValidation
  ) internal returns (
    uint spentAmountIn,
    uint receivedAmountOut
  ) {
    // we check amountIn by threshold, not amountOut
    // because {_closePositionsToGetAmount} is implemented in {get plan, make action}-way
    // {_closePositionsToGetAmount} can be used with swap by aggregators, where amountOut cannot be calculate
    // at the moment of plan building. So, for uniformity, only amountIn is checked everywhere

    if (amountIn_ <= liquidationThresholdForTokenIn_) {
      return (0, 0);
    }

    (ITetuLiquidator.PoolData[] memory route,) = liquidator_.buildRoute(tokenIn_, tokenOut_);

    require(route.length != 0, AppErrors.NO_LIQUIDATION_ROUTE);

    // if the expected value is higher than threshold distribute to destinations
    return (amountIn_, _liquidateWithRoute(converter_, route, liquidator_, tokenIn_, tokenOut_, amountIn_, slippage_, skipValidation));
  }

  /// @notice Make liquidation using given route and check correctness using TetuConverter's price oracle
  /// @param skipValidation Don't check correctness of conversion using TetuConverter's oracle (i.e. for reward tokens)
  function _liquidateWithRoute(
    ITetuConverter converter_,
    ITetuLiquidator.PoolData[] memory route,
    ITetuLiquidator liquidator_,
    address tokenIn_,
    address tokenOut_,
    uint amountIn_,
    uint slippage_,
    bool skipValidation
  ) internal returns (
    uint receivedAmountOut
  ) {
    // we need to approve each time, liquidator address can be changed in controller
    AppLib.approveIfNeeded(tokenIn_, amountIn_, address(liquidator_));

    uint balanceBefore = IERC20(tokenOut_).balanceOf(address(this));
    liquidator_.liquidateWithRoute(route, amountIn_, slippage_);
    uint balanceAfter = IERC20(tokenOut_).balanceOf(address(this));

    require(balanceAfter > balanceBefore, AppErrors.BALANCE_DECREASE);
    receivedAmountOut = balanceAfter - balanceBefore;

    // Oracle in TetuConverter "knows" only limited number of the assets
    // It may not know prices for reward assets, so for rewards this validation should be skipped to avoid TC-4 error
    require(skipValidation || converter_.isConversionValid(tokenIn_, amountIn_, tokenOut_, receivedAmountOut, slippage_), AppErrors.PRICE_IMPACT);
    emit Liquidation(tokenIn_, tokenOut_, amountIn_, amountIn_, receivedAmountOut);
  }
//endregion--------------------------------------------------- Liquidation

//region--------------------------------------------------- Recycle rewards

  /// @notice Recycle the amounts: liquidate a part of each amount, send the other part to the forwarder.
  /// We have two kinds of rewards:
  /// 1) rewards in depositor's assets (the assets returned by _depositorPoolAssets)
  /// 2) any other rewards
  /// All received rewards divided on three parts: to performance receiver+insurance, to forwarder, to compound
  ///   Compound-part of Rewards-2 can be liquidated
  ///   Compound part of Rewards-1 should be just left on the balance
  ///   Performance amounts should be liquidate, result underlying should be sent to performance receiver and insurance.
  ///   All forwarder-parts are returned in amountsToForward and should be transferred to the forwarder outside.
  /// @dev {_recycle} is implemented as separate (inline) function to simplify unit testing
  /// @param rewardTokens_ Full list of reward tokens received from tetuConverter and depositor
  /// @param rewardAmounts_ Amounts of {rewardTokens_}; we assume, there are no zero amounts here
  function recycle(
    IStrategyV3.BaseState storage baseState,
    IConverterStrategyBase.ConverterStrategyBaseState storage csbs,
    address[] memory tokens,
    address controller,
    mapping(address => uint) storage liquidationThresholds,
    address[] memory rewardTokens_,
    uint[] memory rewardAmounts_
  ) external {
    RecycleLocal memory v;
    v.asset = baseState.asset;
    v.compoundRatio = baseState.compoundRatio;
    v.performanceFee = baseState.performanceFee;
    v.thresholds = _getLiquidationThresholds(liquidationThresholds, rewardTokens_, rewardTokens_.length);
    v.debtToInsuranceCurrent = csbs.debtToInsurance;
    v.splitter = baseState.splitter;

    (v.amountsToForward, v.amountPerf, v.debtToInsuranceUpdated) = _recycle(RecycleParams({
      converter: csbs.converter,
      liquidator: AppLib._getLiquidator(controller),
      asset: v.asset,
      compoundRatio: v.compoundRatio,
      tokens: tokens,
      thresholds: v.thresholds,
      rewardTokens: rewardTokens_,
      rewardAmounts: rewardAmounts_,
      performanceFee: v.performanceFee,
      debtToInsurance: v.debtToInsuranceCurrent,
      splitter: v.splitter,
      assetThreshold: AppLib._getLiquidationThreshold(liquidationThresholds[v.asset])
    }));

    if (v.debtToInsuranceCurrent != v.debtToInsuranceUpdated) {
      csbs.debtToInsurance = v.debtToInsuranceUpdated;
      emit OnPayDebtToInsurance(v.debtToInsuranceCurrent, v.debtToInsuranceUpdated);
    }

    // send performance-part of the underlying to the performance receiver and insurance
    (v.toPerf, v.toInsurance) = _sendPerformanceFee(
      v.asset,
      v.amountPerf,
      v.splitter,
      baseState.performanceReceiver,
      baseState.performanceFeeRatio
    );

    // override rewardTokens_, v.amountsToForward by the values actually sent to the forwarder
    (rewardTokens_, v.amountsToForward) = _sendTokensToForwarder(controller, v.splitter, rewardTokens_, v.amountsToForward, v.thresholds);

    emit Recycle(rewardTokens_, v.amountsToForward, v.toPerf, v.toInsurance);
  }

  /// @notice Send {amount_} of {asset_} to {receiver_} and insurance
  /// @param asset_ Underlying asset
  /// @param amount_ Amount of underlying asset to be sent to
  /// @param receiver_ Performance receiver
  /// @param ratio [0..100_000], 100_000 - send full amount to perf, 0 - send full amount to the insurance.
  function _sendPerformanceFee(address asset_, uint amount_, address splitter, address receiver_, uint ratio) internal returns (
    uint toPerf,
    uint toInsurance
  ) {
    // read inside lib for reduce contract space in the main contract
    address insurance = address(ITetuVaultV2(ISplitter(splitter).vault()).insurance());

    toPerf = amount_ * ratio / AppLib.DENOMINATOR;
    toInsurance = amount_ - toPerf;

    if (toPerf != 0) {
      IERC20(asset_).safeTransfer(receiver_, toPerf);
    }
    if (toInsurance != 0) {
      IERC20(asset_).safeTransfer(insurance, toInsurance);
    }
  }

  /// @notice Send {amounts_} to forwarder, skip amounts < thresholds (see SCB-812)
  /// @return tokensOut Tokens sent to the forwarder
  /// @return amountsOut Amounts sent to the forwarder
  function _sendTokensToForwarder(
    address controller_,
    address splitter_,
    address[] memory tokens_,
    uint[] memory amounts_,
    uint[] memory thresholds_
  ) internal returns (
    address[] memory tokensOut,
    uint[] memory amountsOut
  ) {
    uint len = tokens_.length;
    IForwarder forwarder = IForwarder(IController(controller_).forwarder());
    for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
      if (thresholds_[i] > amounts_[i]) {
        amounts_[i] = 0; // it will be excluded in filterZeroAmounts() below
      } else {
        AppLib.approveIfNeeded(tokens_[i], amounts_[i], address(forwarder));
      }
    }

    (tokensOut, amountsOut) = TokenAmountsLib.filterZeroAmounts(tokens_, amounts_);
    if (tokensOut.length != 0) {
      forwarder.registerIncome(tokensOut, amountsOut, ISplitter(splitter_).vault(), true);
    }
  }

  /// @notice Recycle the amounts: split each amount on tree parts: performance+insurance (P), forwarder (F), compound (C)
  ///         Liquidate P+C, send F to the forwarder.
  /// We have two kinds of rewards:
  /// 1) rewards in depositor's assets (the assets returned by _depositorPoolAssets)
  /// 2) any other rewards
  /// All received rewards divided on three parts: to performance receiver+insurance, to forwarder, to compound
  ///   Compound-part of Rewards-2 can be liquidated
  ///   Compound part of Rewards-1 should be just left on the balance
  ///   All forwarder-parts are returned in amountsToForward and should be transferred to the forwarder outside.
  ///   Performance amounts are liquidated, result amount of underlying is returned in {amountToPerformanceAndInsurance}
  /// @return amountsToForward Amounts of {rewardTokens} to be sent to forwarder, zero amounts are allowed here
  /// @return amountToPerformanceAndInsurance Amount of underlying to be sent to performance receiver and insurance
  /// @return debtToInsuranceOut Remain debt to the insurance [in underlying]
  function _recycle(RecycleParams memory p) internal returns (
    uint[] memory amountsToForward,
    uint amountToPerformanceAndInsurance,
    int debtToInsuranceOut
  ) {
    RecycleLocalParams memory v;

    v.len = p.rewardTokens.length;
    require(v.len == p.rewardAmounts.length, AppErrors.WRONG_LENGTHS);

    amountsToForward = new uint[](v.len);

    // rewardAmounts => P + F + C, where P - performance + insurance, F - forwarder, C - compound
    for (uint i; i < v.len; i = AppLib.uncheckedInc(i)) {
      // if we have a debt-to-insurance we should firstly cover the debt using all available rewards
      // and only then we can use leftovers of the rewards for other needs
      if (p.debtToInsurance > int(p.assetThreshold)) {
        (p.rewardAmounts[i], p.debtToInsurance) = _coverDebtToInsuranceFromRewards(p, i, uint(p.debtToInsurance));
        if (p.rewardAmounts[i] < p.thresholds[i]) continue;
      }

      v.amountFC = p.rewardAmounts[i] * (COMPOUND_DENOMINATOR - p.performanceFee) / COMPOUND_DENOMINATOR;
      v.amountC = v.amountFC * p.compoundRatio / COMPOUND_DENOMINATOR;
      v.amountP = p.rewardAmounts[i] - v.amountFC;
      v.rewardToken = p.rewardTokens[i];
      v.amountCP = v.amountC + v.amountP;

      if (v.amountCP > 0) {
        if (AppLib.getAssetIndex(p.tokens, v.rewardToken) != type(uint).max) {
          if (v.rewardToken == p.asset) {
            // This is underlying, liquidation of compound part is not allowed; just keep on the balance, should be handled later
            amountToPerformanceAndInsurance += v.amountP;
          } else {
            // This is secondary asset, Liquidation of compound part is not allowed, we should liquidate performance part only
            // If the performance amount is too small, liquidation will not happen and we will just keep that dust tokens on balance forever
            (, v.receivedAmountOut) = _liquidate(
              p.converter,
              p.liquidator,
              v.rewardToken,
              p.asset,
              v.amountP,
              _REWARD_LIQUIDATION_SLIPPAGE,
              p.thresholds[i],
              false // use conversion validation for these rewards
            );
            amountToPerformanceAndInsurance += v.receivedAmountOut;
          }
        } else {
          // If amount is too small, the liquidation won't be allowed and we will just keep that dust tokens on balance forever
          // The asset is not in the list of depositor's assets, its amount is big enough and should be liquidated
          // We assume here, that {token} cannot be equal to {_asset}
          // because the {_asset} is always included to the list of depositor's assets
          (, v.receivedAmountOut) = _liquidate(
            p.converter,
            p.liquidator,
            v.rewardToken,
            p.asset,
            v.amountCP,
            _REWARD_LIQUIDATION_SLIPPAGE,
            p.thresholds[i],
            true // skip conversion validation for rewards because we can have arbitrary assets here
          );
          amountToPerformanceAndInsurance += v.receivedAmountOut * (p.rewardAmounts[i] - v.amountFC) / v.amountCP;
        }
      }
      amountsToForward[i] = v.amountFC - v.amountC;
    }
    return (amountsToForward, amountToPerformanceAndInsurance, p.debtToInsurance);
  }

  /// @notice Try to cover {p.debtToInsurance} using available rewards of {p.rewardTokens[index]}
  /// @param index Index of the reward token in {p.rewardTokens}
  /// @param debtAmount Debt to insurance that should be covered by the reward tokens
  /// @return rewardsLeftovers Amount of unused reward tokens (it can be used for other needs)
  /// @return debtToInsuranceOut New value of the debt to the insurance
  function _coverDebtToInsuranceFromRewards(RecycleParams memory p, uint index, uint debtAmount) internal returns (
    uint rewardsLeftovers,
    int debtToInsuranceOut
  ) {
    uint spentAmount;
    uint amountToSend;

    if (p.asset == p.rewardTokens[index]) {
      // assume p.debtToInsurance > 0 here
      spentAmount = Math.min(debtAmount, p.rewardAmounts[index]);
      amountToSend = spentAmount;
    } else {
      // estimate amount of underlying that we can receive for the available amount of the reward tokens
      uint amountAsset = p.rewardAmounts[index] > p.assetThreshold
        ? p.liquidator.getPrice(p.rewardTokens[index], p.asset, p.rewardAmounts[index])
        : 0;
      uint amountIn;

      if (amountAsset > debtAmount + p.assetThreshold) {
        // pay a part of the rewards to cover the debt completely
        amountIn = p.rewardAmounts[index] * debtAmount / amountAsset;
      } else {
        // pay all available rewards to cover a part of the debt
        amountIn = p.rewardAmounts[index];
      }

      (spentAmount, amountToSend) = _liquidate(
        p.converter,
        p.liquidator,
        p.rewardTokens[index],
        p.asset,
        amountIn,
        _REWARD_LIQUIDATION_SLIPPAGE,
        p.thresholds[index],
        true // skip conversion validation for rewards because we can have arbitrary assets here
      );
    }

    IERC20(p.asset).safeTransfer(address(ITetuVaultV2(ISplitter(p.splitter).vault()).insurance()), amountToSend);

    rewardsLeftovers = AppLib.sub0(p.rewardAmounts[index], spentAmount);
    debtToInsuranceOut = int(debtAmount) - int(amountToSend);

    emit OnCoverDebtToInsurance(p.rewardTokens[index], spentAmount, debtAmount, debtToInsuranceOut);
  }
//endregion----------------------------------------------- Recycle rewards

//region--------------------------------------------------- Before deposit
  /// @notice Default implementation of ConverterStrategyBase.beforeDeposit
  /// @param amount_ Amount of underlying to be deposited
  /// @param tokens_ Tokens received from {_depositorPoolAssets}
  /// @param indexAsset_ Index of main {asset} in {tokens}
  /// @param weights_ Depositor pool weights
  /// @param totalWeight_ Sum of {weights_}
  function beforeDeposit(
    ITetuConverter converter_,
    uint amount_,
    address[] memory tokens_,
    uint indexAsset_,
    uint[] memory weights_,
    uint totalWeight_,
    mapping(address => uint) storage liquidationThresholds
  ) external returns (
    uint[] memory tokenAmounts
  ) {
    // temporary save collateral to tokensAmounts
    tokenAmounts = _getCollaterals(amount_, tokens_, weights_, totalWeight_, indexAsset_, AppLib._getPriceOracle(converter_));

    // make borrow and save amounts of tokens available for deposit to tokenAmounts, zero result amounts are possible
    tokenAmounts = _getTokenAmounts(
      converter_,
      tokens_,
      indexAsset_,
      tokenAmounts,
      AppLib._getLiquidationThreshold(liquidationThresholds[tokens_[indexAsset_]])
    );
  }

  /// @notice For each {token_} calculate a part of {amount_} to be used as collateral according to the weights.
  ///         I.e. we have 300 USDC, we need to split it on 100 USDC, 100 USDT, 100 DAI
  ///         USDC is main asset, USDT and DAI should be borrowed. We check amounts of USDT and DAI on the balance
  ///         and return collaterals reduced on that amounts. For main asset, we return full amount always (100 USDC).
  /// @param tokens_ Tokens received from {_depositorPoolAssets}
  /// @param indexAsset_ Index of main {asset} in {tokens}
  /// @return tokenAmountsOut Length of the array is equal to the length of {tokens_}
  function _getCollaterals(
    uint amount_,
    address[] memory tokens_,
    uint[] memory weights_,
    uint totalWeight_,
    uint indexAsset_,
    IPriceOracle priceOracle
  ) internal view returns (
    uint[] memory tokenAmountsOut
  ) {
    uint len = tokens_.length;
    tokenAmountsOut = new uint[](len);

    // get token prices and decimals
    (uint[] memory prices, uint[] memory decs) = AppLib._getPricesAndDecs(priceOracle, tokens_, len);

    // split the amount on tokens proportionally to the weights
    for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
      uint amountAssetForToken = amount_ * weights_[i] / totalWeight_;

      if (i == indexAsset_) {
        tokenAmountsOut[i] = amountAssetForToken;
      } else {
        // if we have some tokens on balance then we need to use only a part of the collateral
        uint tokenAmountToBeBorrowed = amountAssetForToken
          * prices[indexAsset_]
          * decs[i]
          / prices[i]
          / decs[indexAsset_];

        uint tokenBalance = IERC20(tokens_[i]).balanceOf(address(this));
        if (tokenBalance < tokenAmountToBeBorrowed) {
          tokenAmountsOut[i] = amountAssetForToken * (tokenAmountToBeBorrowed - tokenBalance) / tokenAmountToBeBorrowed;
        }
      }
    }
  }

  /// @notice Make borrow and return amounts of {tokens} available to deposit
  /// @param tokens_ Tokens received from {_depositorPoolAssets}
  /// @param indexAsset_ Index of main {asset} in {tokens}
  /// @param collaterals_ Amounts of main asset that can be used as collateral to borrow {tokens_}
  /// @param thresholdAsset_ Value of liquidation threshold for the main (collateral) asset
  /// @return tokenAmountsOut Amounts of {tokens}  available to deposit
  function _getTokenAmounts(
    ITetuConverter converter_,
    address[] memory tokens_,
    uint indexAsset_,
    uint[] memory collaterals_,
    uint thresholdAsset_
  ) internal returns (
    uint[] memory tokenAmountsOut
  ) {
    // content of tokenAmounts will be modified in place
    uint len = tokens_.length;
    tokenAmountsOut = new uint[](len);
    address asset = tokens_[indexAsset_];

    for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
      if (i != indexAsset_) {
        address token = tokens_[i];
        if (collaterals_[i] != 0) {
          AppLib.approveIfNeeded(asset, collaterals_[i], address(converter_));
          _openPosition(
            converter_,
            "", // entry kind = 0: fixed collateral amount, max possible borrow amount
            asset,
            token,
            collaterals_[i],
            thresholdAsset_
          );

          // zero borrowed amount is possible here (conversion is not available)
          // if it's not suitable for depositor, the depositor should check zero amount in other places
        }
        tokenAmountsOut[i] = IERC20(token).balanceOf(address(this));
      }
    }

    tokenAmountsOut[indexAsset_] = Math.min(
      collaterals_[indexAsset_],
      IERC20(asset).balanceOf(address(this))
    );
  }
//endregion--------------------------------------------------- Before deposit

//region--------------------------------------------------- Make requested amount

  /// @notice Convert {amountsToConvert_} to the given {asset}
  ///         Swap leftovers (if any) to the given asset.
  ///         If result amount is less than expected, try to close any other available debts (1 repay per block only)
  /// @param tokens_ Results of _depositorPoolAssets() call (list of depositor's asset in proper order)
  /// @param indexAsset_ Index of the given {asset} in {tokens}
  /// @param requestedBalance Total amount of the given asset that we need to have on balance at the end.
  ///                         Max uint means attempt to withdraw all possible amount.
  /// @return expectedBalance Expected asset balance after all swaps and repays
  function makeRequestedAmount(
    address[] memory tokens_,
    uint indexAsset_,
    ITetuConverter converter_,
    ITetuLiquidator liquidator_,
    uint requestedBalance,
    mapping(address => uint) storage liquidationThresholds_
  ) external returns (uint expectedBalance) {
    DataSetLocal memory v = DataSetLocal({
      len: tokens_.length,
      converter: converter_,
      tokens: tokens_,
      indexAsset: indexAsset_,
      liquidator: liquidator_
    });
    uint[] memory _liquidationThresholds = _getLiquidationThresholds(liquidationThresholds_, v.tokens, v.len);
    expectedBalance = _closePositionsToGetAmount(v, _liquidationThresholds, requestedBalance);
  }
  //endregion-------------------------------------------- Make requested amount

//region ------------------------------------------------ Close position
  /// @notice Close debts (if it's allowed) in converter until we don't have {requestedAmount} on balance
  /// @dev We assume here that this function is called before closing any positions in the current block
  /// @param liquidationThresholds Min allowed amounts-out for liquidations
  /// @param requestedBalance Total amount of the given asset that we need to have on balance at the end.
  ///                         Max uint means attempt to withdraw all possible amount.
  /// @return expectedBalance Expected asset balance after all swaps and repays
  function closePositionsToGetAmount(
    ITetuConverter converter_,
    ITetuLiquidator liquidator,
    uint indexAsset,
    mapping(address => uint) storage liquidationThresholds,
    uint requestedBalance,
    address[] memory tokens
  ) external returns (
    uint expectedBalance
  ) {
    uint len = tokens.length;
    return _closePositionsToGetAmount(
      DataSetLocal({
        len: len,
        converter: converter_,
        tokens: tokens,
        indexAsset: indexAsset,
        liquidator: liquidator
      }),
      _getLiquidationThresholds(liquidationThresholds, tokens, len),
      requestedBalance
    );
  }

  /// @notice Close debts (if it's allowed) in converter until we don't have {requestedAmount} on balance
  /// @dev Implements {IterationPlanLib.PLAN_SWAP_REPAY} only
  ///      Note: AAVE3 allows to make two repays in a single block, see Aave3SingleBlockTest in TetuConverter
  ///      but it doesn't allow to make borrow and repay in a single block.
  /// @param liquidationThresholds_ Min allowed amounts-out for liquidations
  /// @param requestedBalance Total amount of the given asset that we need to have on balance at the end.
  ///                         Max uint means attempt to withdraw all possible amount.
  /// @return expectedBalance Expected asset balance after all swaps and repays
  function _closePositionsToGetAmount(
    DataSetLocal memory d_,
    uint[] memory liquidationThresholds_,
    uint requestedBalance
  ) internal returns (
    uint expectedBalance
  ) {
    if (requestedBalance != 0) {
      //let's get a bit more amount on balance to prevent situation "zero balance, not-zero debts"
      requestedBalance = applyRequestedBalanceGap(requestedBalance);
      CloseDebtsForRequiredAmountLocal memory v;
      v.asset = d_.tokens[d_.indexAsset];

      // v.planKind = IterationPlanLib.PLAN_SWAP_REPAY; // PLAN_SWAP_REPAY == 0, so we don't need this line
      v.balanceAdditions = new uint[](d_.len);
      expectedBalance = IERC20(v.asset).balanceOf(address(this));

      (v.prices, v.decs) = AppLib._getPricesAndDecs(AppLib._getPriceOracle(d_.converter), d_.tokens, d_.len);

      for (uint i; i < d_.len; i = AppLib.uncheckedInc(i)) {
        if (i == d_.indexAsset) continue;

        v.balanceAsset = IERC20(v.asset).balanceOf(address(this));
        v.balanceToken = IERC20(d_.tokens[i]).balanceOf(address(this));

        // Make one or several iterations. Do single swap and single repaying (both are optional) on each iteration.
        // Calculate expectedAmount of received underlying. Swap leftovers at the end even if requestedAmount is 0 at that moment.
        do {
          // generate iteration plan: [swap], [repay]
          (v.idxToSwap1, v.amountToSwap, v.idxToRepay1) = IterationPlanLib.buildIterationPlan(
            [address(d_.converter), address(d_.liquidator)],
            d_.tokens,
            liquidationThresholds_,
            v.prices,
            v.decs,
            v.balanceAdditions,
            [0, IterationPlanLib.PLAN_SWAP_REPAY, 0, requestedBalance, d_.indexAsset, i, 0]
          );
          if (v.idxToSwap1 == 0 && v.idxToRepay1 == 0) break;

          // make swap if necessary
          uint spentAmountIn;
          if (v.idxToSwap1 != 0) {
            uint indexIn = v.idxToSwap1 - 1;
            uint indexOut = indexIn == d_.indexAsset ? i : d_.indexAsset;
            (spentAmountIn,) = _liquidate(
              d_.converter,
              d_.liquidator,
              d_.tokens[indexIn],
              d_.tokens[indexOut],
              v.amountToSwap,
              _ASSET_LIQUIDATION_SLIPPAGE,
              liquidationThresholds_[indexIn],
              false
            );

            if (indexIn == d_.indexAsset) {
              expectedBalance = AppLib.sub0(expectedBalance, spentAmountIn);
            } else if (indexOut == d_.indexAsset) {
              expectedBalance += spentAmountIn * v.prices[i] * v.decs[d_.indexAsset] / v.prices[d_.indexAsset] / v.decs[i];

              // if we already received enough amount on balance, we can avoid additional actions
              // to avoid high gas consumption in the cases like SCB-787
              uint balanceAsset = IERC20(v.asset).balanceOf(address(this));
              if (balanceAsset + liquidationThresholds_[d_.indexAsset] > requestedBalance) {
                v.balanceAsset = balanceAsset;
                break;
              }
            }
          }

          // repay a debt if necessary
          if (v.idxToRepay1 != 0) {
            uint indexBorrow = v.idxToRepay1 - 1;
            uint indexCollateral = indexBorrow == d_.indexAsset ? i : d_.indexAsset;
            uint amountToRepay = IERC20(d_.tokens[indexBorrow]).balanceOf(address(this));

            (uint expectedAmountOut, uint repaidAmountOut, uint amountSendToRepay) = _repayDebt(
              d_.converter,
              d_.tokens[indexCollateral],
              d_.tokens[indexBorrow],
              amountToRepay
            );

            if (indexBorrow == d_.indexAsset) {
              expectedBalance = expectedBalance > amountSendToRepay
                ? expectedBalance - amountSendToRepay
                : 0;
            } else if (indexCollateral == d_.indexAsset) {
              require(expectedAmountOut >= spentAmountIn, AppErrors.BALANCE_DECREASE);
              if (repaidAmountOut < amountSendToRepay) {
                // SCB-779: expectedAmountOut was estimated for amountToRepay, but we have paid repaidAmountOut only
                expectedBalance += expectedAmountOut * repaidAmountOut / amountSendToRepay;
              } else {
                expectedBalance += expectedAmountOut;
              }
            }
          }

          // update balances
          v.newBalanceAsset = IERC20(v.asset).balanceOf(address(this));
          v.newBalanceToken = IERC20(d_.tokens[i]).balanceOf(address(this));

          v.exitLoop = (v.balanceAsset == v.newBalanceAsset && v.balanceToken == v.newBalanceToken);
          v.balanceAsset = v.newBalanceAsset;
          v.balanceToken = v.newBalanceToken;
        } while (!v.exitLoop);

        if (v.balanceAsset + liquidationThresholds_[d_.indexAsset] > requestedBalance) break;
      }
    }

    return expectedBalance;
  }
//endregion ------------------------------------------------ Close position

//region ------------------------------------------------ Repay debts
  /// @notice Repay {amountIn} and get collateral in return, calculate expected amount
  ///         Take into account possible debt-gap and the fact that the amount of debt may be less than {amountIn}
  /// @param amountToRepay Max available amount of borrow asset that we can repay
  /// @return expectedAmountOut Estimated amount of main asset that should be added to balance = collateral - {toSell}
  /// @return repaidAmountOut Actually paid amount
  /// @return amountSendToRepay Amount send to repay
  function _repayDebt(
    ITetuConverter converter,
    address collateralAsset,
    address borrowAsset,
    uint amountToRepay
  ) internal returns (
    uint expectedAmountOut,
    uint repaidAmountOut,
    uint amountSendToRepay
  ) {
    uint balanceBefore = IERC20(borrowAsset).balanceOf(address(this));

    // get amount of debt with debt-gap
    (uint needToRepay,) = converter.getDebtAmountCurrent(address(this), collateralAsset, borrowAsset, true);
    amountSendToRepay = Math.min(amountToRepay < needToRepay ? amountToRepay : needToRepay, balanceBefore);

    // get expected amount without debt-gap
    uint swappedAmountOut;
    (expectedAmountOut, swappedAmountOut) = converter.quoteRepay(address(this), collateralAsset, borrowAsset, amountSendToRepay);

    if (expectedAmountOut > swappedAmountOut) {
      // SCB-789 Following situation is possible
      //    needToRepay = 100, needToRepayExact = 90 (debt gap is 10)
      //    1) amountRepay = 80
      //       expectedAmountOut is calculated for 80, no problems
      //    2) amountRepay = 99,
      //       expectedAmountOut is calculated for 90 + 9 (90 - repay, 9 - direct swap)
      //       expectedAmountOut must be reduced on 9 here (!)
      expectedAmountOut -= swappedAmountOut;
    }

    // close the debt
    (, repaidAmountOut) = _closePositionExact(converter, collateralAsset, borrowAsset, amountSendToRepay, balanceBefore);

    return (expectedAmountOut, repaidAmountOut, amountSendToRepay);
  }
  //endregion ------------------------------------------------ Repay debts

//region------------------------------------------------ Other helpers

  /// @return liquidationThresholdsOut Liquidation thresholds of the {tokens_}, result values > 0
  function _getLiquidationThresholds(
    mapping(address => uint) storage liquidationThresholds,
    address[] memory tokens_,
    uint len
  ) internal view returns (
    uint[] memory liquidationThresholdsOut
  ) {
    liquidationThresholdsOut = new uint[](len);
    for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
      liquidationThresholdsOut[i] = AppLib._getLiquidationThreshold(liquidationThresholds[tokens_[i]]);
    }
  }

  function applyRequestedBalanceGap(uint amount_) internal pure returns (uint) {
    return amount_ == type(uint).max
      ? amount_
      : amount_ * (COMPOUND_DENOMINATOR + REQUESTED_BALANCE_GAP) / COMPOUND_DENOMINATOR;
  }
//endregion--------------------------------------------- Other helpers
}

","keccak256":"0x16389df1a6afe915da264e33522940a9d9272cce6f0a6a487743ea2cc66ae2f3","license":"BUSL-1.1"},"contracts/strategies/ConverterStrategyBaseLib2.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "@tetu_io/tetu-contracts-v2/contracts/interfaces/IForwarder.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/ITetuVaultV2.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/ISplitter.sol";
import "@tetu_io/tetu-contracts-v2/contracts/strategy/StrategyLib.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/IPriceOracle.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/ITetuConverter.sol";
import "@tetu_io/tetu-contracts-v2/contracts/openzeppelin/Math.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/ITetuLiquidator.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/IConverterController.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/IStrategyV3.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/IBookkeeper.sol";
import "../libs/AppErrors.sol";
import "../libs/AppLib.sol";
import "../libs/TokenAmountsLib.sol";
import "../libs/ConverterEntryKinds.sol";
import "../interfaces/IConverterStrategyBase.sol";

/// @notice Continuation of ConverterStrategyBaseLib (workaround for size limits)
library ConverterStrategyBaseLib2 {
  using SafeERC20 for IERC20;

//region --------------------------------------- Data types
  struct CalcInvestedAssetsLocal {
    uint len;
    uint[] debts;
    address asset;
    address token;
  }
//endregion --------------------------------------- Data types

//region --------------------------------------- CONSTANTS
  uint internal constant DENOMINATOR = 100_000;

  /// @dev 0.5% of max loss for strategy TVL
  /// @notice Same value as StrategySplitterV2.HARDWORK_LOSS_TOLERANCE
  uint public constant HARDWORK_LOSS_TOLERANCE = 500;

  /// @dev 0.5% of max profit for strategy TVL
  /// @notice Limit max amount of profit that can be send to insurance after price changing
  uint public constant PRICE_CHANGE_PROFIT_TOLERANCE = HARDWORK_LOSS_TOLERANCE;

//endregion --------------------------------------- CONSTANTS

//region----------------------------------------- EVENTS
  event LiquidationThresholdChanged(address token, uint amount);
  event ReinvestThresholdPercentChanged(uint amount);
  event SendToInsurance(uint sentAmount, uint unsentAmount);

  /// @notice Increase to debts between new and previous checkpoints.
  /// @param tokens List of possible collateral/borrow assets. One of the is unerlying.
  /// @param deltaGains Amounts by which the debt has reduced (supply profit) [sync with {tokens}]
  /// @param deltaLosses Amounts by which the debt has increased (increase of amount-to-pay) [sync with {tokens}]
  /// @param prices Prices of the {tokens}
  /// @param increaseToDebt Total amount of increasing of the debt to the insurance in underlying
  event OnIncreaseDebtToInsurance(
    address[] tokens,
    uint[] deltaGains,
    uint[] deltaLosses,
    uint[] prices,
    int increaseToDebt
  );

  /// @param increaseToDebt The value on which the debt to insurance was increased
  /// @param debtToInsuranceBefore Value of the debt to insurance before fix price change
  /// @param debtToInsuranceAfter New value of the debt to insurance
  /// @param increaseToDebt Amount on which debt was increased.
  /// Actual value {debtToInsuranceAfter}-{debtToInsuranceBefore} can be less than increaseToDebt
  /// because some amount can be left uncovered.
  event FixPriceChanges(
    uint investedAssetsBefore,
    uint investedAssetsOut,
    int debtToInsuranceBefore,
    int debtToInsuranceAfter,
    int increaseToDebt
  );

  /// @param lossToCover Amount of loss that should be covered (it fits to allowed limits, no revert)
  /// @param debtToInsuranceInc The amount by which the debt to insurance increases
  /// @param amountCovered Actually covered amount of loss. If amountCovered < lossToCover => the insurance is not enough
  /// @param lossUncovered Amount of uncovered losses (not enough insurance)
  event OnCoverLoss(
    uint lossToCover,
    int debtToInsuranceInc,
    uint amountCovered,
    uint lossUncovered
  );

  /// @notice Value of {debtToInsurance} was increased on {increaseToDebt} inside fix-price-change
  /// in the case when invested-asset amounts were increased.
  /// @dev See comments in {_coverLossAfterPriceChanging}: actual profit-to-cover amount can be less than {increaseToDebt}
  /// @param debtToInsuranceBefore Value of debtToInsurance before fix-price-change
  /// @param increaseToDebt Value on which {debtToInsuranceBefore} was incremented
  event ChangeDebtToInsuranceOnProfit(
    int debtToInsuranceBefore,
    int increaseToDebt
  );

  /// @notice Amount {lossCovered}+{lossUncovered} should be covered, but it's too high and will produce revert
  /// on the splitter side. So, only {lossCovered} can be covered, {lossUncovered} are not covered
  event UncoveredLoss(uint lossCovered, uint lossUncovered, uint investedAssetsBefore, uint investedAssetsAfter);

  /// @notice Register amounts received for supplying collaterals and amount paid for the debts
  /// @param gains Amount received by all pool adapters for the provided collateral, in underlying
  /// @param losses Amount paid by all pool adapters for the debts, in underlying
  event BorrowResults(uint gains, uint losses);
//endregion----------------------------------------- EVENTS

//region----------------------------------------- MAIN LOGIC
  /// @notice Get balances of the {tokens_} except balance of the token at {indexAsset} position
  function getAvailableBalances(
    address[] memory tokens_,
    uint indexAsset
  ) external view returns (uint[] memory) {
    uint len = tokens_.length;
    uint[] memory amountsToConvert = new uint[](len);
    for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
      if (i == indexAsset) continue;
      amountsToConvert[i] = IERC20(tokens_[i]).balanceOf(address(this));
    }
    return amountsToConvert;
  }


  /// @notice Calculate amount of liquidity that should be withdrawn from the pool to get {targetAmount_}
  ///               liquidityAmount = _depositorLiquidity() * {liquidityRatioOut} / 1e18
  ///         User needs to withdraw {targetAmount_} in some asset.
  ///         There are three kinds of available liquidity:
  ///         1) liquidity in the pool - {depositorLiquidity_}
  ///         2) Converted amounts on balance of the strategy - {baseAmounts_}
  ///         3) Liquidity locked in the debts.
  /// @param targetAmount Required amount of main asset to be withdrawn from the strategy; type(uint).max - withdraw all
  /// @param quoteAmounts Results of _depositorQuoteExit(depositorLiquidity)
  /// @return resultAmount Amount of liquidity that should be withdrawn from the pool, cannot exceed depositorLiquidity
  function getLiquidityAmount(
    uint targetAmount,
    address[] memory tokens,
    uint indexAsset,
    ITetuConverter converter,
    uint[] memory quoteAmounts,
    uint depositorLiquidity,
    uint indexUnderlying
  ) external view returns (
    uint resultAmount
  ) {
    // total amount of assetsInPool recalculated to the underlying
    // we need to calculate this value in the case of partial withdraw only
    // so we assume below that it is equal to 0 if full withdraw is required
    uint totalUnderlying;

    if (targetAmount != type(uint).max) {
      // reduce targetAmount_ on the amounts of not-underlying assets available on the balance
      uint len = tokens.length;
      (uint[] memory prices, uint[] memory decs) = AppLib._getPricesAndDecs(AppLib._getPriceOracle(converter), tokens, len);

      // calculate total amount of assets invested to the pool
      for (uint i; i < tokens.length; i = AppLib.uncheckedInc(i)) {
        totalUnderlying += (indexAsset == i)
          ? quoteAmounts[i]
          : quoteAmounts[i] * prices[i] * decs[indexUnderlying] / prices[indexUnderlying] / decs[i];
      }

      for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
        // assume here that the targetAmount_ is already reduced on available balance of the target asset
        if (indexAsset == i) continue;

        uint tokenBalance = IERC20(tokens[i]).balanceOf(address(this));
        if (tokenBalance != 0) {
          uint tokenBalanceInAsset = tokenBalance * prices[i] * decs[indexAsset] / prices[indexAsset] / decs[i];

          targetAmount = targetAmount > tokenBalanceInAsset
            ? targetAmount - tokenBalanceInAsset
            : 0;

          uint tokenBalanceInUnderlying = indexUnderlying == indexAsset
            ? tokenBalanceInAsset
            : tokenBalance * prices[i] * decs[indexUnderlying] / prices[indexUnderlying] / decs[i];

          totalUnderlying = totalUnderlying > tokenBalanceInUnderlying
            ? totalUnderlying - tokenBalanceInUnderlying
            : 0;
        }
      }

      if (indexAsset != indexUnderlying) {
        // convert targetAmount_ to underlying
        targetAmount =  targetAmount * prices[indexAsset] * decs[indexUnderlying] / prices[indexUnderlying] / decs[indexAsset];
      }
    }

    uint liquidityRatioOut = totalUnderlying == 0
      ? 1e18
      : ((targetAmount == 0)
        ? 0
        : 1e18 * 101 * targetAmount / totalUnderlying / 100 // a part of amount that we are going to withdraw + 1% on top
      );

    resultAmount = liquidityRatioOut == 0
      ? 0
      : Math.min(liquidityRatioOut * depositorLiquidity / 1e18, depositorLiquidity);
  }

  /// @notice Claim rewards from tetuConverter, generate result list of all available rewards and airdrops
  /// @dev The post-processing is rewards conversion to the main asset
  /// @param tokens_ tokens received from {_depositorPoolAssets}
  /// @param rewardTokens_ List of rewards claimed from the internal pool
  /// @param rewardTokens_ Amounts of rewards claimed from the internal pool
  /// @param tokensOut List of available rewards - not zero amounts, reward tokens don't repeat
  /// @param amountsOut Amounts of available rewards
  function claimConverterRewards(
    ITetuConverter converter_,
    address[] memory tokens_,
    address[] memory rewardTokens_,
    uint[] memory rewardAmounts_,
    uint[] memory balancesBefore
  ) external returns (
    address[] memory tokensOut,
    uint[] memory amountsOut
  ) {
    // Rewards from TetuConverter
    (address[] memory tokensTC, uint[] memory amountsTC) = converter_.claimRewards(address(this));

    // Join arrays and recycle tokens
    (tokensOut, amountsOut) = TokenAmountsLib.combineArrays(
      rewardTokens_, rewardAmounts_,
      tokensTC, amountsTC,
      // by default, depositor assets have zero amounts here
      tokens_, new uint[](tokens_.length)
    );

    // set fresh balances for depositor tokens
    uint len = tokensOut.length;
    for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
      for (uint j; j < tokens_.length; j = AppLib.uncheckedInc(j)) {
        if (tokensOut[i] == tokens_[j]) {
          amountsOut[i] = IERC20(tokens_[j]).balanceOf(address(this)) - balancesBefore[j];
        }
      }
    }

    // filter zero amounts out
    (tokensOut, amountsOut) = TokenAmountsLib.filterZeroAmounts(tokensOut, amountsOut);
  }

  /// @notice Get price of {tokenB} in term of {tokenA} with 18 decimals
  function getOracleAssetsPrice(ITetuConverter converter, address tokenA, address tokenB) external view returns (
    uint price
  ) {
    IPriceOracle oracle = AppLib._getPriceOracle(converter);
    uint priceA = oracle.getAssetPrice(tokenA);
    uint priceB = oracle.getAssetPrice(tokenB);
    price = priceA > 0 ? 1e18 * priceB / priceA : type(uint).max;
  }

  function getAssetPriceFromConverter(ITetuConverter converter, address token) external view returns (uint) {
    return AppLib._getPriceOracle(converter).getAssetPrice(token);
  }

  /// @notice Try to find zero amount
  /// @return True if {amounts_} array contains zero amount
  function findZeroAmount(uint[] memory amounts_) internal pure returns (bool) {
    uint len = amounts_.length;
    for (uint i = 0; i < len; i = AppLib.uncheckedInc(i)) {
      if (amounts_[i] == 0) return true;
    }
    return false;
  }
//endregion ----------------------------------------- MAIN LOGIC

//region -------------------------------------------- Cover loss, send profit to insurance
  /// @notice Send given {amount} of {asset} (== underlying) to the insurance
  /// @param totalAssets_ Total strategy balance = balance of underlying + current invested assets amount
  /// @param balance Current balance of the underlying
  /// @return sentAmount Amount of underlying sent to the insurance
  /// @return unsentAmount Missed part of the {amount} that were not sent to the insurance
  function sendToInsurance(address asset, uint amount, address splitter, uint totalAssets_, uint balance) external returns (
    uint sentAmount,
    uint unsentAmount
  ) {
    return _sendToInsurance(asset, amount, splitter, totalAssets_, balance);
  }

  function _sendToInsurance(address asset, uint amount, address splitter, uint totalAssets_, uint balance) internal returns (
    uint sentAmount,
    uint unsentAmount
  ) {
    uint amountToSend = Math.min(amount, balance);
    if (amountToSend != 0) {
      // max amount that can be send to insurance is limited by PRICE_CHANGE_PROFIT_TOLERANCE

      // Amount limitation should be implemented in the same way as in StrategySplitterV2._coverLoss
      // Revert or cut amount in both cases

      require(totalAssets_ != 0, AppErrors.ZERO_BALANCE);
      amountToSend = Math.min(amountToSend, PRICE_CHANGE_PROFIT_TOLERANCE * totalAssets_ / 100_000);
      //require(amountToSend <= PRICE_CHANGE_PROFIT_TOLERANCE * strategyBalance / 100_000, AppErrors.EARNED_AMOUNT_TOO_HIGH);

      IERC20(asset).safeTransfer(address(ITetuVaultV2(ISplitter(splitter).vault()).insurance()), amountToSend);
    }

    sentAmount = amountToSend;
    unsentAmount = amount > amountToSend
      ? amount - amountToSend
      : 0;

    emit SendToInsurance(sentAmount, unsentAmount);
  }

  function _registerIncome(uint assetBefore, uint assetAfter) internal pure returns (uint earned, uint lost) {
    if (assetAfter > assetBefore) {
      earned = assetAfter - assetBefore;
    } else {
      lost = assetBefore - assetAfter;
    }
    return (earned, lost);
  }

  /// @notice Send ProfitToCover to insurance - code fragment of the requirePayAmountBack()
  ///         moved here to reduce size of requirePayAmountBack()
  /// @param theAsset_ The asset passed from Converter
  /// @param balanceTheAsset_ Current balance of {theAsset_}
  /// @param investedAssets_ Value of investedAssets after call fixPriceChange()
  /// @param earnedByPrices_ ProfitToCover received from fixPriceChange()
  /// @return balanceTheAssetOut Final balance of {theAsset_} (after sending profit-to-cover to the insurance)
  function sendProfitGetAssetBalance(
    address theAsset_,
    uint balanceTheAsset_,
    uint investedAssets_,
    uint earnedByPrices_,
    IStrategyV3.BaseState storage baseState_
  ) external returns (
    uint balanceTheAssetOut
  ) {
    balanceTheAssetOut = balanceTheAsset_;
    if (earnedByPrices_ != 0) {
      address underlying = baseState_.asset;
      uint balanceUnderlying = theAsset_ == underlying
        ? balanceTheAsset_
        : AppLib.balance(underlying);

      _sendToInsurance(underlying, earnedByPrices_, baseState_.splitter, investedAssets_ + balanceUnderlying, balanceUnderlying);

      if (theAsset_ == underlying) {
        balanceTheAssetOut = AppLib.balance(theAsset_);
      }
    }
  }
//endregion -------------------------------------------- Cover loss, send profit to insurance

//region ---------------------------------------- Setters
  function checkReinvestThresholdPercentChanged(address controller, uint percent_) external {
    StrategyLib.onlyOperators(controller);
    require(percent_ <= DENOMINATOR, StrategyLib.WRONG_VALUE);
    emit ReinvestThresholdPercentChanged(percent_);
  }

  function checkLiquidationThresholdChanged(address controller, address token, uint amount) external {
    StrategyLib.onlyOperators(controller);
    emit LiquidationThresholdChanged(token, amount);
  }
//endregion ---------------------------------------- Setters

//region ---------------------------------------- Withdraw helpers
  /// @notice Get amount of assets that we expect to receive after withdrawing
  ///         ratio = amount-LP-tokens-to-withdraw / total-amount-LP-tokens-in-pool
  /// @param reserves_ Reserves of the {poolAssets_}, same order, same length (we don't check it)
  ///                  The order of tokens should be same as in {_depositorPoolAssets()},
  ///                  one of assets must be {asset_}
  /// @param liquidityAmount_ Amount of LP tokens that we are going to withdraw
  /// @param totalSupply_ Total amount of LP tokens in the depositor
  /// @return withdrawnAmountsOut Expected withdrawn amounts (decimals == decimals of the tokens)
  function getExpectedWithdrawnAmounts(
    uint[] memory reserves_,
    uint liquidityAmount_,
    uint totalSupply_
  ) internal pure returns (
    uint[] memory withdrawnAmountsOut
  ) {
    uint ratio = totalSupply_ == 0
      ? 0
      : (liquidityAmount_ >= totalSupply_
        ? 1e18
        : 1e18 * liquidityAmount_ / totalSupply_
      );

    uint len = reserves_.length;
    withdrawnAmountsOut = new uint[](len);

    if (ratio != 0) {
      for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
        withdrawnAmountsOut[i] = reserves_[i] * ratio / 1e18;
      }
    }
  }

  /// @notice Calculate expected amount of the main asset after withdrawing
  /// @param withdrawnAmounts_ Expected amounts to be withdrawn from the pool
  /// @param amountsToConvert_ Amounts on balance initially available for the conversion
  /// @return amountsOut Expected amounts of the main asset received after conversion withdrawnAmounts+amountsToConvert
  function getExpectedAmountMainAsset(
    address[] memory tokens,
    uint indexAsset,
    ITetuConverter converter,
    uint[] memory withdrawnAmounts_,
    uint[] memory amountsToConvert_
  ) internal returns (
    uint[] memory amountsOut
  ) {
    uint len = tokens.length;
    amountsOut = new uint[](len);
    for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
      if (i == indexAsset) {
        amountsOut[i] = withdrawnAmounts_[i];
      } else {
        uint amount = withdrawnAmounts_[i] + amountsToConvert_[i];
        if (amount != 0) {
          (amountsOut[i],) = converter.quoteRepay(address(this), tokens[indexAsset], tokens[i], amount);
        }
      }
    }

    return amountsOut;
  }

  /// @notice Add {withdrawnAmounts} to {amountsToConvert}, calculate {expectedAmountMainAsset}
  /// @param amountsToConvert Amounts of {tokens} to be converted, they are located on the balance before withdraw
  /// @param withdrawnAmounts Amounts of {tokens} that were withdrew from the pool
  function postWithdrawActions(
    ITetuConverter converter,
    address[] memory tokens,
    uint indexAsset,

    uint[] memory reservesBeforeWithdraw,
    uint liquidityAmountWithdrew,
    uint totalSupplyBeforeWithdraw,

    uint[] memory amountsToConvert,
    uint[] memory withdrawnAmounts
  ) external returns (
    uint[] memory expectedMainAssetAmounts,
    uint[] memory _amountsToConvert
  ) {
    // estimate expected amount of assets to be withdrawn
    uint[] memory expectedWithdrawAmounts = getExpectedWithdrawnAmounts(
      reservesBeforeWithdraw,
      liquidityAmountWithdrew,
      totalSupplyBeforeWithdraw
    );

    // from received amounts after withdraw calculate how much we receive from converter for them in terms of the underlying asset
    expectedMainAssetAmounts = getExpectedAmountMainAsset(
      tokens,
      indexAsset,
      converter,
      expectedWithdrawAmounts,
      amountsToConvert
    );

    uint len = tokens.length;
    for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
      amountsToConvert[i] += withdrawnAmounts[i];
    }

    return (expectedMainAssetAmounts, amountsToConvert);
  }

  /// @notice return {withdrawnAmounts} with zero values and expected amount calculated using {amountsToConvert_}
  function postWithdrawActionsEmpty(
    ITetuConverter converter,
    address[] memory tokens,
    uint indexAsset,
    uint[] memory amountsToConvert_
  ) external returns (
    uint[] memory expectedAmountsMainAsset
  ) {
    expectedAmountsMainAsset = getExpectedAmountMainAsset(
      tokens,
      indexAsset,
      converter,
      // there are no withdrawn amounts
      new uint[](tokens.length), // array with all zero values
      amountsToConvert_
    );
  }
//endregion ------------------------------------- Withdraw helpers

//region---------------------------------------- calcInvestedAssets
  /// @notice Calculate amount we will receive when we withdraw all from pool
  /// @dev This is writable function because we need to update current balances in the internal protocols.
  /// @param indexAsset Index of the underlying (main asset) in {tokens}
  /// @param makeCheckpoint_ True - call IBookkeeper.checkpoint in the converter
  /// @return amountOut Invested asset amount under control (in terms of underlying)
  /// @return prices Asset prices in USD, decimals 18
  /// @return decs 10**decimals
  function calcInvestedAssets(
    address[] memory tokens,
    uint[] memory depositorQuoteExitAmountsOut,
    uint indexAsset,
    ITetuConverter converter_,
    bool makeCheckpoint_
  ) external returns (
    uint amountOut,
    uint[] memory prices,
    uint[] memory decs
  ) {
    return _calcInvestedAssets(tokens, depositorQuoteExitAmountsOut, indexAsset, converter_, makeCheckpoint_);
  }

  /// @notice Calculate amount we will receive when we withdraw all from pool
  /// @dev This is writable function because we need to update current balances in the internal protocols.
  /// @param indexAsset Index of the underlying (main asset) in {tokens}
  /// @param makeCheckpoint_ True - call IBookkeeper.checkpoint in the converter
  /// @return amountOut Invested asset amount under control (in terms of underlying)
  /// @return prices Asset prices in USD, decimals 18
  /// @return decs 10**decimals
  function _calcInvestedAssets(
    address[] memory tokens,
    uint[] memory depositorQuoteExitAmountsOut,
    uint indexAsset,
    ITetuConverter converter_,
    bool makeCheckpoint_
  ) internal returns (
    uint amountOut,
    uint[] memory prices,
    uint[] memory decs
  ) {
    CalcInvestedAssetsLocal memory v;
    v.len = tokens.length;
    v.asset = tokens[indexAsset];

    // calculate prices, decimals
    (prices, decs) = AppLib._getPricesAndDecs(AppLib._getPriceOracle(converter_), tokens, v.len);

    // A debt is registered below if we have X amount of asset, need to pay Y amount of the asset and X < Y
    // In this case: debt = Y - X, the order of tokens is the same as in {tokens} array
    for (uint i; i < v.len; i = AppLib.uncheckedInc(i)) {
      if (i == indexAsset) {
        // Current strategy balance of main asset is not taken into account here because it's add by splitter
        amountOut += depositorQuoteExitAmountsOut[i];
      } else {
        v.token = tokens[i];
        // possible reverse debt: collateralAsset = tokens[i], borrowAsset = underlying
        // investedAssets is calculated using exact debts, debt-gaps are not taken into account
        (uint toPay, uint collateral) = converter_.getDebtAmountCurrent(address(this), v.token, v.asset, false);
        if (amountOut < toPay) {
          setDebt(v, indexAsset, toPay);
        } else {
          amountOut -= toPay;
        }

        // available amount to repay
        uint toRepay = collateral + IERC20(v.token).balanceOf(address(this)) + depositorQuoteExitAmountsOut[i];

        // direct debt: collateralAsset = underlying, borrowAsset = tokens[i]
        // investedAssets is calculated using exact debts, debt-gaps are not taken into account
        (toPay, collateral) = converter_.getDebtAmountCurrent(address(this), v.asset, v.token, false);
        amountOut += collateral;

        if (toRepay >= toPay) {
          amountOut += (toRepay - toPay) * prices[i] * decs[indexAsset] / prices[indexAsset] / decs[i];
        } else {
          // there is not enough amount to pay the debt
          // let's register a debt and try to resolve it later below
          setDebt(v, i, toPay - toRepay);
        }
      }
    }
    if (v.debts.length == v.len) {
      // we assume here, that it would be always profitable to save collateral
      // f.e. if there is not enough amount of USDT on our balance and we have a debt in USDT,
      // it's profitable to change any available asset to USDT, pay the debt and return the collateral back
      for (uint i; i < v.len; i = AppLib.uncheckedInc(i)) {
        if (v.debts[i] == 0) continue;

        // estimatedAssets should be reduced on the debt-value
        // this estimation is approx and do not count price impact on the liquidation
        // we will able to count the real output only after withdraw process
        uint debtInAsset = v.debts[i] * prices[i] * decs[indexAsset] / prices[indexAsset] / decs[i];
        if (debtInAsset > amountOut) {
          // The debt is greater than we can pay. We shouldn't try to pay the debt in this case
          amountOut = 0;
        } else {
          amountOut -= debtInAsset;
        }
      }
    }

    if (makeCheckpoint_) {
      _callCheckpoint(tokens, converter_);
    }

    return (amountOut, prices, decs);
  }

  /// @notice Make new checkpoint in converter's bookkeeper
  /// As results, a next call of checkpoint will return amount of increases to debts ("deltas")
  /// since current moment up to the moment of the next call (we need such deltas in _fixPriceChanges only)
  function _callCheckpoint(address[] memory tokens, ITetuConverter converter_) internal returns (
    uint[] memory deltaGains,
    uint[] memory deltaLosses
  ) {
    IBookkeeper a = IBookkeeper(IConverterController(converter_.controller()).bookkeeper());
    return a.checkpoint(tokens);
  }

  /// @notice Lazy initialization of v.debts, add {value} to {v.debts[index]}
  function setDebt(CalcInvestedAssetsLocal memory v, uint index, uint value) pure internal {
    if (v.debts.length == 0) {
      // lazy initialization
      v.debts = new uint[](v.len);
    }

    // to pay the following amount we need to swap some other asset at first
    v.debts[index] += value;
  }

  /// @notice Calculate the token amounts for deposit and amount of loss (as old-total-asset - new-total-asset)
  /// @param liquidationThresholdsAB [liquidityThreshold of token A, liquidityThreshold of tokenB]
  /// @return loss New total assets - old total assets
  /// @return tokenAmounts Balances of the token A and token B.
  ///                     If any balance is zero it's not possible to enter to the pool, so return empty array (len 0)
  function getTokenAmountsPair(
    ITetuConverter converter,
    uint totalAssets,
    address tokenA,
    address tokenB,
    uint[2] calldata liquidationThresholdsAB
  ) external returns (
    uint loss,
    uint[] memory tokenAmounts
  ) {
    tokenAmounts = new uint[](2);
    tokenAmounts[0] = AppLib.balance(tokenA);
    tokenAmounts[1] = AppLib.balance(tokenB);

    address[] memory tokens = new address[](2);
    tokens[0] = tokenA;
    tokens[1] = tokenB;

    uint[] memory amounts = new uint[](2);
    amounts[0] = tokenAmounts[0];

    (uint newTotalAssets,,) = _calcInvestedAssets(tokens, amounts, 0, converter, true);
    return (
      newTotalAssets < totalAssets
        ? totalAssets - newTotalAssets
        : 0,
      (tokenAmounts[0] < liquidationThresholdsAB[0] || tokenAmounts[1] < liquidationThresholdsAB[1])
        ? new uint[](0)
        : tokenAmounts
    );
  }

  /// @notice Swap can give us more amount out than expected, so we will receive increasing of share price.
  ///         To prevent it, we need to send exceeded amount to insurance,
  ///         but it's too expensive to make such transfer at the end of withdrawAggByStep.
  ///         So, we postpone sending the profit until the next call of fixPriceChange
  ///         by manually setting investedAssets equal to the oldTotalAssets
  /// @dev If profitToCover was sent only partly, we will postpone sending of remain amount up to the next call
  ///      of fixPriceChange in same manner
  /// @param oldTotalAssets Total asset at the moment after last call of fixPriceChange,
  ///                       decreased on the value of profitToCover.
  function fixTooHighInvestedAssets(
    address asset_,
    uint oldTotalAssets,
    IConverterStrategyBase.ConverterStrategyBaseState storage csbs_
  ) external {
    uint balance = IERC20(asset_).balanceOf(address(this));
    uint newTotalAssets = csbs_.investedAssets + balance;

    if (oldTotalAssets < newTotalAssets) {
      // total asset was increased (i.e. because of too profitable swaps)
      // this increment will increase share price
      // we should send added amount to insurance to avoid share price change
      // anyway, it's too expensive to do it here
      // so, we postpone sending the profit until the next call of fixPriceChange
      if (oldTotalAssets > balance) {
        csbs_.investedAssets = oldTotalAssets - balance;
      }
    }
  }
//endregion------------------------------------- calcInvestedAssets

//region ------------------------------------------------------- Bookkeeper logic
  /// @notice Make checkpoint (it's writable function) and calculate total cost of the deltas in terms of the {asset}
  /// @param tokens Full list of tokens that can be used as collateral/borrow asset by the current strategy
  /// @param indexAsset Index of the underlying in {tokens}
  /// @return increaseToDebt Total increase-to-debt since previous checkpoint [in underlying]
  function _getIncreaseToDebt(
    address[] memory tokens,
    uint indexAsset,
    uint[] memory prices,
    uint[] memory decs,
    ITetuConverter converter
  ) internal returns (
    int increaseToDebt
  ) {
    IBookkeeper a = IBookkeeper(IConverterController(converter.controller()).bookkeeper());
    (uint[] memory deltaGains, uint[] memory deltaLosses) = a.checkpoint(tokens);

    uint len = tokens.length;
    for (uint i; i < len; i = AppLib.uncheckedInc(i)) {
      if (i == indexAsset) {
        increaseToDebt -= int(deltaGains[i]);
        increaseToDebt += int(deltaLosses[i]);
      } else {
        increaseToDebt += (int(deltaLosses[i]) - int(deltaGains[i]))
          * int(prices[i]) * int(decs[indexAsset]) / int(prices[indexAsset]) / int(decs[i]);
      }
    }
    emit OnIncreaseDebtToInsurance(tokens, deltaGains, deltaLosses, prices, increaseToDebt);

    return increaseToDebt;
  }

  /// @notice Register income and cover possible loss after price changing, emit FixPriceChanges
  /// @param investedAssetsBefore Currently stored value of _csbs.investedAssets
  /// @param investedAssetsAfter Actual value of invested assets calculated at the current moment
  /// @param increaseToDebt The amount by which the total loan debts increased for the selected period
  /// @return earned Amount earned because of price changing
  function _coverLossAfterPriceChanging(
    IConverterStrategyBase.ConverterStrategyBaseState storage csbs,
    uint investedAssetsBefore,
    uint investedAssetsAfter,
    int increaseToDebt,
    IStrategyV3.BaseState storage baseState
  ) internal returns (uint earned) {
    int debtToInsurance0 = csbs.debtToInsurance;
    if (investedAssetsAfter > investedAssetsBefore) {
      earned = investedAssetsAfter - investedAssetsBefore;
      if (increaseToDebt != 0) {
        // Earned amount will be send to the insurance later.
        // Probably it can be reduced by same limitations as {lost} amount below
        // and so, it will be necessary to decrease increaseToDebt proportionally.
        // For simplicity, we increase debtToInsurance on full increaseToDebt always
        // in assumption, that such profits are always low.
        csbs.debtToInsurance += increaseToDebt;
        emit ChangeDebtToInsuranceOnProfit(debtToInsurance0, increaseToDebt);
      }
    } else {
      uint lost = investedAssetsBefore - investedAssetsAfter;
      if (lost != 0) {
        uint totalAsset = investedAssetsAfter + IERC20(baseState.asset).balanceOf(address(this));
        (uint lossToCover, uint lossUncovered) = _getSafeLossToCover(lost, totalAsset);

        if (lossUncovered != 0) {
          // we need to cover lost-amount, but this amount is too high and will produce revert in the splitter
          // so, we will cover only part of {lost} and leave other part uncovered.
          emit UncoveredLoss(lossToCover, lossUncovered, investedAssetsBefore, investedAssetsAfter);
        }

        // if we compensate lost only partially, we reduce both amounts "from prices" and "from debts" proportionally
        _coverLossAndCheckResults(csbs, baseState.splitter, lossToCover, increaseToDebt * int(lossToCover) / int(lost));

      }
    }

    emit FixPriceChanges(
      investedAssetsBefore,
      investedAssetsAfter,
      debtToInsurance0,
      csbs.debtToInsurance,
      increaseToDebt
    );
    return earned;
  }

  /// @notice Call coverPossibleStrategyLoss, covered loss will be sent to vault.
  ///         If the loss were covered only partially, emit {NotEnoughInsurance}
  function coverLossAndCheckResults(
    IConverterStrategyBase.ConverterStrategyBaseState storage csbs,
    address splitter,
    uint lossToCover
  ) external {
    _coverLossAndCheckResults(csbs, splitter, lossToCover, int(lossToCover));
  }

  /// @notice Call coverPossibleStrategyLoss, covered loss will be sent to vault.
  function _coverLossAndCheckResults(
    IConverterStrategyBase.ConverterStrategyBaseState storage csbs,
    address splitter,
    uint lossToCover,
    int debtToInsuranceInc
  ) internal {
    address asset = ISplitter(splitter).asset();
    address vault = ISplitter(splitter).vault();

    uint balanceBefore = IERC20(asset).balanceOf(vault);
    ISplitter(splitter).coverPossibleStrategyLoss(0, lossToCover);
    uint balanceAfter = IERC20(asset).balanceOf(vault);

    if (debtToInsuranceInc != 0) {
      csbs.debtToInsurance += debtToInsuranceInc;
    }

    uint delta = AppLib.sub0(balanceAfter, balanceBefore);
    uint uncovered = AppLib.sub0(lossToCover, delta);

    // we don't add uncovered amount to the debts to the insurance
    emit OnCoverLoss(
      lossToCover,
      lossToCover == 0
        ? int(0)
        : debtToInsuranceInc * int(lossToCover - uncovered) / int(lossToCover),
      delta,
      uncovered
    );
  }

  /// @notice Cut loss-value to safe value that doesn't produce revert inside splitter
  function _getSafeLossToCover(uint loss, uint totalAssets_) internal pure returns (
    uint lossToCover,
    uint lossUncovered
  ) {
    // see StrategySplitterV2._declareStrategyIncomeAndCoverLoss, _coverLoss implementations
    lossToCover = Math.min(loss, ConverterStrategyBaseLib2.HARDWORK_LOSS_TOLERANCE * totalAssets_ / 100_000);
    lossUncovered = AppLib.sub0(loss, lossToCover);
  }

  /// @notice Calculate profit/loss happened because of price changing.
  /// Try to cover the loss, send the profit to the insurance.
  /// Increment debt to insurance on amount of increase of the debts.
  /// @param amountsInPool Amount of tokens that can be received from the pool after withdrawing all liquidity.
  /// The order of tokens is same as in the {tokens}
  /// @param tokens Result of {_depositorPoolAssets}
  /// @param indexAsset Index of the underlying in {tokens}
  /// @return investedAssetsOut Updated value of {csbs.investedAssets}
  /// @return earnedOut Profit that was received because of price changes. It should be sent back to insurance.
  function fixPriceChanges(
    IConverterStrategyBase.ConverterStrategyBaseState storage csbs,
    IStrategyV3.BaseState storage baseState,
    uint[] memory amountsInPool,
    address[] memory tokens,
    uint indexAsset
  ) external returns (
    uint investedAssetsOut,
    uint earnedOut
  ) {
    ITetuConverter converter = csbs.converter;
    uint investedAssetsBefore = csbs.investedAssets;

    uint[] memory prices;
    uint[] memory decs;

    (investedAssetsOut, prices, decs) = _calcInvestedAssets(tokens, amountsInPool, indexAsset, converter, false);
    csbs.investedAssets = investedAssetsOut;

    int increaseToDebt = _getIncreaseToDebt(tokens, indexAsset, prices, decs, converter);
    earnedOut = _coverLossAfterPriceChanging(csbs, investedAssetsBefore, investedAssetsOut, increaseToDebt, baseState);
  }

  /// @notice Register amounts received for supplying collaterals and amount paid for the debts
  ///         for the current period (a new period is started after each hardwork operation)
  function registerBorrowResults(ITetuConverter converter, address asset) external {
    IBookkeeper a = IBookkeeper(IConverterController(converter.controller()).bookkeeper());
    (uint gains, uint losses) = a.startPeriod(asset);
    if (gains != 0 && losses != 0) {
      emit BorrowResults(gains, losses);
    }
  }
//endregion ------------------------------------------------------- Bookkeeper logic


}

","keccak256":"0xe17e08619f632fa8f9580d655c7c0aac9dbcaf03464adc6dea6b70d2027f1edb","license":"BUSL-1.1"},"contracts/strategies/kyber/KyberConverterStrategyLogicLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "./KyberLib.sol";
import "./KyberDebtLib.sol";
import "./KyberStrategyErrors.sol";
import "@tetu_io/tetu-contracts-v2/contracts/lib/StringLib.sol";
import "@tetu_io/tetu-contracts-v2/contracts/openzeppelin/SafeERC20.sol";
import "../pair/PairBasedStrategyLogicLib.sol";

library KyberConverterStrategyLogicLib {
  using SafeERC20 for IERC20;

  //region ------------------------------------------------ Constants
  uint internal constant LIQUIDATOR_SWAP_SLIPPAGE_STABLE = 300;
  uint internal constant LIQUIDATOR_SWAP_SLIPPAGE_VOLATILE = 500;
  /// @dev 0.5% by default
  uint internal constant DEFAULT_FUSE_THRESHOLD = 5e15;
  IBasePositionManager internal constant KYBER_NFT = IBasePositionManager(0xe222fBE074A436145b255442D919E4E3A6c6a480);
  IKyberSwapElasticLM internal constant FARMING_CENTER = IKyberSwapElasticLM(0x7D5ba536ab244aAA1EA42aB88428847F25E3E676);
  // ITicksFeesReader internal constant TICKS_FEES_READER = ITicksFeesReader(0x8Fd8Cb948965d9305999D767A02bf79833EADbB3);
  address public constant KNC = 0x1C954E8fe737F99f68Fa1CCda3e51ebDB291948C;
  //endregion ------------------------------------------------ Constants

  //region ------------------------------------------------ Events
  event Rebalanced(uint loss, uint profitToCover, uint coveredByRewards);
  event RebalancedDebt(uint loss, uint profitToCover, uint coveredByRewards);
  event KyberFeesClaimed(uint fee0, uint fee1);
  event KyberRewardsClaimed(uint reward);
  //endregion ------------------------------------------------ Events

  //region ------------------------------------------------ Data types
  struct State {
    PairBasedStrategyLogicLib.PairState pair;
    // additional (specific) state

    uint tokenId;
    // farming
    uint pId;
    bool staked;

    /// @notice reserve space for future needs
    uint[10] __gap;
  }

  struct RebalanceLocal {
    /// @notice Fuse for token A and token B
    PairBasedStrategyLib.FuseStateParams fuseAB;
    ITetuConverter converter;
    IPool pool;
    address tokenA;
    address tokenB;
    bool isStablePool;
    uint[2] liquidationThresholdsAB;

    bool fuseStatusChangedAB;
    PairBasedStrategyLib.FuseStatus fuseStatusAB;

    uint poolPrice;
    uint poolPriceAdjustment;
  }

  struct EnterLocalVariables {
    IPool pool;
    int24 upperTick;
    int24 lowerTick;
    uint tokenId;
    uint pId;
  }

  struct ExitLocalVariables {
    address strategyProfitHolder;
    uint pId;
    address tokenA;
    address tokenB;
  }

  //endregion ------------------------------------------------ Data types

  //region ------------------------------------------------ Helpers

  /// @param controllerPool [controller, pool]
  /// @param fuseThresholds Fuse thresholds for tokens (stable pool only)
  function initStrategyState(
    State storage state,
    address[2] memory controllerPool,
    int24 tickRange,
    int24 rebalanceTickRange,
    address asset_,
    bool isStablePool,
    uint[4] calldata fuseThresholds
  ) external {
    require(controllerPool[1] != address(0), AppErrors.ZERO_ADDRESS);
    address token0 = address(IPool(controllerPool[1]).token0());
    address token1 = address(IPool(controllerPool[1]).token1());

    int24[4] memory tickData;
    {
      int24 tickSpacing = KyberLib.getTickSpacing(IPool(controllerPool[1]));
      if (tickRange != 0) {
        require(tickRange == tickRange / tickSpacing * tickSpacing, KyberStrategyErrors.INCORRECT_TICK_RANGE);
        require(rebalanceTickRange == rebalanceTickRange / tickSpacing * tickSpacing, KyberStrategyErrors.INCORRECT_REBALANCE_TICK_RANGE);
      }
      tickData[0] = tickSpacing;
      (tickData[1], tickData[2]) = KyberDebtLib.calcTickRange(IPool(controllerPool[1]), tickRange, tickSpacing);
      tickData[3] = rebalanceTickRange;
    }

    PairBasedStrategyLogicLib.setInitialDepositorValues(
      state.pair,
      [controllerPool[1], asset_, token0, token1],
      tickData,
      isStablePool,
      fuseThresholds
    );

    address liquidator = IController(controllerPool[0]).liquidator();
    IERC20(token0).approve(liquidator, type(uint).max);
    IERC20(token1).approve(liquidator, type(uint).max);
    IERC20(token0).approve(address(KYBER_NFT), type(uint).max);
    IERC20(token1).approve(address(KYBER_NFT), type(uint).max);
    IERC721(address(KYBER_NFT)).setApprovalForAll(address(FARMING_CENTER), true);
  }

  function createSpecificName(PairBasedStrategyLogicLib.PairState storage pairState) external view returns (string memory) {
    return string(abi.encodePacked(
      "Kyber ",
      IERC20Metadata(pairState.tokenA).symbol(),
      "/",
      IERC20Metadata(pairState.tokenB).symbol())
    );
  }

  function getPoolReserves(PairBasedStrategyLogicLib.PairState storage pairState) external view returns (uint[] memory reserves) {
    reserves = new uint[](2);
    (uint160 sqrtRatioX96, , ,) = IPool(pairState.pool).getPoolState();

    (reserves[0], reserves[1]) = KyberLib.getAmountsForLiquidity(
      sqrtRatioX96,
      pairState.lowerTick,
      pairState.upperTick,
      pairState.totalLiquidity
    );

    if (pairState.depositorSwapTokens) {
      (reserves[0], reserves[1]) = (reserves[1], reserves[0]);
    }
  }

  /// @dev Gets the liquidator swap slippage based on the pool type (stable or volatile).
  /// @return The liquidator swap slippage percentage.
  function _getLiquidatorSwapSlippage(bool isStablePool) internal pure returns (uint) {
    return isStablePool ? LIQUIDATOR_SWAP_SLIPPAGE_STABLE : LIQUIDATOR_SWAP_SLIPPAGE_VOLATILE;
  }

  /// @notice Calculate proportions of the tokens for entry kind 1
  /// @param pool Pool instance.
  /// @param lowerTick The lower tick of the pool's main range.
  /// @param upperTick The upper tick of the pool's main range.
  /// @param depositorSwapTokens A boolean indicating if need to use token B instead of token A.
  /// @return prop0 Proportion onf token A. Any decimals are allowed, prop[0 or 1]/(prop0 + prop1) are important only
  /// @return prop1 Proportion onf token B. Any decimals are allowed, prop[0 or 1]/(prop0 + prop1) are important only
  function getEntryDataProportions(IPool pool, int24 lowerTick, int24 upperTick, bool depositorSwapTokens) external view returns (uint, uint) {
    return KyberDebtLib.getEntryDataProportions(pool, lowerTick, upperTick, depositorSwapTokens);
  }
  //endregion ------------------------------------------------ Helpers

  //region ------------------------------------------------ Pool info

  function getEntryData(
    IPool pool,
    int24 lowerTick,
    int24 upperTick,
    bool depositorSwapTokens
  ) public view returns (bytes memory entryData) {
    return KyberDebtLib.getEntryData(pool, lowerTick, upperTick, depositorSwapTokens);
  }
  //endregion ------------------------------------------------ Pool info

  //region ------------------------------------------------ Join the pool
  function enter(
    State storage state,
    uint[] memory amountsDesired_
  ) external returns (uint[] memory amountsConsumed, uint liquidityOut) {
    EnterLocalVariables memory vars = EnterLocalVariables({
      pool: IPool(state.pair.pool),
      lowerTick : state.pair.lowerTick,
      upperTick : state.pair.upperTick,
      tokenId : state.tokenId,
      pId : state.pId
    });
    bool depositorSwapTokens = state.pair.depositorSwapTokens;
    (address token0, address token1) = depositorSwapTokens ? (state.pair.tokenB, state.pair.tokenA) : (state.pair.tokenA, state.pair.tokenB);
    if (depositorSwapTokens) {
      (amountsDesired_[0], amountsDesired_[1]) = (amountsDesired_[1], amountsDesired_[0]);
    }
    amountsConsumed = new uint[](2);
    uint128 liquidity;

    if (vars.tokenId > 0) {
      (IBasePositionManager.Position memory pos,) = KYBER_NFT.positions(vars.tokenId);
      if (pos.tickLower != vars.lowerTick || pos.tickUpper != vars.upperTick) {
        KYBER_NFT.burn(vars.tokenId);
        vars.tokenId = 0;
      }
    }

    if (vars.tokenId == 0) {
      (vars.tokenId, liquidity, amountsConsumed[0], amountsConsumed[1]) = KYBER_NFT.mint(IBasePositionManager.MintParams(
        token0,
        token1,
        IPool(state.pair.pool).swapFeeUnits(),
        vars.lowerTick,
        vars.upperTick,
        KyberLib.getPreviousTicks(vars.pool, vars.lowerTick, vars.upperTick),
        amountsDesired_[0],
        amountsDesired_[1],
        0,
        0,
        address(this),
        block.timestamp
      ));

      state.tokenId = vars.tokenId;

      {
        if (!isFarmEnded(vars.pId)) {
          uint[] memory nftIds = new uint[](1);
          nftIds[0] = vars.tokenId;
          uint[] memory liqs = new uint[](1);
          liqs[0] = uint(liquidity);
          FARMING_CENTER.deposit(nftIds);
          state.staked = true;
          FARMING_CENTER.join(vars.pId, nftIds, liqs);
        }
      }
    } else {
      (liquidity, amountsConsumed[0], amountsConsumed[1],) = KYBER_NFT.addLiquidity(IBasePositionManager.IncreaseLiquidityParams(
        vars.tokenId,
        KyberLib.getPreviousTicks(vars.pool, vars.lowerTick, vars.upperTick),
        amountsDesired_[0],
        amountsDesired_[1],
        0,
        0,
        block.timestamp
      ));

      if (!isFarmEnded(vars.pId)) {
        uint[] memory nftIds = new uint[](1);
        nftIds[0] = vars.tokenId;
        if (state.pair.totalLiquidity == 0) {
          FARMING_CENTER.deposit(nftIds);
          state.staked = true;
        }

        uint[] memory liqs = new uint[](1);
        liqs[0] = uint(liquidity);
        FARMING_CENTER.join(vars.pId, nftIds, liqs);
      }
    }

    state.pair.totalLiquidity += liquidity;
    liquidityOut = uint(liquidity);
  }
  //endregion ------------------------------------------------ Join the pool

  //region ------------------------------------------------ Exit from the pool

  /// @param emergency Emergency exit (only withdraw, don't claim any rewards or make any other additional actions)
  function exit(
    State storage state,
    uint128 liquidityAmountToExit,
    bool emergency
  ) external returns (uint[] memory amountsOut) {
    amountsOut = new uint[](2);

    ExitLocalVariables memory vars = ExitLocalVariables({
      strategyProfitHolder : state.pair.strategyProfitHolder,
      pId : state.pId,
      tokenA : state.pair.tokenA,
      tokenB : state.pair.tokenB
    });

    uint128 liquidity = state.pair.totalLiquidity;

    require(liquidity >= liquidityAmountToExit, KyberStrategyErrors.WRONG_LIQUIDITY);

    bool staked = state.staked;

    uint[] memory nftIds = new uint[](1);
    nftIds[0] = state.tokenId;
    uint[] memory liqs = new uint[](1);
    uint feeA;
    uint feeB;

    // get rewards
    if (staked) {
      if (!emergency) {
        uint reward = _harvest(nftIds[0], vars.pId);
        // send to profit holder
        if (reward > 0) {
          IERC20(KNC).safeTransfer(vars.strategyProfitHolder, reward);
        }

        // get fees
        // when exiting, fees are collected twice so as not to lose anything when rebalancing (the position goes out of range)
        (feeA, feeB) = _claimFees(state);
      }

      liqs[0] = uint(liquidity);

      FARMING_CENTER.exit(vars.pId, nftIds, liqs);

      // withdraw
      FARMING_CENTER.withdraw(nftIds);
      state.staked = false;
    }

    // burn liquidity
    uint rTokensOwed;
    (amountsOut[0], amountsOut[1], rTokensOwed) = KYBER_NFT.removeLiquidity(IBasePositionManager.RemoveLiquidityParams(nftIds[0], liquidityAmountToExit, 0, 0, block.timestamp));

    if (!emergency && rTokensOwed != 0) {
//      KYBER_NFT.syncFeeGrowth(nftIds[0]);
      (,uint amount0, uint amount1) = KYBER_NFT.burnRTokens(IBasePositionManager.BurnRTokenParams(nftIds[0], 0, 0, block.timestamp));
      if (state.pair.depositorSwapTokens) {
        feeA += amount1;
        feeB += amount0;
        emit KyberFeesClaimed(amount1, amount0);
      } else {
        feeA += amount0;
        feeB += amount1;
        emit KyberFeesClaimed(amount0, amount1);
      }
    }

    // transfer tokens
    KYBER_NFT.transferAllTokens(vars.tokenA, 0, address(this));
    KYBER_NFT.transferAllTokens(vars.tokenB, 0, address(this));

    // send fees to profit holder
    if (feeA > 0) {
      IERC20(vars.tokenA).safeTransfer(vars.strategyProfitHolder, feeA);
    }
    if (feeB > 0) {
      IERC20(vars.tokenB).safeTransfer(vars.strategyProfitHolder, feeB);
    }

    liquidity -= liquidityAmountToExit;
    state.pair.totalLiquidity = liquidity;

    if (liquidity != 0 && !isFarmEnded(vars.pId)) {
      liqs[0] = uint(liquidity);
      FARMING_CENTER.deposit(nftIds);
      state.staked = true;
      FARMING_CENTER.join(vars.pId, nftIds, liqs);
    }
  }

  function quoteExit(
    PairBasedStrategyLogicLib.PairState storage pairState,
    uint128 liquidityAmountToExit
  ) public view returns (uint[] memory amountsOut) {
    (uint160 sqrtRatioX96, , ,) = IPool(pairState.pool).getPoolState();
    amountsOut = new uint[](2);
    (amountsOut[0], amountsOut[1]) = KyberLib.getAmountsForLiquidity(
      sqrtRatioX96,
      pairState.lowerTick,
      pairState.upperTick,
      liquidityAmountToExit
    );
    if (pairState.depositorSwapTokens) {
      (amountsOut[0], amountsOut[1]) = (amountsOut[1], amountsOut[0]);
    }
  }
  //endregion ------------------------------------------------ Exit from the pool

  //region ------------------------------------------------ Rewards

  function claimRewardsBeforeExitIfRequired(State storage state) external {
    (,bool needUnstake) = needRebalanceStaking(state);
    if (needUnstake) {
      claimRewards(state, false);
    }
  }

  function claimRewards(State storage state, bool transferFromProfitHolder) public returns (
    address[] memory tokensOut,
    uint[] memory amountsOut,
    uint[] memory balancesBefore
  ) {
    address strategyProfitHolder = state.pair.strategyProfitHolder;
    uint tokenId = state.tokenId;
    tokensOut = new address[](3);
    tokensOut[0] = state.pair.tokenA;
    tokensOut[1] = state.pair.tokenB;
    tokensOut[2] = KNC;

    balancesBefore = new uint[](3);
    for (uint i; i < tokensOut.length; i++) {
      balancesBefore[i] = AppLib.balance(tokensOut[i]);
    }

    amountsOut = new uint[](3);
    if (tokenId > 0 && state.pair.totalLiquidity > 0) {
      (amountsOut[0], amountsOut[1]) = _claimFees(state);
      amountsOut[2] = _harvest(tokenId, state.pId);
      if (!transferFromProfitHolder) {
        if (amountsOut[0] > 0) {
          IERC20(tokensOut[0]).safeTransfer(strategyProfitHolder, amountsOut[0]);
        }
        if (amountsOut[1] > 0) {
          IERC20(tokensOut[1]).safeTransfer(strategyProfitHolder, amountsOut[1]);
        }
        if (amountsOut[2] > 0) {
          IERC20(tokensOut[2]).safeTransfer(strategyProfitHolder, amountsOut[2]);
        }
      }
    }

    if (transferFromProfitHolder) {
      for (uint i; i < tokensOut.length; ++i) {
        uint b = IERC20(tokensOut[i]).balanceOf(strategyProfitHolder);
        if (b > 0) {
          IERC20(tokensOut[i]).transferFrom(strategyProfitHolder, address(this), b);
          amountsOut[i] += b;
        }
      }
    }
  }

  function _claimFees(State storage state) internal returns (uint amountA, uint amountB) {
    uint[] memory nftIds = new uint[](1);
    nftIds[0] = state.tokenId;
    address tokenA = state.pair.tokenA;
    address tokenB = state.pair.tokenB;
    uint bABefore = AppLib.balance(tokenA);
    uint bBBefore = AppLib.balance(tokenB);

    // this extracting fees method was disabled because it can trigger revert 'No rToken to burn'
    // reproduced in test test/strategies/polygon/pair/PairBasedStrategyActionResponseIntTest.ts
    // (uint token0Owed, uint token1Owed) = TICKS_FEES_READER.getTotalFeesOwedToPosition(address(KYBER_NFT), state.pair.pool, nftIds[0]);
    // if (token0Owed > 0 || token1Owed > 0) {

    (IBasePositionManager.Position memory pos,) = KYBER_NFT.positions(nftIds[0]);
    if (pos.rTokenOwed > 0) {
      FARMING_CENTER.claimFee(nftIds, 0, 0, state.pair.pool, false, block.timestamp);

      amountA = AppLib.balance(tokenA) - bABefore;
      amountB = AppLib.balance(tokenB) - bBBefore;
      emit KyberFeesClaimed(amountA, amountB);
    }
  }

  function _harvest(uint tokenId, uint pId) internal returns (uint amount) {
    uint[] memory nftIds = new uint[](1);
    nftIds[0] = tokenId;
    uint[] memory pids = new uint[](1);
    pids[0] = pId;
    IKyberSwapElasticLM.HarvestData memory data = IKyberSwapElasticLM.HarvestData({
      pIds: pids
    });
    bytes[] memory datas = new bytes[](1);
    datas[0] = abi.encode(data);
    uint bBefore = AppLib.balance(KNC);
    FARMING_CENTER.harvestMultiplePools(nftIds, datas);
    amount = AppLib.balance(KNC) - bBefore;
    if (amount > 0) {
      emit KyberRewardsClaimed(amount);
    }
  }

  function calcEarned(address asset, address controller, address[] memory rewardTokens, uint[] memory amounts) external view returns (uint) {
    ITetuLiquidator liquidator = ITetuLiquidator(IController(controller).liquidator());
    uint len = rewardTokens.length;
    uint earned;
    for (uint i; i < len; ++i) {
      address token = rewardTokens[i];
      if (token == asset) {
        earned += amounts[i];
      } else {
        earned += liquidator.getPrice(rewardTokens[i], asset, amounts[i]);
      }
    }

    return earned;
  }
  //endregion ------------------------------------------------ Rewards

  //region ------------------------------------------------ Rebalance
  /// @notice Determine if the strategy needs to be rebalanced.
  /// @return needRebalance A boolean indicating if {rebalanceNoSwaps} should be called
  function needStrategyRebalance(PairBasedStrategyLogicLib.PairState storage pairState, ITetuConverter converter_) external view returns (
    bool needRebalance
  ) {
    address pool = pairState.pool;
    // poolPrice should have same decimals as a price from oracle == 18
    uint poolPriceAdjustment = PairBasedStrategyLib.getPoolPriceAdjustment(IERC20Metadata(pairState.tokenA).decimals());
    uint poolPrice = KyberLib.getPrice(pool, pairState.tokenB) * poolPriceAdjustment;
    (needRebalance, , ) = PairBasedStrategyLogicLib.needStrategyRebalance(
      pairState,
      converter_,
      KyberDebtLib.getCurrentTick(IPool(pool)),
      poolPrice
    );
  }

  function needRebalanceStaking(State storage state) public view returns (bool needStake, bool needUnstake) {
    bool farmEnded = isFarmEnded(state.pId);
    bool haveLiquidity = state.pair.totalLiquidity > 0;
    bool staked = state.staked;
    needStake = haveLiquidity && !farmEnded && !staked;
    needUnstake = haveLiquidity && farmEnded && staked;
  }

  function isFarmEnded(uint pId) public view returns(bool) {
    (,,uint endTime,,,,,) = FARMING_CENTER.getPoolInfo(pId);
    return endTime < block.timestamp;
  }

  /// @notice Make rebalance without swaps (using borrowing only).
  /// @param converterLiquidator [TetuConverter, TetuLiquidator]
  /// @param checkNeedRebalance_ True if the function should ensure that the rebalance is required
  /// @param totalAssets_ Current value of totalAssets()
  /// @return tokenAmounts Token amounts for deposit. If length == 0 - rebalance wasn't made and no deposit is required.
  function rebalanceNoSwaps(
    IConverterStrategyBase.ConverterStrategyBaseState storage csbs,
    PairBasedStrategyLogicLib.PairState storage pairState,
    address[2] calldata converterLiquidator,
    uint totalAssets_,
    uint profitToCover,
    address splitter,
    bool checkNeedRebalance_,
    mapping(address => uint) storage liquidityThresholds_,
    bool needStakeOrUnstakeOnly
  ) external returns (
    uint[] memory tokenAmounts
  ) {
    RebalanceLocal memory v;
    _initLocalVars(v, ITetuConverter(converterLiquidator[0]), pairState, liquidityThresholds_);
    v.poolPrice = KyberLib.getPrice(address(v.pool), pairState.tokenB) * v.poolPriceAdjustment;
    bool needRebalance;
    int24 tick = KyberDebtLib.getCurrentTick(v.pool);
    (needRebalance, v.fuseStatusChangedAB, v.fuseStatusAB) = PairBasedStrategyLogicLib.needStrategyRebalance(
      pairState,
      v.converter,
      tick,
      v.poolPrice
    );

    // update fuse status if necessary
    if (needRebalance) {
      // we assume here, that needRebalance is true if any fuse has changed state, see needStrategyRebalance impl
      PairBasedStrategyLogicLib.updateFuseStatus(pairState, v.fuseStatusChangedAB, v.fuseStatusAB);
    }

    require(!checkNeedRebalance_ || needRebalance, KyberStrategyErrors.NO_REBALANCE_NEEDED);

    // rebalancing debt, setting new tick range
    if (needRebalance || needStakeOrUnstakeOnly) {
      KyberDebtLib.rebalanceNoSwaps(converterLiquidator, pairState, profitToCover, totalAssets_, splitter, v.liquidationThresholdsAB, tick);

      uint loss;
      (loss, tokenAmounts) = ConverterStrategyBaseLib2.getTokenAmountsPair(v.converter, totalAssets_, v.tokenA, v.tokenB, v.liquidationThresholdsAB);
      if (loss != 0) {
        ConverterStrategyBaseLib2.coverLossAndCheckResults(csbs, splitter, loss);
      }
      emit Rebalanced(loss, profitToCover, 0);
    }

    return tokenAmounts;
  }

  /// @notice Initialize {v} by state values
  function _initLocalVars(
    RebalanceLocal memory v,
    ITetuConverter converter_,
    PairBasedStrategyLogicLib.PairState storage pairState,
    mapping(address => uint) storage liquidityThresholds_
  ) internal view {
    v.pool = IPool(pairState.pool);
    v.fuseAB = pairState.fuseAB;
    v.converter = converter_;
    v.tokenA = pairState.tokenA;
    v.tokenB = pairState.tokenB;
    v.isStablePool = pairState.isStablePool;
    v.liquidationThresholdsAB[0] = AppLib._getLiquidationThreshold(liquidityThresholds_[v.tokenA]);
    v.liquidationThresholdsAB[1] = AppLib._getLiquidationThreshold(liquidityThresholds_[v.tokenB]);
    uint poolPriceDecimals = IERC20Metadata(v.tokenA).decimals();
    v.poolPriceAdjustment = poolPriceDecimals < 18 ? 10 ** (18 - poolPriceDecimals) : 1;
  }

  /// @notice Get proportion of not-underlying in the pool, [0...1e18]
  ///         prop.underlying : prop.not.underlying = 1e18 - PropNotUnderlying18 : propNotUnderlying18
  function getPropNotUnderlying18(PairBasedStrategyLogicLib.PairState storage pairState) view external returns (uint) {
    // get pool proportions
    IPool pool = IPool(pairState.pool);
    bool depositorSwapTokens = pairState.depositorSwapTokens;
    (int24 newLowerTick, int24 newUpperTick) = KyberDebtLib._calcNewTickRange(pool, pairState.lowerTick, pairState.upperTick, pairState.tickSpacing);
    (uint consumed0, uint consumed1) = KyberDebtLib.getEntryDataProportions(pool, newLowerTick, newUpperTick, depositorSwapTokens);

    require(consumed0 + consumed1 > 0, AppErrors.ZERO_VALUE);
    return consumed1 * 1e18 / (consumed0 + consumed1);
  }
  //endregion ------------------------------------------------ Rebalance

  //region ------------------------------------------------ WithdrawByAgg
  /// @param addr_ [tokenToSwap, aggregator, controller, converter, splitter]
  /// @param values_ [amountToSwap_, profitToCover, oldTotalAssets, entryToPool]
  /// @return completed All debts were closed, leftovers were swapped to proper proportions
  /// @return tokenAmountsOut Amounts to be deposited to pool. This array is empty if no deposit allowed/required.
  function withdrawByAggStep(
    IConverterStrategyBase.ConverterStrategyBaseState storage csbs,
    address[5] calldata addr_,
    uint[4] calldata values_,
    bytes memory swapData,
    bytes memory planEntryData,
    PairBasedStrategyLogicLib.PairState storage pairState,
    mapping(address => uint) storage liquidationThresholds
  ) external returns (
    bool completed,
    uint[] memory tokenAmountsOut
  ) {
    uint entryToPool = values_[3];
    address[2] memory tokens = [pairState.tokenA, pairState.tokenB];

    // Calculate amounts to be deposited to pool, calculate loss, fix profitToCover
    uint[] memory tokenAmounts;
    uint loss;
    (completed, tokenAmounts, loss) = PairBasedStrategyLogicLib.withdrawByAggStep(addr_, values_, swapData, planEntryData, tokens, liquidationThresholds);

    // cover loss
    if (loss != 0) {
      ConverterStrategyBaseLib2.coverLossAndCheckResults(
        csbs,
        addr_[4], // splitter
        loss
      );
    }
    emit RebalancedDebt(loss, values_[1], 0);

    if (entryToPool == PairBasedStrategyLib.ENTRY_TO_POOL_IS_ALLOWED
      || (entryToPool == PairBasedStrategyLib.ENTRY_TO_POOL_IS_ALLOWED_IF_COMPLETED && completed)
    ) {
      // We are going to enter to the pool: update lowerTick and upperTick, initialize tokenAmountsOut
      (pairState.lowerTick, pairState.upperTick) = KyberDebtLib._calcNewTickRange(
        IPool(pairState.pool),
        pairState.lowerTick,
        pairState.upperTick,
        pairState.tickSpacing
      );
      tokenAmountsOut = tokenAmounts;
    }

    return (completed, tokenAmountsOut); // hide warning
  }
  //endregion ------------------------------------------------ WithdrawByAgg
}
","keccak256":"0x8b2bc439adaca7592de88654f1edd9da9f6d83fc7c5f1279014191c0d29bc675","license":"BUSL-1.1"},"contracts/strategies/kyber/KyberDebtLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "../ConverterStrategyBaseLib.sol";
import "../ConverterStrategyBaseLib2.sol";
import "./KyberLib.sol";
import "./KyberStrategyErrors.sol";
import "./KyberConverterStrategyLogicLib.sol";
import "../../libs/BorrowLib.sol";
import "../pair/PairBasedStrategyLib.sol";
import "../pair/PairBasedStrategyLogicLib.sol";

library KyberDebtLib {
  using SafeERC20 for IERC20;

  //region  -------------------------------------------- Entry data
  /// @notice Calculate proportions of the tokens for entry kind 1
  /// @param pool Pool instance
  /// @param lowerTick The lower tick of the pool's main range.
  /// @param upperTick The upper tick of the pool's main range.
  /// @param depositorSwapTokens A boolean indicating if need to use token B instead of token A.
  /// @return prop0 Proportion onf token A. Any decimals are allowed, prop[0 or 1]/(prop0 + prop1) are important only
  /// @return prop1 Proportion onf token B. Any decimals are allowed, prop[0 or 1]/(prop0 + prop1) are important only
  function getEntryDataProportions(
    IPool pool,
    int24 lowerTick,
    int24 upperTick,
    bool depositorSwapTokens
  ) internal view returns (uint, uint) {
    address token1 = address(pool.token1());
    uint token1Price = KyberLib.getPrice(address(pool), token1);

    uint token1Decimals = IERC20Metadata(token1).decimals();

    uint token0Desired = token1Price;
    uint token1Desired = 10 ** token1Decimals;
    require(token1Desired != 0, AppErrors.ZERO_VALUE);

    // calculate proportions
    (uint consumed0, uint consumed1,) = KyberLib.addLiquidityPreview(address(pool), lowerTick, upperTick, token0Desired, token1Desired);
    return depositorSwapTokens
      ? (1e18*consumed1 * token1Price / token1Desired, 1e18*consumed0)
      : (1e18*consumed0, 1e18*consumed1 * token1Price / token1Desired);
  }

  function getEntryData(
    IPool pool,
    int24 lowerTick,
    int24 upperTick,
    bool depositorSwapTokens
  ) public view returns (bytes memory entryData) {
    (uint prop0, uint prop1) = getEntryDataProportions(pool, lowerTick, upperTick, depositorSwapTokens);
    entryData = abi.encode(1, prop0, prop1);
  }
  //endregion  -------------------------------------------- Entry data

  //region  -------------------------------------------- Calc tick range
  function calcTickRange(IPool pool, int24 tickRange, int24 tickSpacing) public view returns (int24 lowerTick, int24 upperTick) {
    (, int24 tick, ,) = pool.getPoolState();
    return PairBasedStrategyLogicLib.calcTickRange(tick, tickRange, tickSpacing);
  }

  function getCurrentTick(IPool pool) public view returns(int24 tick) {
    (, tick, ,) = pool.getPoolState();
  }

  /// @notice Calculate the new tick range for a Kyber pool, the tick is read from the pool
  /// @param pool The Kyber pool to calculate the new tick range for.
  /// @param lowerTick The current lower tick value for the pool.
  /// @param upperTick The current upper tick value for the pool.
  /// @param tickSpacing The tick spacing for the pool.
  /// @return lowerTickNew The new lower tick value for the pool.
  /// @return upperTickNew The new upper tick value for the pool.
  function _calcNewTickRange(
    IPool pool,
    int24 lowerTick,
    int24 upperTick,
    int24 tickSpacing
  ) internal view returns (int24 lowerTickNew, int24 upperTickNew) {
    int24 currentTick = getCurrentTick(pool);
    return _calcNewTickRangeForTick(currentTick, lowerTick, upperTick, tickSpacing);
  }

  /// @notice Calculate the new tick range for a Kyber pool, the tick is already known
  function _calcNewTickRangeForTick(
    int24 currentTick,
    int24 lowerTick,
    int24 upperTick,
    int24 tickSpacing
  ) internal pure returns (int24 lowerTickNew, int24 upperTickNew) {
    int24 fullTickRange = upperTick - lowerTick;
    int24 tickRange = fullTickRange == tickSpacing
      ? int24(0)
      : fullTickRange / 2;
    return PairBasedStrategyLogicLib.calcTickRange(currentTick, tickRange, tickSpacing);
  }
  //endregion  -------------------------------------------- Calc tick range

  //region  -------------------------------------------- Rebalance
  /// @param liquidationThresholdsAB [liquidityThreshold of token A, liquidityThreshold of tokenB]
  function rebalanceNoSwaps(
    address[2] calldata converterLiquidator,
    PairBasedStrategyLogicLib.PairState storage pairState,
    uint profitToCover,
    uint totalAssets,
    address splitter,
    uint[2] calldata liquidationThresholdsAB,
    int24 tick
  ) external {
    (int24 newLowerTick, int24 newUpperTick) = _calcNewTickRangeForTick(tick, pairState.lowerTick, pairState.upperTick, pairState.tickSpacing);
    (uint prop0, uint prop1) = getEntryDataProportions(IPool(pairState.pool), newLowerTick, newUpperTick, pairState.depositorSwapTokens);
    PairBasedStrategyLogicLib._rebalanceNoSwaps(
      converterLiquidator,
      pairState,
      profitToCover,
      totalAssets,
      splitter,
  liquidationThresholdsAB,
      prop0 * BorrowLib.SUM_PROPORTIONS / (prop0 + prop1)
    );
    (pairState.lowerTick, pairState.upperTick) = (newLowerTick, newUpperTick);
  }
  //endregion  -------------------------------------------- Rebalance
}
","keccak256":"0x8918dc754b31a3d83a32f9d291b5a776dd9870a83955da6a82f0fd5719f28dec","license":"BUSL-1.1"},"contracts/strategies/kyber/KyberLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

import "../../integrations/kyber/IPool.sol";
import "../../integrations/kyber/IBasePositionManager.sol";
import "../../integrations/kyber/IKyberSwapElasticLM.sol";
import "../../integrations/kyber/ITicksFeesReader.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/IERC20Metadata.sol";

library KyberLib {
  uint8 internal constant RESOLUTION = 96;
  uint internal constant Q96 = 0x1000000000000000000000000;
  uint private constant TWO_96 = 2 ** 96;
  /// @dev The minimum value that can be returned from #getSqrtRatioAtTick. Equivalent to getSqrtRatioAtTick(MIN_TICK)
  uint160 private constant MIN_SQRT_RATIO = 4295128739 + 1;
  /// @dev The maximum value that can be returned from #getSqrtRatioAtTick. Equivalent to getSqrtRatioAtTick(MAX_TICK)
  uint160 private constant MAX_SQRT_RATIO = 1461446703485210103287273052203988822378723970342 - 1;
  /// @dev The minimum tick that may be passed to #getSqrtRatioAtTick computed from log base 1.0001 of 2**-128
  int24 internal constant MIN_TICK = - 887272;
  /// @dev The maximum tick that may be passed to #getSqrtRatioAtTick computed from log base 1.0001 of 2**128
  int24 internal constant MAX_TICK = - MIN_TICK;

  function getPreviousTicks(IPool pool, int24 tickLower, int24 tickUpper) external view returns (int24[2] memory ticks) {
    (ticks[0],) = _getNearestInitializedTicks(pool, tickLower);
    (ticks[1],) = _getNearestInitializedTicks(pool, tickUpper);
  }

  function _getNearestInitializedTicks(IPool pool, int24 tick) internal view returns (int24 previous, int24 next) {
    require(MIN_TICK <= tick && tick <= MAX_TICK, 'tick not in range');
    // if queried tick already initialized, fetch and return values
    (previous, next) = pool.initializedTicks(tick);
    if (previous != 0 || next != 0) return (previous, next);

    // search downtick from MAX_TICK
    if (tick > 0) {
      previous = MAX_TICK;
      while (previous > tick) {
        (previous, ) = pool.initializedTicks(previous);
      }
      (, next) = pool.initializedTicks(previous);
    } else {
      // search uptick from MIN_TICK
      next = MIN_TICK;
      while (next < tick) {
        (, next) = pool.initializedTicks(next);
      }
      (previous, ) = pool.initializedTicks(next);
    }
  }

  function getTickSpacing(IPool pool) external view returns (int24) {
    return pool.tickDistance();
  }

  function addLiquidityPreview(address pool_, int24 lowerTick_, int24 upperTick_, uint amount0Desired_, uint amount1Desired_) external view returns (uint amount0Consumed, uint amount1Consumed, uint128 liquidityOut) {
    IPool pool = IPool(pool_);
    (uint160 sqrtRatioX96, , ,) = pool.getPoolState();
    liquidityOut = getLiquidityForAmounts(sqrtRatioX96, lowerTick_, upperTick_, amount0Desired_, amount1Desired_);
    (amount0Consumed, amount1Consumed) = getAmountsForLiquidity(sqrtRatioX96, lowerTick_, upperTick_, liquidityOut);
  }

  /// @notice Computes the maximum amount of liquidity received for a given amount of token0, token1, the current
  /// pool prices and the prices at the tick boundaries
  function getLiquidityForAmounts(
    uint160 sqrtRatioX96,
    int24 lowerTick,
    int24 upperTick,
    uint amount0,
    uint amount1
  ) public pure returns (uint128 liquidity) {
    uint160 sqrtRatioAX96 = _getSqrtRatioAtTick(lowerTick);
    uint160 sqrtRatioBX96 = _getSqrtRatioAtTick(upperTick);
    if (sqrtRatioAX96 > sqrtRatioBX96) {
      (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);
    }

    if (sqrtRatioX96 <= sqrtRatioAX96) {
      liquidity = _getLiquidityForAmount0(sqrtRatioAX96, sqrtRatioBX96, amount0);
    } else if (sqrtRatioX96 < sqrtRatioBX96) {
      uint128 liquidity0 = _getLiquidityForAmount0(sqrtRatioX96, sqrtRatioBX96, amount0);
      uint128 liquidity1 = _getLiquidityForAmount1(sqrtRatioAX96, sqrtRatioX96, amount1);
      liquidity = liquidity0 < liquidity1 ? liquidity0 : liquidity1;
    } else {
      liquidity = _getLiquidityForAmount1(sqrtRatioAX96, sqrtRatioBX96, amount1);
    }
  }

  /// @notice Computes the token0 and token1 value for a given amount of liquidity, the current
  /// pool prices and the prices at the tick boundaries
  function getAmountsForLiquidity(
    uint160 sqrtRatioX96,
    int24 lowerTick,
    int24 upperTick,
    uint128 liquidity
  ) public pure returns (uint amount0, uint amount1) {
    uint160 sqrtRatioAX96 = _getSqrtRatioAtTick(lowerTick);
    uint160 sqrtRatioBX96 = _getSqrtRatioAtTick(upperTick);

    if (sqrtRatioAX96 > sqrtRatioBX96) {
      (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);
    }

    if (sqrtRatioX96 <= sqrtRatioAX96) {
      amount0 = _getAmount0ForLiquidity(sqrtRatioAX96, sqrtRatioBX96, liquidity);
    } else if (sqrtRatioX96 < sqrtRatioBX96) {
      amount0 = _getAmount0ForLiquidity(sqrtRatioX96, sqrtRatioBX96, liquidity);
      amount1 = _getAmount1ForLiquidity(sqrtRatioAX96, sqrtRatioX96, liquidity);
    } else {
      amount1 = _getAmount1ForLiquidity(sqrtRatioAX96, sqrtRatioBX96, liquidity);
    }
  }

  /// @notice Calculates floor(a×b÷denominator) with full precision. Throws if result overflows a uint or denominator == 0
  /// @param a The multiplicand
  /// @param b The multiplier
  /// @param denominator The divisor
  /// @return result The 256-bit result
  /// @dev Credit to Remco Bloemen under MIT license https://xn--2-umb.com/21/muldiv
  function mulDiv(
    uint a,
    uint b,
    uint denominator
  ) public pure returns (uint result) {
    unchecked {
      // 512-bit multiply [prod1 prod0] = a * b
      // Compute the product mod 2**256 and mod 2**256 - 1
      // then use the Chinese Remainder Theorem to reconstruct
      // the 512 bit result. The result is stored in two 256
      // variables such that product = prod1 * 2**256 + prod0
      uint prod0;
      // Least significant 256 bits of the product
      uint prod1;
      // Most significant 256 bits of the product
      assembly {
        let mm := mulmod(a, b, not(0))
        prod0 := mul(a, b)
        prod1 := sub(sub(mm, prod0), lt(mm, prod0))
      }

      // Handle non-overflow cases, 256 by 256 division
      if (prod1 == 0) {
        require(denominator > 0);
        assembly {
          result := div(prod0, denominator)
        }
        return result;
      }

      // Make sure the result is less than 2**256.
      // Also prevents denominator == 0
      require(denominator > prod1);

      ///////////////////////////////////////////////
      // 512 by 256 division.
      ///////////////////////////////////////////////

      // Make division exact by subtracting the remainder from [prod1 prod0]
      // Compute remainder using mulmod
      uint remainder;
      assembly {
        remainder := mulmod(a, b, denominator)
      }
      // Subtract 256 bit number from 512 bit number
      assembly {
        prod1 := sub(prod1, gt(remainder, prod0))
        prod0 := sub(prod0, remainder)
      }

      // Factor powers of two out of denominator
      // Compute largest power of two divisor of denominator.
      // Always >= 1.
      // EDIT for 0.8 compatibility:
      // see: https://ethereum.stackexchange.com/questions/96642/unary-operator-cannot-be-applied-to-type-uint
      uint twos = denominator & (~denominator + 1);

      // Divide denominator by power of two
      assembly {
        denominator := div(denominator, twos)
      }

      // Divide [prod1 prod0] by the factors of two
      assembly {
        prod0 := div(prod0, twos)
      }
      // Shift in bits from prod1 into prod0. For this we need
      // to flip `twos` such that it is 2**256 / twos.
      // If twos is zero, then it becomes one
      assembly {
        twos := add(div(sub(0, twos), twos), 1)
      }
      prod0 |= prod1 * twos;

      // Invert denominator mod 2**256
      // Now that denominator is an odd number, it has an inverse
      // modulo 2**256 such that denominator * inv = 1 mod 2**256.
      // Compute the inverse by starting with a seed that is correct
      // correct for four bits. That is, denominator * inv = 1 mod 2**4
      uint inv = (3 * denominator) ^ 2;
      // Now use Newton-Raphson iteration to improve the precision.
      // Thanks to Hensel's lifting lemma, this also works in modular
      // arithmetic, doubling the correct bits in each step.
      inv *= 2 - denominator * inv;
      // inverse mod 2**8
      inv *= 2 - denominator * inv;
      // inverse mod 2**16
      inv *= 2 - denominator * inv;
      // inverse mod 2**32
      inv *= 2 - denominator * inv;
      // inverse mod 2**64
      inv *= 2 - denominator * inv;
      // inverse mod 2**128
      inv *= 2 - denominator * inv;
      // inverse mod 2**256

      // Because the division is now exact we can divide by multiplying
      // with the modular inverse of denominator. This will give us the
      // correct result modulo 2**256. Since the precoditions guarantee
      // that the outcome is less than 2**256, this is the final result.
      // We don't need to compute the high bits of the result and prod1
      // is no longer required.
      result = prod0 * inv;
      return result;
    }
  }

  /// @notice Calculates ceil(a×b÷denominator) with full precision. Throws if result overflows a uint or denominator == 0
  /// @param a The multiplicand
  /// @param b The multiplier
  /// @param denominator The divisor
  /// @return result The 256-bit result
  function mulDivRoundingUp(
    uint a,
    uint b,
    uint denominator
  ) internal pure returns (uint result) {
    result = mulDiv(a, b, denominator);
    if (mulmod(a, b, denominator) > 0) {
      require(result < type(uint).max);
      result++;
    }
  }

  /// @notice Calculates price in pool
  /// @return price with decimals of paired token
  function getPrice(address pool_, address tokenIn) public view returns (uint) {
    IPool pool = IPool(pool_);
    address token0 = address(pool.token0());
    address token1 = address(pool.token1());

    uint tokenInDecimals = tokenIn == token0 ? IERC20Metadata(token0).decimals() : IERC20Metadata(token1).decimals();
    uint tokenOutDecimals = tokenIn == token1 ? IERC20Metadata(token0).decimals() : IERC20Metadata(token1).decimals();
    (uint160 sqrtPriceX96,,,) = pool.getPoolState();

    uint divider = tokenOutDecimals < 18 ? _max(10 ** tokenOutDecimals / 10 ** tokenInDecimals, 1) : 1;

    uint priceDigits = _countDigits(uint(sqrtPriceX96));
    uint purePrice;
    uint precision;
    if (tokenIn == token0) {
      precision = 10 ** ((priceDigits < 29 ? 29 - priceDigits : 0) + tokenInDecimals);
      uint part = uint(sqrtPriceX96) * precision / TWO_96;
      purePrice = part * part;
    } else {
      precision = 10 ** ((priceDigits > 29 ? priceDigits - 29 : 0) + tokenInDecimals);
      uint part = TWO_96 * precision / uint(sqrtPriceX96);
      purePrice = part * part;
    }
    return purePrice / divider / precision / (precision > 1e18 ? (precision / 1e18) : 1);
  }

  /// @notice Computes the amount of liquidity received for a given amount of token0 and price range
  /// @dev Calculates amount0 * (sqrt(upper) * sqrt(lower)) / (sqrt(upper) - sqrt(lower)).
  /// @param sqrtRatioAX96 A sqrt price
  /// @param sqrtRatioBX96 Another sqrt price
  /// @param amount0 The amount0 being sent in
  /// @return liquidity The amount of returned liquidity
  function _getLiquidityForAmount0(uint160 sqrtRatioAX96, uint160 sqrtRatioBX96, uint amount0) internal pure returns (uint128 liquidity) {
    if (sqrtRatioAX96 > sqrtRatioBX96) {
      (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);
    }
    uint intermediate = mulDiv(sqrtRatioAX96, sqrtRatioBX96, Q96);
    return _toUint128(mulDiv(amount0, intermediate, sqrtRatioBX96 - sqrtRatioAX96));
  }

  /// @notice Computes the amount of liquidity received for a given amount of token1 and price range
  /// @dev Calculates amount1 / (sqrt(upper) - sqrt(lower)).
  /// @param sqrtRatioAX96 A sqrt price
  /// @param sqrtRatioBX96 Another sqrt price
  /// @param amount1 The amount1 being sent in
  /// @return liquidity The amount of returned liquidity
  function _getLiquidityForAmount1(uint160 sqrtRatioAX96, uint160 sqrtRatioBX96, uint amount1) internal pure returns (uint128 liquidity) {
    if (sqrtRatioAX96 > sqrtRatioBX96) {
      (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);
    }
    return _toUint128(mulDiv(amount1, Q96, sqrtRatioBX96 - sqrtRatioAX96));
  }

  /// @notice Computes the amount of token0 for a given amount of liquidity and a price range
  /// @param sqrtRatioAX96 A sqrt price
  /// @param sqrtRatioBX96 Another sqrt price
  /// @param liquidity The liquidity being valued
  /// @return amount0 The amount0
  function _getAmount0ForLiquidity(uint160 sqrtRatioAX96, uint160 sqrtRatioBX96, uint128 liquidity) internal pure returns (uint amount0) {
    if (sqrtRatioAX96 > sqrtRatioBX96) {
      (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);
    }
    return mulDivRoundingUp(1, mulDivRoundingUp(uint(liquidity) << RESOLUTION, sqrtRatioBX96 - sqrtRatioAX96, sqrtRatioBX96), sqrtRatioAX96);
  }

  /// @notice Computes the amount of token1 for a given amount of liquidity and a price range
  /// @param sqrtRatioAX96 A sqrt price
  /// @param sqrtRatioBX96 Another sqrt price
  /// @param liquidity The liquidity being valued
  /// @return amount1 The amount1
  function _getAmount1ForLiquidity(uint160 sqrtRatioAX96, uint160 sqrtRatioBX96, uint128 liquidity) internal pure returns (uint amount1) {
    if (sqrtRatioAX96 > sqrtRatioBX96) {
      (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);
    }
    return mulDivRoundingUp(liquidity, sqrtRatioBX96 - sqrtRatioAX96, Q96);
  }

  function _countDigits(uint n) internal pure returns (uint) {
    if (n == 0) {
      return 0;
    }
    uint count = 0;
    while (n != 0) {
      n = n / 10;
      ++count;
    }
    return count;
  }

  function _min(uint a, uint b) internal pure returns (uint) {
    return a < b ? a : b;
  }

  function _max(uint a, uint b) internal pure returns (uint) {
    return a > b ? a : b;
  }

  function _toUint128(uint x) private pure returns (uint128 y) {
    require((y = uint128(x)) == x);
  }

  /// @notice Calculates sqrt(1.0001^tick) * 2^96
  /// @dev Throws if |tick| > max tick
  /// @param tick The input tick for the above formula
  /// @return sqrtPriceX96 A Fixed point Q64.96 number representing the sqrt of the ratio of the two assets (token1/token0)
  /// at the given tick
  function _getSqrtRatioAtTick(int24 tick)
  internal
  pure
  returns (uint160 sqrtPriceX96)
  {
    uint256 absTick =
      tick < 0 ? uint256(- int256(tick)) : uint256(int256(tick));

    // EDIT: 0.8 compatibility
    require(absTick <= uint256(int256(MAX_TICK)), "T");

    uint256 ratio =
      absTick & 0x1 != 0
        ? 0xfffcb933bd6fad37aa2d162d1a594001
        : 0x100000000000000000000000000000000;
    if (absTick & 0x2 != 0)
      ratio = (ratio * 0xfff97272373d413259a46990580e213a) >> 128;
    if (absTick & 0x4 != 0)
      ratio = (ratio * 0xfff2e50f5f656932ef12357cf3c7fdcc) >> 128;
    if (absTick & 0x8 != 0)
      ratio = (ratio * 0xffe5caca7e10e4e61c3624eaa0941cd0) >> 128;
    if (absTick & 0x10 != 0)
      ratio = (ratio * 0xffcb9843d60f6159c9db58835c926644) >> 128;
    if (absTick & 0x20 != 0)
      ratio = (ratio * 0xff973b41fa98c081472e6896dfb254c0) >> 128;
    if (absTick & 0x40 != 0)
      ratio = (ratio * 0xff2ea16466c96a3843ec78b326b52861) >> 128;
    if (absTick & 0x80 != 0)
      ratio = (ratio * 0xfe5dee046a99a2a811c461f1969c3053) >> 128;
    if (absTick & 0x100 != 0)
      ratio = (ratio * 0xfcbe86c7900a88aedcffc83b479aa3a4) >> 128;
    if (absTick & 0x200 != 0)
      ratio = (ratio * 0xf987a7253ac413176f2b074cf7815e54) >> 128;
    if (absTick & 0x400 != 0)
      ratio = (ratio * 0xf3392b0822b70005940c7a398e4b70f3) >> 128;
    if (absTick & 0x800 != 0)
      ratio = (ratio * 0xe7159475a2c29b7443b29c7fa6e889d9) >> 128;
    if (absTick & 0x1000 != 0)
      ratio = (ratio * 0xd097f3bdfd2022b8845ad8f792aa5825) >> 128;
    if (absTick & 0x2000 != 0)
      ratio = (ratio * 0xa9f746462d870fdf8a65dc1f90e061e5) >> 128;
    if (absTick & 0x4000 != 0)
      ratio = (ratio * 0x70d869a156d2a1b890bb3df62baf32f7) >> 128;
    if (absTick & 0x8000 != 0)
      ratio = (ratio * 0x31be135f97d08fd981231505542fcfa6) >> 128;
    if (absTick & 0x10000 != 0)
      ratio = (ratio * 0x9aa508b5b7a84e1c677de54f3e99bc9) >> 128;
    if (absTick & 0x20000 != 0)
      ratio = (ratio * 0x5d6af8dedb81196699c329225ee604) >> 128;
    if (absTick & 0x40000 != 0)
      ratio = (ratio * 0x2216e584f5fa1ea926041bedfe98) >> 128;
    if (absTick & 0x80000 != 0)
      ratio = (ratio * 0x48a170391f7dc42444e8fa2) >> 128;

    if (tick > 0) ratio = type(uint256).max / ratio;

    // this divides by 1<<32 rounding up to go from a Q128.128 to a Q128.96.
    // we then downcast because we know the result always fits within 160 bits due to our tick input constraint
    // we round up in the division so getTickAtSqrtRatio of the output price is always consistent
    sqrtPriceX96 = uint160(
      (ratio >> 32) + (ratio % (1 << 32) == 0 ? 0 : 1)
    );
  }

  /// @notice Calculates the greatest tick value such that getRatioAtTick(tick) <= ratio
  /// @dev Throws in case sqrtPriceX96 < MIN_SQRT_RATIO, as MIN_SQRT_RATIO is the lowest value getRatioAtTick may
  /// ever return.
  /// @param sqrtPriceX96 The sqrt ratio for which to compute the tick as a Q64.96
  /// @return tick The greatest tick for which the ratio is less than or equal to the input ratio
  function _getTickAtSqrtRatio(uint160 sqrtPriceX96) internal pure returns (int24 tick) {
    // second inequality must be < because the price can never reach the price at the max tick
    require(
      sqrtPriceX96 >= MIN_SQRT_RATIO && sqrtPriceX96 < MAX_SQRT_RATIO,
      "R"
    );
    uint256 ratio = uint256(sqrtPriceX96) << 32;

    uint256 r = ratio;
    uint256 msb = 0;

    assembly {
      let f := shl(7, gt(r, 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF))
      msb := or(msb, f)
      r := shr(f, r)
    }
    assembly {
      let f := shl(6, gt(r, 0xFFFFFFFFFFFFFFFF))
      msb := or(msb, f)
      r := shr(f, r)
    }
    assembly {
      let f := shl(5, gt(r, 0xFFFFFFFF))
      msb := or(msb, f)
      r := shr(f, r)
    }
    assembly {
      let f := shl(4, gt(r, 0xFFFF))
      msb := or(msb, f)
      r := shr(f, r)
    }
    assembly {
      let f := shl(3, gt(r, 0xFF))
      msb := or(msb, f)
      r := shr(f, r)
    }
    assembly {
      let f := shl(2, gt(r, 0xF))
      msb := or(msb, f)
      r := shr(f, r)
    }
    assembly {
      let f := shl(1, gt(r, 0x3))
      msb := or(msb, f)
      r := shr(f, r)
    }
    assembly {
      let f := gt(r, 0x1)
      msb := or(msb, f)
    }

    if (msb >= 128) r = ratio >> (msb - 127);
    else r = ratio << (127 - msb);

    int256 log_2 = (int256(msb) - 128) << 64;

    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(63, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(62, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(61, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(60, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(59, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(58, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(57, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(56, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(55, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(54, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(53, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(52, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(51, f))
      r := shr(f, r)
    }
    assembly {
      r := shr(127, mul(r, r))
      let f := shr(128, r)
      log_2 := or(log_2, shl(50, f))
    }

    tick = _getFinalTick(log_2, sqrtPriceX96);
  }

  function _getFinalTick(int256 log_2, uint160 sqrtPriceX96) internal pure returns (int24 tick) {
    // 128.128 number
    int256 log_sqrt10001 = log_2 * 255738958999603826347141;

    int24 tickLow = int24((log_sqrt10001 - 3402992956809132418596140100660247210) >> 128);
    int24 tickHi = int24((log_sqrt10001 + 291339464771989622907027621153398088495) >> 128);

    tick = (tickLow == tickHi)
      ? tickLow
      : (_getSqrtRatioAtTick(tickHi) <= sqrtPriceX96 ? tickHi : tickLow);
  }
}
