pragma solidity 0.8.17;

import "../openzeppelin/SafeERC20.sol";
import "../openzeppelin/EnumerableMap.sol";
import "../interfaces/IProxyControlled.sol";
import "../proxy/ControllableV3.sol";

/// @title A central contract of the TETU platform.
///        Holds all important contract addresses.
///        Able to upgrade proxies with time-lock.
/// @author belbix
contract ControllerV2 is ControllableV3, IController {
  using EnumerableSet for EnumerableSet.AddressSet;
  using EnumerableMap for EnumerableMap.UintToUintMap;
  using EnumerableMap for EnumerableMap.UintToAddressMap;
  using EnumerableMap for EnumerableMap.AddressToUintMap;

  enum AddressType {
    UNKNOWN, // 0
    GOVERNANCE, // 1
    TETU_VOTER, // 2
    PLATFORM_VOTER, // 3
    LIQUIDATOR, // 4
    FORWARDER, // 5
    INVEST_FUND, // 6
    VE_DIST // 7
  }

  struct AddressAnnounce {
    uint _type;
    address newAddress;
    uint timeLockAt;
  }

  struct ProxyAnnounce {
    address proxy;
    address implementation;
    uint timeLockAt;
  }

  // *************************************************************
  //                        CONSTANTS
  // *************************************************************

  /// @dev Version of this contract. Adjust manually on each code modification.
  string public constant CONTROLLER_VERSION = "2.0.1";
  uint public constant TIME_LOCK = 18 hours;

  // *************************************************************
  //                        VARIABLES
  //                Keep names and ordering!
  //                 Add only in the bottom.
  // *************************************************************

  // --- restrictions

  /// @dev Gnosis safe multi signature wallet with maximum power under the platform.
  address public override governance;
  /// @dev Operators can execute not-critical functions of the platform.
  EnumerableSet.AddressSet internal _operators;

  // --- dependency

  /// @dev Voter for distribute TETU to gauges.
  address public override voter;
  /// @dev External solution for sell any tokens with minimal gas usage.
  address public override liquidator;
  /// @dev Accumulate performance fees and distribute them properly.
  address public override forwarder;
  /// @dev Contract for holding assets for the Second Stage
  address public override investFund;
  /// @dev Contract for accumulate TETU rewards for veTETU and weekly distribute them.
  address public override veDistributor;
  /// @dev Special voter for platform attributes.
  address public override platformVoter;

  // --- elements

  /// @dev Set of valid vaults
  EnumerableSet.AddressSet internal _vaults;

  // --- time locks

  EnumerableMap.UintToUintMap internal _addressTimeLocks;
  EnumerableMap.UintToAddressMap internal _addressAnnounces;

  EnumerableMap.AddressToUintMap internal _proxyTimeLocks;
  mapping(address => address) public proxyAnnounces;

  // *************************************************************
  //                        EVENTS
  // *************************************************************

  event AddressChangeAnnounced(uint _type, address value);
  event AddressChanged(uint _type, address oldAddress, address newAddress);
  event AddressAnnounceRemove(uint _type);
  event ProxyUpgradeAnnounced(address proxy, address implementation);
  event ProxyUpgraded(address proxy, address implementation);
  event ProxyAnnounceRemoved(address proxy);
  event RegisterVault(address vault);
  event VaultRemoved(address vault);
  event OperatorAdded(address operator);
  event OperatorRemoved(address operator);

  // *************************************************************
  //                        INIT
  // *************************************************************

  /// @dev Proxy initialization. Call it after contract deploy.
  function init(address _governance) external initializer {
    require(_governance != address(0), "WRONG_INPUT");
    governance = _governance;
    __Controllable_init(address(this));
    _operators.add(_governance);
  }

  // *************************************************************
  //                     RESTRICTIONS
  // *************************************************************

  function _onlyGovernance() internal view {
    require(msg.sender == governance, "DENIED");
  }

  function _onlyOperators() internal view {
    require(_operators.contains(msg.sender), "DENIED");
  }

  // *************************************************************
  //                        VIEWS
  // *************************************************************

  /// @dev Return all announced address changes.
  function addressAnnouncesList() external view returns (AddressAnnounce[] memory announces) {
    uint length = _addressTimeLocks.length();
    announces = new AddressAnnounce[](length);
    for (uint i; i < length; ++i) {
      (uint _type, uint timeLock) = _addressTimeLocks.at(i);
      address newAddress = _addressAnnounces.get(_type);
      announces[i] = AddressAnnounce(_type, newAddress, timeLock);
    }
  }

  /// @dev Return all announced proxy upgrades.
  function proxyAnnouncesList() external view returns (ProxyAnnounce[] memory announces) {
    uint length = _proxyTimeLocks.length();
    announces = new ProxyAnnounce[](length);
    for (uint i; i < length; ++i) {
      (address proxy, uint timeLock) = _proxyTimeLocks.at(i);
      address implementation = proxyAnnounces[proxy];
      announces[i] = ProxyAnnounce(proxy, implementation, timeLock);
    }
  }

  /// @dev Return true if the value exist in the operator set.
  function isOperator(address value) external view override returns (bool) {
    return _operators.contains(value);
  }

  /// @dev Return all operators. Expect the array will have reasonable size.
  function operatorsList() external view returns (address[] memory) {
    return _operators.values();
  }

  /// @dev Return all vaults. Array can be too big for use this function.
  function vaultsList() external view override returns (address[] memory) {
    return _vaults.values();
  }

  /// @dev Vault set size.
  function vaultsListLength() external view override returns (uint) {
    return _vaults.length();
  }

  /// @dev Return vault with given id. Ordering can be changed with time!
  function vaults(uint id) external view override returns (address) {
    return _vaults.at(id);
  }

  /// @dev Return true if the vault valid.
  function isValidVault(address _vault) external view override returns (bool) {
    return _vaults.contains(_vault);
  }

  /// @dev See {IERC165-supportsInterface}.
  function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
    return interfaceId == InterfaceIds.I_CONTROLLER || super.supportsInterface(interfaceId);
  }

  // *************************************************************
  //          SET ADDRESSES WITH TIME-LOCK PROTECTION
  // *************************************************************

  /// @dev Add announce information for given address type.
  function announceAddressChange(AddressType _type, address value) external {
    _onlyGovernance();
    require(value != address(0), "ZERO_VALUE");
    require(_addressAnnounces.set(uint(_type), value), "ANNOUNCED");
    _addressTimeLocks.set(uint(_type), block.timestamp + TIME_LOCK);

    emit AddressChangeAnnounced(uint(_type), value);
  }

  /// @dev Change time-locked address and remove lock info.
  ///      Less strict for reduce governance actions.
  function changeAddress(AddressType _type) external {
    _onlyOperators();

    address newAddress = _addressAnnounces.get(uint(_type));
    uint timeLock = _addressTimeLocks.get(uint(_type));
    // no need to check values - get for non-exist values will be reverted
    address oldAddress;

    if (_type == AddressType.GOVERNANCE) {
      oldAddress = governance;
      governance = newAddress;

    } else if (_type == AddressType.TETU_VOTER) {
      oldAddress = voter;
      voter = newAddress;

    } else if (_type == AddressType.LIQUIDATOR) {
      oldAddress = liquidator;
      liquidator = newAddress;

    } else if (_type == AddressType.FORWARDER) {
      _requireInterface(newAddress, InterfaceIds.I_FORWARDER);
      oldAddress = forwarder;
      forwarder = newAddress;

    } else if (_type == AddressType.INVEST_FUND) {
      oldAddress = investFund;
      investFund = newAddress;

    } else if (_type == AddressType.VE_DIST) {
      _requireInterface(newAddress, InterfaceIds.I_VE_DISTRIBUTOR);
      oldAddress = veDistributor;
      veDistributor = newAddress;

    } else if (_type == AddressType.PLATFORM_VOTER) {
      _requireInterface(newAddress, InterfaceIds.I_PLATFORM_VOTER);
      oldAddress = platformVoter;
      platformVoter = newAddress;
    } else {
      revert("UNKNOWN");
    }

    // skip time-lock for initialization
    if (oldAddress != address(0)) {
      require(timeLock < block.timestamp, "LOCKED");
    }

    _addressAnnounces.remove(uint(_type));
    _addressTimeLocks.remove(uint(_type));

    emit AddressChanged(uint(_type), oldAddress, newAddress);
  }

  /// @dev Remove announced address change.
  function removeAddressAnnounce(AddressType _type) external {
    _onlyOperators();

    _addressAnnounces.remove(uint(_type));
    _addressTimeLocks.remove(uint(_type));

    emit AddressAnnounceRemove(uint(_type));
  }

  // *************************************************************
  //          UPGRADE PROXIES WITH TIME-LOCK PROTECTION
  // *************************************************************

  function announceProxyUpgrade(
    address[] memory proxies,
    address[] memory implementations
  ) external {
    _onlyGovernance();
    require(proxies.length == implementations.length, "WRONG_INPUT");

    for (uint i; i < proxies.length; i++) {
      address proxy = proxies[i];
      address implementation = implementations[i];

      require(implementation != address(0), "ZERO_IMPL");
      require(_proxyTimeLocks.set(proxy, block.timestamp + TIME_LOCK), "ANNOUNCED");
      proxyAnnounces[proxy] = implementation;

      emit ProxyUpgradeAnnounced(proxy, implementation);
    }
  }

  /// @dev Upgrade proxy. Less strict for reduce governance actions.
  function upgradeProxy(address[] memory proxies) external {
    _onlyOperators();

    for (uint i; i < proxies.length; i++) {
      address proxy = proxies[i];
      uint timeLock = _proxyTimeLocks.get(proxy);
      // Map get will revert on not exist key, no need to check to zero
      address implementation = proxyAnnounces[proxy];

      require(timeLock < block.timestamp, "LOCKED");

      IProxyControlled(proxy).upgrade(implementation);

      _proxyTimeLocks.remove(proxy);
      delete proxyAnnounces[proxy];

      emit ProxyUpgraded(proxy, implementation);
    }
  }

  function removeProxyAnnounce(address proxy) external {
    _onlyOperators();

    _proxyTimeLocks.remove(proxy);
    delete proxyAnnounces[proxy];

    emit ProxyAnnounceRemoved(proxy);
  }

  // *************************************************************
  //                     REGISTER ACTIONS
  // *************************************************************

  /// @dev Register vault in the system.
  ///      Operator should do it as part of deployment process.
  function registerVault(address vault) external {
    _onlyOperators();

    require(_vaults.add(vault), "EXIST");
    emit RegisterVault(vault);
  }

  /// @dev Remove vault from the system. Only for critical cases.
  function removeVault(address vault) external {
    _onlyGovernance();

    require(_vaults.remove(vault), "NOT_EXIST");
    emit VaultRemoved(vault);
  }

  /// @dev Register new operator.
  function registerOperator(address value) external {
    _onlyGovernance();

    require(_operators.add(value), "EXIST");
    emit OperatorAdded(value);
  }

  /// @dev Remove operator.
  function removeOperator(address value) external {
    _onlyGovernance();

    require(_operators.remove(value), "NOT_EXIST");
    emit OperatorRemoved(value);
  }

}

// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "../openzeppelin/SafeERC20.sol";
import "../openzeppelin/ReentrancyGuard.sol";
import "../openzeppelin/EnumerableSet.sol";
import "../openzeppelin/Math.sol";
import "../interfaces/ITetuLiquidator.sol";
import "../interfaces/IVoter.sol";
import "../interfaces/IForwarder.sol";
import "../interfaces/IMultiPool.sol";
import "../interfaces/IBribe.sol";
import "../proxy/ControllableV3.sol";

/// @title This contract should contains a buffer of fees from strategies.
///        Periodically sell rewards and distribute to their destinations.
/// @author belbix
contract ForwarderV3 is ReentrancyGuard, ControllableV3, IForwarder {
  using SafeERC20 for IERC20;
  using EnumerableSet for EnumerableSet.AddressSet;

  // *************************************************************
  //                        CONSTANTS
  // *************************************************************
  /// @dev Version of this contract. Adjust manually on each code modification.
  string public constant FORWARDER_VERSION = "3.0.0";
  /// @dev Denominator for different ratios. It is default for the whole platform.
  uint public constant RATIO_DENOMINATOR = 100_000;
  /// @dev If slippage not defined for concrete token will be used 5% tolerance.
  uint public constant DEFAULT_SLIPPAGE = 5_000;
  /// @dev Max handled destinations from queue per call.
  uint public constant MAX_DESTINATIONS = 50;

  // *************************************************************
  //                        VARIABLES
  //                Keep names and ordering!
  //                 Add only in the bottom.
  // *************************************************************

  // -- ratios

  /// @dev Percent of tokens for Invest Fund
  uint public toInvestFundRatio;
  /// @dev Percent of tokens for gauges.
  uint public toGaugesRatio;

  // -- convert options

  /// @dev We will convert all tokens to TETU and distribute to destinations.
  address public tetu;
  /// @dev Contract for bribes distribution
  address public bribe;
  /// @dev Minimum amount of TETU tokens for distribution.
  uint public tetuThreshold;
  /// @dev Specific slippages for volatile tokens.
  mapping(address => uint) public tokenSlippage;

  // -- registered destinations

  /// @dev Tokens ready for distribution
  ///      This Set need for easy handle tokens off-chain, can be removed for gas optimisation.
  EnumerableSet.AddressSet internal _queuedTokens;
  /// @dev Token => Set of destinations with positive balances for the given token
  mapping(address => EnumerableSet.AddressSet) internal _destinationQueue;
  /// @dev Destination => Tokens ready to distribute
  mapping(address => EnumerableSet.AddressSet) internal _tokensPerDestination;
  /// @dev Token => Destination => Registered amount
  mapping(address => mapping(address => uint)) public amountPerDestination;

  // *************************************************************
  //                        EVENTS
  // *************************************************************

  event Distributed(
    address sender,
    address incomeToken,
    uint queuedBalance,
    uint tetuValue,
    uint tetuBalance,
    uint toInvestFund,
    uint toGauges,
    uint toBribes
  );
  event InvestFundRatioChanged(uint oldValue, uint newValue);
  event GaugeRatioChanged(uint oldValue, uint newValue);
  event TetuThresholdChanged(uint oldValue, uint newValue);
  event SlippageChanged(address token, uint value);

  // *************************************************************
  //                        INIT
  // *************************************************************

  /// @dev Proxy initialization. Call it after contract deploy.
  function init(address controller_, address _tetu, address _bribe) external initializer {
    _requireInterface(_bribe, InterfaceIds.I_BRIBE);
    _requireERC20(_tetu);
    __Controllable_init(controller_);
    tetu = _tetu;
    bribe = _bribe;
    // 10k TETU by default
    tetuThreshold = 10_000 * 1e18;
  }

  // *************************************************************
  //                      GOV ACTIONS
  // *************************************************************

  /// @dev Check that sender is governance.
  function _onlyGov() internal view {
    require(isGovernance(msg.sender), "DENIED");
  }

  /// @dev Set specific token slippage for given token.
  function setSlippage(address token, uint value) external {
    _onlyGov();
    require(value < RATIO_DENOMINATOR, "TOO_HIGH");

    tokenSlippage[token] = value;
    emit SlippageChanged(token, value);
  }

  /// @dev Set TETU threshold for distribution.
  function setTetuThreshold(uint value) external {
    _onlyGov();

    emit TetuThresholdChanged(tetuThreshold, value);
    tetuThreshold = value;
  }

  // *************************************************************
  //                     VOTER ACTIONS
  // *************************************************************

  /// @dev Check that sender is platform voter.
  function _onlyPlatformVoter() internal view {
    require(msg.sender == IController(controller()).platformVoter(), "DENIED");
  }

  /// @dev veTETU holders can change proportion via special voter.
  function setInvestFundRatio(uint value) external override {
    _onlyPlatformVoter();
    require(value <= RATIO_DENOMINATOR, "TOO_HIGH");

    emit InvestFundRatioChanged(toInvestFundRatio, value);
    toInvestFundRatio = value;
  }

  /// @dev veTETU holders can change proportion via special voter.
  function setGaugesRatio(uint value) external override {
    _onlyPlatformVoter();
    require(value <= RATIO_DENOMINATOR, "TOO_HIGH");

    emit GaugeRatioChanged(toGaugesRatio, value);
    toGaugesRatio = value;
  }

  // *************************************************************
  //                         VIEWS
  // *************************************************************

  /// @dev Size of array of tokens ready for distribution.
  function queuedTokensLength() external view returns (uint) {
    return _queuedTokens.length();
  }

  /// @dev Return queued token address for given id. Ordering can be changed between calls!
  function queuedTokenAt(uint i) external view returns (address) {
    return _queuedTokens.at(i);
  }

  /// @dev Size of array of tokens ready for distribution for given destination.
  function tokenPerDestinationLength(address destination) public view override returns (uint) {
    return _tokensPerDestination[destination].length();
  }

  /// @dev Return queued token address for given id and destination. Ordering can be changed between calls!
  function tokenPerDestinationAt(address destination, uint i) external view override returns (address) {
    return _tokensPerDestination[destination].at(i);
  }

  /// @dev Size of array of destinations for distribution for given token.
  function destinationsLength(address incomeToken) external view returns (uint) {
    return _destinationQueue[incomeToken].length();
  }

  /// @dev Return destination for given income token. Ordering can be changed between calls!
  function destinationAt(address incomeToken, uint i) external view returns (address) {
    return _destinationQueue[incomeToken].at(i);
  }

  /// @dev In case of too many queued destinations `targetTokenThreshold` should be lowered to reasonable value.
  function getQueuedDestinations(address token) public view returns (
    address[] memory queuedDestinations,
    uint[] memory queuedAmounts,
    uint balance
  ){
    EnumerableSet.AddressSet storage destinations = _destinationQueue[token];
    mapping(address => uint) storage tokenPerDst = amountPerDestination[token];
    uint length = Math.min(destinations.length(), MAX_DESTINATIONS);

    queuedDestinations = new address[](length);
    queuedAmounts = new uint[](length);
    balance = 0;
    for (uint i; i < length; ++i) {
      address destination = destinations.at(i);
      queuedDestinations[i] = destination;
      uint amount = tokenPerDst[destination];
      balance += amount;
      queuedAmounts[i] = amount;
    }
  }

  // *************************************************************
  //                     REGISTER INCOME
  // *************************************************************

  /// @dev Strategy should call this on reward liquidation after compound part.
  ///      Register tokens for the given destination.
  function registerIncome(
    address[] memory tokens,
    uint[] memory amounts,
    address vaults,
    bool isDistribute
  ) external nonReentrant override {

    for (uint i; i < tokens.length; ++i) {
      address token = tokens[i];
      uint amount = amounts[i];
      IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

      amountPerDestination[token][vaults] += amount;
      // suppose to be not unique, relatively cheap
      _destinationQueue[token].add(vaults);
      _tokensPerDestination[vaults].add(token);
      _queuedTokens.add(token);
    }

    // call it on cheap network
    if (isDistribute) {
      _distributeAll(vaults);
    }
  }

  // *************************************************************
  //                      DISTRIBUTE
  // *************************************************************

  function distributeAll(address destination) external nonReentrant override {
    _distributeAll(destination);
  }

  function _distributeAll(address destination) internal {
    address[] memory tokens = _tokensPerDestination[destination].values();
    uint length = tokens.length;
    for (uint i; i < length; ++i) {
      _distribute(tokens[i]);
    }
  }

  /// @dev Try to distribute given income token using  a target token from converter.
  ///      No strict access.
  ///      We assume that amount will be distributed before accumulate huge value reasonable for arbitrage attack.
  function distribute(address incomeToken) external nonReentrant override {
    _distribute(incomeToken);
  }

  function _distribute(address incomeToken) internal {

    (address[] memory vaults, uint[] memory queuedAmounts, uint queuedBalance)
    = getQueuedDestinations(incomeToken);

    IController controller_ = IController(controller());
    address _tetu = tetu;

    (uint tetuBalance, uint tetuValue) = _liquidate(controller_, incomeToken, _tetu, queuedBalance);

    if (tetuBalance != 0) {
      uint toInvestFund = tetuBalance * toInvestFundRatio / RATIO_DENOMINATOR;
      uint toGauges = (tetuBalance - toInvestFund) * toGaugesRatio / RATIO_DENOMINATOR;
      uint toBribes = (tetuBalance - toInvestFund) - toGauges;

      if (toInvestFund != 0) {
        IERC20(_tetu).safeTransfer(controller_.investFund(), toInvestFund);
      }

      if (toGauges != 0) {
        address voter = controller_.voter();
        IERC20(_tetu).safeApprove(voter, toGauges);
        IVoter(voter).notifyRewardAmount(toGauges);
      }

      if (toBribes != 0) {
        _distributeToBribes(
          incomeToken,
          _tetu,
          vaults,
          queuedAmounts,
          queuedBalance,
          toBribes
        );
      }

      emit Distributed(
        msg.sender,
        incomeToken,
        queuedBalance,
        tetuValue,
        tetuBalance,
        toInvestFund,
        toGauges,
        toBribes
      );
    }
  }

  function _liquidate(
    IController controller_,
    address tokenIn,
    address _tetu,
    uint amount
  ) internal returns (uint boughtTetu, uint tetuValue) {

    if (tokenIn == _tetu) {
      return (amount, amount);
    }

    boughtTetu = 0;
    ITetuLiquidator _liquidator = ITetuLiquidator(controller_.liquidator());

    (ITetuLiquidator.PoolData[] memory route, string memory error)
    = _liquidator.buildRoute(tokenIn, _tetu);

    if (route.length == 0) {
      revert(error);
    }

    // calculate balance in tetu value for check threshold
    tetuValue = _liquidator.getPriceForRoute(route, amount);

    // if the value higher than threshold distribute to destinations
    if (tetuValue > tetuThreshold) {

      uint slippage = tokenSlippage[tokenIn];
      if (slippage == 0) {
        slippage = DEFAULT_SLIPPAGE;
      }

      uint tetuBalanceBefore = IERC20(_tetu).balanceOf(address(this));

      _approveIfNeed(tokenIn, address(_liquidator), amount);
      _liquidator.liquidateWithRoute(route, amount, slippage);

      boughtTetu = IERC20(_tetu).balanceOf(address(this)) - tetuBalanceBefore;
    }
  }

  // *************************************************************
  //                      INTERNAL LOGIC
  // *************************************************************


  function _distributeToBribes(
    address incomeToken,
    address tokenToDistribute,
    address[] memory vaults,
    uint[] memory queuedAmounts,
    uint queuedBalance,
    uint toDistribute
  ) internal {
    address _bribe = bribe;
    uint _epoch = IBribe(_bribe).epoch();
    _approveIfNeed(tokenToDistribute, _bribe, toDistribute);

    uint remaining = toDistribute;
    for (uint i; i < vaults.length; i++) {
      uint toSend = toDistribute * queuedAmounts[i] / queuedBalance;
      // for avoid rounding issue send all remaining amount
      if (i == vaults.length - 1) {
        toSend = remaining;
      } else {
        remaining -= toSend;
      }

      _registerRewardInBribe(_bribe, vaults[i], tokenToDistribute);
      IBribe(_bribe).notifyForNextEpoch(vaults[i], tokenToDistribute, toSend);
      IBribe(_bribe).notifyDelayedRewards(vaults[i], tokenToDistribute, _epoch);

      // clear queued data
      _destinationQueue[incomeToken].remove(vaults[i]);
      delete amountPerDestination[incomeToken][vaults[i]];
      _tokensPerDestination[vaults[i]].remove(incomeToken);
    }

    if (IERC20(incomeToken).balanceOf(address(this)) == 0) {
      _queuedTokens.remove(incomeToken);
    }
  }

  function _registerRewardInBribe(address _bribe, address stakingToken, address rewardToken) internal {
    if (!IMultiPool(_bribe).isRewardToken(stakingToken, rewardToken)) {
      IMultiPool(_bribe).registerRewardToken(stakingToken, rewardToken);
    }
  }

  function _approveIfNeed(address token, address dst, uint amount) internal {
    if (IERC20(token).allowance(address(this), dst) < amount) {
      IERC20(token).safeApprove(dst, 0);
      IERC20(token).safeApprove(dst, type(uint).max);
    }
  }

  /// @dev See {IERC165-supportsInterface}.
  function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
    return interfaceId == InterfaceIds.I_FORWARDER || super.supportsInterface(interfaceId);
  }

}

