/// @title Swap tokens via UniswapV3 contracts.
/// @author belbix
contract Uni3Swapper is ControllableV3, ISwapper, IUniswapV3SwapCallback {
  using SafeERC20 for IERC20;

  struct SwapCallbackData {
    address tokenIn;
    uint amount;
  }

