interface IBalancerBoostedAaveStablePool {
  event AmpUpdateStarted(
    uint256 startValue,
    uint256 endValue,
    uint256 startTime,
    uint256 endTime
  );
  event AmpUpdateStopped(uint256 currentValue);
  event Approval(
    address indexed owner,
    address indexed spender,
    uint256 value
  );
