    require(shares <= maxMint(receiver), "MAX");

    assets = previewMint(shares);
    // No need to check for rounding error, previewMint rounds up.

    // Need to transfer before minting or ERC777s could reenter.
    _asset.safeTransferFrom(msg.sender, address(this), assets);

    if(totalSupply() == 0) {
      _mint(receiver, shares - INITIAL_SHARES);
      _mint(DEAD_ADDRESS, INITIAL_SHARES);
    } else {
      _mint(receiver, shares);
    }

    emit Deposit(msg.sender, receiver, assets, shares);

    afterDeposit(assets, shares, receiver);
  }

  function withdraw(
    uint assets,
    address receiver,
    address owner
  ) public nonReentrant virtual override returns (uint shares) {
    require(assets <= maxWithdraw(owner), "MAX");

    shares = previewWithdraw(assets);
    // No need to check for rounding error, previewWithdraw rounds up.

    if (msg.sender != owner) {
      uint allowed = _allowances[owner][msg.sender];
      // Saves gas for limited approvals.
      if (allowed != type(uint).max) {
        _allowances[owner][msg.sender] = allowed - shares;
      }
    }

    beforeWithdraw(assets, shares, receiver, owner);

    _burn(owner, shares);

    emit Withdraw(msg.sender, receiver, owner, assets, shares);

    _asset.safeTransfer(receiver, assets);
  }

  /// @dev Redeems shares from owner and sends assets to receiver.
  function redeem(
    uint shares,
    address receiver,
    address owner
  ) public nonReentrant virtual override returns (uint assets) {
    require(shares <= maxRedeem(owner), "MAX");

    if (msg.sender != owner) {
      uint allowed = _allowances[owner][msg.sender];
      // Saves gas for limited approvals.
      if (allowed != type(uint).max) {
        _allowances[owner][msg.sender] = allowed - shares;
      }
    }

    assets = previewRedeem(shares);
    // Check for rounding error since we round down in previewRedeem.
    require(assets != 0, "ZERO_ASSETS");

    beforeWithdraw(assets, shares, receiver, owner);

    _burn(owner, shares);

    emit Withdraw(msg.sender, receiver, owner, assets, shares);

    _asset.safeTransfer(receiver, assets);
  }

  /*//////////////////////////////////////////////////////////////
  //                  ACCOUNTING LOGIC
  //////////////////////////////////////////////////////////////*/

  /// @dev Total amount of the underlying asset that is “managed” by Vault
  function totalAssets() public view virtual override returns (uint);

  function convertToShares(uint assets) public view virtual override returns (uint) {
    uint supply = totalSupply();
    // Saves an extra SLOAD if totalSupply is non-zero.
    return supply == 0 ? assets : assets.mulDivDown(supply, totalAssets());
  }

  function convertToAssets(uint shares) public view virtual override returns (uint) {
    uint supply = totalSupply();
    // Saves an extra SLOAD if totalSupply is non-zero.
    return supply == 0 ? shares : shares.mulDivDown(totalAssets(), supply);
  }

  function previewDeposit(uint assets) public view virtual override returns (uint) {
    return convertToShares(assets);
  }

  function previewMint(uint shares) public view virtual override returns (uint) {
    uint supply = totalSupply();
    // Saves an extra SLOAD if totalSupply is non-zero.
    return supply == 0 ? shares : shares.mulDivUp(totalAssets(), supply);
  }

  function previewWithdraw(uint assets) public view virtual override returns (uint) {
    uint supply = totalSupply();
    // Saves an extra SLOAD if totalSupply is non-zero.
    return supply == 0 ? assets : assets.mulDivUp(supply, totalAssets());
  }

  function previewRedeem(uint shares) public view virtual override returns (uint) {
    return convertToAssets(shares);
  }

  ///////////////////////////////////////////////////////////////
  //           DEPOSIT/WITHDRAWAL LIMIT LOGIC
  ///////////////////////////////////////////////////////////////

  function maxDeposit(address) public view virtual override returns (uint) {
    return type(uint).max - 1;
  }

  function maxMint(address) public view virtual override returns (uint) {
    return type(uint).max - 1;
  }

  function maxWithdraw(address owner) public view virtual override returns (uint) {
    return convertToAssets(balanceOf(owner));
  }

  function maxRedeem(address owner) public view virtual override returns (uint) {
    return balanceOf(owner);
  }

  ///////////////////////////////////////////////////////////////
  //                INTERNAL HOOKS LOGIC
  ///////////////////////////////////////////////////////////////

  /// @param owner The owner of the amount to be withdrawn
  function beforeWithdraw(uint assets, uint shares, address receiver, address owner) internal virtual {}

  /// @param receiver The receiver of the shares received after deposit
  function afterDeposit(uint assets, uint shares, address receiver) internal virtual {}

  /**
 * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
  uint[49] private __gap;
}

// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "../openzeppelin/ReentrancyGuard.sol";
import "../openzeppelin/Math.sol";
import "../openzeppelin/SafeERC20.sol";
import "../openzeppelin/EnumerableMap.sol";
import "../interfaces/ITetuVaultV2.sol";
import "../interfaces/IStrategyV2.sol";
import "../interfaces/ISplitter.sol";
import "../interfaces/IProxyControlled.sol";
import "../proxy/ControllableV3.sol";

/// @title Proxy solution for connection a vault with multiple strategies
///        Version 2 has auto-rebalance logic adopted to strategies with fees.
/// @author belbix
contract StrategySplitterV2 is ControllableV3, ReentrancyGuard, ISplitter {
  using SafeERC20 for IERC20;
  using EnumerableMap for EnumerableMap.AddressToUintMap;

  // *********************************************
  //                  CONSTANTS
  // *********************************************

  /// @dev Version of this contract. Adjust manually on each code modification.
  string public constant SPLITTER_VERSION = "2.1.1";
  /// @dev APR denominator. Represent 100% APR.
  uint public constant APR_DENOMINATOR = 100_000;
  /// @dev Delay between hardwork calls for a strategy.
  uint public constant HARDWORK_DELAY = 12 hours;
  /// @dev How much APR history elements will be counted in average APR calculation.
  uint public constant HISTORY_DEEP = 3;
  /// @dev Time lock for adding new strategies.
  uint public constant TIME_LOCK = 18 hours;
  /// @dev 0.5% of max loss for strategy TVL
  uint public constant INVEST_LOSS_TOLERANCE = 500;
  /// @dev 0.5%  of max loss for strategy TVL
  uint public constant WITHDRAW_LOSS_TOLERANCE = 500;
  /// @dev 0.5% of max loss for strategy TVL
  uint public constant HARDWORK_LOSS_TOLERANCE = 500;

  // *********************************************
  //                 VARIABLES
  // *********************************************

  /// @dev Underlying asset
  address public override asset;
  /// @dev Connected vault
  address public override vault;
  /// @dev Array of strategies under control
  address[] public strategies;
  /// @dev Paused strategies
  mapping(address => bool) public pausedStrategies;
  /// @dev Current strategies average APRs. Uses for deposit/withdraw ordering.
  mapping(address => uint) public strategiesAPR;
  /// @dev Strategies APR history. Uses for calculate average APR.
  mapping(address => uint[]) public strategiesAPRHistory;
  /// @dev Last strategies doHardWork call timestamp. Uses for calls delay.
  mapping(address => uint) public lastHardWorks;
  /// @dev Flag represents doHardWork call. Need for not call HW on deposit again in connected vault.
  bool public override isHardWorking;
  /// @dev Strategy => timestamp. Strategies scheduled for adding.
  EnumerableMap.AddressToUintMap internal _scheduledStrategies;
  /// @dev Changed to true after a strategy adding
  bool internal inited;
  /// @dev How much underlying can be invested to the strategy
  mapping(address => uint) public strategyCapacity;
  /// @dev Return true for registered strategy
  mapping(address => bool) public isValidStrategy;

  // *********************************************
  //                  EVENTS
  // *********************************************

  event StrategyAdded(address strategy, uint apr);
  event StrategyRemoved(address strategy);
  event Rebalance(
    address topStrategy,
    address lowStrategy,
    uint percent,
    uint strategyLossOnWithdraw,
    uint strategyLossOnInvest,
    uint lowStrategyBalance
  );
  event HardWork(
    address sender,
    address strategy,
    uint tvl,
    uint earned,
    uint lost,
    uint apr,
    uint avgApr
  );
  event StrategyScheduled(address strategy, uint startTime, uint timeLock);
  event ScheduledStrategyRemove(address strategy);
  event ManualAprChanged(address sender, address strategy, uint newApr, uint oldApr);
  event Paused(address strategy, address sender);
  event ContinueInvesting(address strategy, uint apr, address sender);
  event Loss(address strategy, uint amount);
  event Invested(address strategy, uint amount);
  event WithdrawFromStrategy(address strategy);
  event SetStrategyCapacity(address strategy, uint capacity);
  event InvestFailed(string error);

  // *********************************************
  //                 INIT
  // *********************************************

  /// @dev Initialize contract after setup it as proxy implementation
  function init(address controller_, address _asset, address _vault) external initializer override {
    __Controllable_init(controller_);
    _requireERC20(_asset);
    asset = _asset;
    _requireInterface(_vault, InterfaceIds.I_TETU_VAULT_V2);
    vault = _vault;
  }

  // *********************************************
  //                 RESTRICTIONS
  // *********************************************

  /// @dev Restrict access only for governance
  function _onlyGov() internal view {
    require(isGovernance(msg.sender), "SS: Denied");
  }

  /// @dev Restrict access only for operators
  function _onlyOperators() internal view {
    require(IController(controller()).isOperator(msg.sender), "SS: Denied");
  }

  /// @dev Restrict access only for vault
  function _onlyVault() internal view {
    require(msg.sender == vault, "SS: Denied");
  }

  /// @dev Restrict access only for operators or vault
  function _onlyOperatorsOrVault() internal view {
    require(msg.sender == vault || IController(controller()).isOperator(msg.sender), "SS: Denied");
  }

  // *********************************************
  //                    VIEWS
  // *********************************************

  /// @dev Amount of underlying assets under control of splitter.
  function totalAssets() public view override returns (uint256){
    address _asset = asset;
    uint balance = IERC20(_asset).balanceOf(address(this));
    uint length = strategies.length;
    for (uint i = 0; i < length; i++) {
      balance += IStrategyV2(strategies[i]).totalAssets();
    }
    return balance;
  }

  /// @dev Return maximum available balance to withdraw without calling more than 1 strategy
  function maxCheapWithdraw() external view returns (uint) {
    address _asset = asset;
    uint strategyBalance;
    if (strategies.length != 0) {
      strategyBalance = IStrategyV2(strategies[0]).totalAssets();
    }
    return strategyBalance + IERC20(_asset).balanceOf(address(this));
  }

  /// @dev Length of strategy array
  function strategiesLength() external view returns (uint) {
    return strategies.length;
  }

  /// @dev Returns strategy array
  function allStrategies() external view returns (address[] memory) {
    return strategies;
  }

  /// @dev Length of APR history for given strategy
  function strategyAPRHistoryLength(address strategy) external view returns (uint) {
    return strategiesAPRHistory[strategy].length;
  }

  /// @dev Return all scheduled strategies with start lock time.
  function scheduledStrategies() external view returns (address[] memory _strategies, uint[] memory locks) {
    uint length = _scheduledStrategies.length();
    _strategies = new address[](length);
    locks = new uint[](length);
    for (uint i; i < length; ++i) {
      (_strategies[i], locks[i]) = _scheduledStrategies.at(i);
    }
  }

  /// @dev See {IERC165-supportsInterface}.
  function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
    return interfaceId == InterfaceIds.I_SPLITTER || super.supportsInterface(interfaceId);
  }

  /// @dev There are strategy capacities of two kinds: external (from splitter) and internal (from strategy)
  ///      We should use minimum value (but: zero external capacity means no capacity)
  function getStrategyCapacity(address strategy) public view returns (uint capacity) {
    capacity = strategyCapacity[strategy];
    if (capacity == 0) {
      capacity = IStrategyV2(strategy).capacity();
    } else {
      capacity = Math.min(capacity, IStrategyV2(strategy).capacity());
    }
  }

  // *********************************************
  //                GOV ACTIONS
  // *********************************************

  /// @dev Schedule strategy for adding in the splitter.
  ///      Not inited splitter(without strategies) not require scheduling.
  function scheduleStrategies(address[] memory _strategies) external {
    _onlyGov();

    for (uint i; i < _strategies.length; i++) {
      require(_scheduledStrategies.set(_strategies[i], block.timestamp), "SS: Exist");
      emit StrategyScheduled(_strategies[i], block.timestamp, TIME_LOCK);
    }
  }

  /// @dev Remove scheduled strategies.
  function removeScheduledStrategies(address[] memory _strategies) external {
    _onlyGov();

    for (uint i; i < _strategies.length; i++) {
      require(_scheduledStrategies.remove(_strategies[i]), "SS: Not exist");
      emit ScheduledStrategyRemove(_strategies[i]);
    }
  }

  /// @dev Add new managed strategy. Should be an uniq address.
  ///      Strategy should have the same underlying asset with current contract.
  function addStrategies(address[] memory _strategies, uint[] memory expectedAPR) external {
    // only initial action will require strict access
    // already scheduled strategies can be added by anyone
    require(_strategies.length == expectedAPR.length, "WRONG_INPUT");

    bool _inited = inited;
    address[] memory existStrategies = strategies;
    address[] memory addedStrategies = new address[](_strategies.length);
    for (uint i = 0; i < _strategies.length; i++) {
      address strategy = _strategies[i];
      uint apr = expectedAPR[i];

      // --- restrictions ----------

      require(IStrategyV2(strategy).asset() == asset, "SS: Wrong asset");
      require(IStrategyV2(strategy).splitter() == address(this), "SS: Wrong splitter");
      require(IControllable(strategy).isController(controller()), "SS: Wrong controller");
      require(!_contains(addedStrategies, strategy), "SS: Duplicate");
      require(!_contains(existStrategies, strategy) && !isValidStrategy[strategy], "SS: Already exist");
      require(IProxyControlled(strategy).implementation() != address(0), "SS: Wrong proxy");
      // allow add strategies without time lock only for the fist call (assume the splitter is new)
      if (_inited) {
        (bool found, uint startTime) = _scheduledStrategies.tryGet(strategy);
        require(found && startTime != 0 && startTime + TIME_LOCK < block.timestamp, "SS: Time lock");
        _scheduledStrategies.remove(strategy);
      } else {
        // only initial action requires strict access
        _onlyGov();
      }
      // ----------------------------

      isValidStrategy[strategy] = true;
      strategies.push(strategy);
      _setStrategyAPR(strategy, apr);
      addedStrategies[i] = strategy;
      lastHardWorks[strategy] = block.timestamp;
      emit StrategyAdded(strategy, apr);
    }
    _sortStrategiesByAPR();
    if (!_inited) {
      inited = true;
    }
  }

  /// @dev Remove given strategy, reset APR and withdraw all underlying to this contract
  function removeStrategies(address[] memory strategies_) external {
    _onlyGov();

    for (uint i = 0; i < strategies_.length; i++) {
      _removeStrategy(strategies_[i]);
    }
    _sortStrategiesByAPR();
  }

  function _removeStrategy(address strategy) internal {
    uint length = strategies.length;
    require(length > 0, "SS: Empty strategies");
    uint idx;
    bool found;
    for (uint256 i = 0; i < length; i++) {
      if (strategies[i] == strategy) {
        idx = i;
        found = true;
        break;
      }
    }
    require(found, "SS: Strategy not found");
    if (length > 1) {
      strategies[idx] = strategies[length - 1];
    }
    strategies.pop();

    _setStrategyAPR(strategy, 0);
    delete isValidStrategy[strategy];

    // for expensive strategies should be called before removing
    // without loss covering
    IStrategyV2(strategy).withdrawAllToSplitter();
    emit StrategyRemoved(strategy);
  }

  // *********************************************
  //                OPERATOR ACTIONS
  // *********************************************

  /// @dev Valid strategies was added after initialisation. It is a way for refreshing them.
  function refreshValidStrategies() external {
    _onlyOperators();

    uint length = strategies.length;
    for (uint i = 0; i < length; i++) {
      isValidStrategy[strategies[i]] = true;
    }
  }

  /// @dev Withdraw some percent from strategy with lowest APR and deposit to strategy with highest APR.
  /// @param percent Range of 1-100
  /// @param lossTolerance Range of 0-100_000
  function rebalance(uint percent, uint lossTolerance) external {
    _onlyOperators();

    require(lossTolerance <= HARDWORK_LOSS_TOLERANCE, "SS: Too high tolerance");
    uint length = strategies.length;
    require(length > 1, "SS: Length");
    require(percent <= 100, "SS: Percent");

    uint topStrategyWithoutCapacity = type(uint).max;

    for (uint i = 0; i < length; i++) {
      address strategy = strategies[i];
      uint capacity = getStrategyCapacity(strategy);
      if (capacity != 0) {
        uint strategyBalance = IStrategyV2(strategy).totalAssets();
        if (strategyBalance < capacity) {
          topStrategyWithoutCapacity = i;
          break;
        }
      } else {
        topStrategyWithoutCapacity = i;
        break;
      }
    }
    require(topStrategyWithoutCapacity != type(uint).max, "SS: All capped");


    address lowStrategy;

    uint lowStrategyBalance;
    // loop for all strategies since from top uncapped
    for (uint i = length; i > topStrategyWithoutCapacity + 1; i--) {
      lowStrategy = strategies[i - 1];
      lowStrategyBalance = IStrategyV2(lowStrategy).totalAssets();
      if (lowStrategyBalance == 0) {
        continue;
      }
      break;
    }

    // if we are able to withdraw something let's do it
    uint strategyLossOnWithdraw;
    if (lowStrategyBalance != 0) {
      strategyLossOnWithdraw = (percent == 100)
        ? IStrategyV2(lowStrategy).withdrawAllToSplitter()
        : IStrategyV2(lowStrategy).withdrawToSplitter(lowStrategyBalance * percent / 100);
    }
    // need to emit loss separately
    if (strategyLossOnWithdraw != 0) {
      // for withdraw need to use balance before
      _coverLoss(vault, strategyLossOnWithdraw, lossTolerance, lowStrategyBalance);
      emit Loss(lowStrategy, strategyLossOnWithdraw);
    }

    (address topStrategy, uint strategyLossOnInvest, uint strategyBalanceAfterInvest) = _investToTopStrategy(
      false // we assume here, that total-assets-amount of the strategy was just updated in withdraw above
    );
    require(topStrategy != address(0), "SS: Not invested");
    // need to emit loss separately
    if (strategyLossOnInvest != 0) {
      // for invest need to use balance after
      _coverLoss(vault, strategyLossOnInvest, lossTolerance, strategyBalanceAfterInvest);
      emit Loss(topStrategy, strategyLossOnInvest);
    }


    emit Rebalance(
      topStrategy,
      lowStrategy,
      percent,
      strategyLossOnWithdraw,
      strategyLossOnInvest,
      lowStrategyBalance
    );
  }

  function setAPRs(address[] memory _strategies, uint[] memory aprs) external {
    _onlyOperators();
    require(_strategies.length == aprs.length, "WRONG_INPUT");
    for (uint i; i < aprs.length; i++) {
      address strategy = _strategies[i];
      require(isValidStrategy[strategy], "SS: Invalid strategy");
      require(!pausedStrategies[strategy], "SS: Paused");
      uint oldAPR = strategiesAPR[strategy];
      _setStrategyAPR(strategy, aprs[i]);
      emit ManualAprChanged(msg.sender, strategy, aprs[i], oldAPR);
    }
    _sortStrategiesByAPR();
  }

  /// @dev Pause investing. For withdraw need to call emergencyExit() on the strategy.
  function pauseInvesting(address strategy) external {
    _onlyOperators();
    require(!pausedStrategies[strategy], "SS: Paused");
    require(isValidStrategy[strategy], "SS: Invalid strategy");

    pausedStrategies[strategy] = true;
    uint oldAPR = strategiesAPR[strategy];
    _setStrategyAPR(strategy, 0);
    _sortStrategiesByAPR();
    emit ManualAprChanged(msg.sender, strategy, 0, oldAPR);
    emit Paused(strategy, msg.sender);
  }

  /// @dev Resumes the ability to invest for given strategy.
  function continueInvesting(address strategy, uint apr) external {
    _onlyOperators();
    require(pausedStrategies[strategy], "SS: Not paused");
    require(isValidStrategy[strategy], "SS: Invalid strategy");

    pausedStrategies[strategy] = false;
    _setStrategyAPR(strategy, apr);
    _sortStrategiesByAPR();
    emit ManualAprChanged(msg.sender, strategy, apr, 0);
    emit ContinueInvesting(strategy, apr, msg.sender);
  }

  function setStrategyCapacity(address strategy, uint capacity) external {
    _onlyOperators();
    require(isValidStrategy[strategy], "SS: Invalid strategy");
    strategyCapacity[strategy] = capacity;
    emit SetStrategyCapacity(strategy, capacity);
  }

  // *********************************************
  //                VAULT ACTIONS
  // *********************************************

  /// @dev Invest to the first strategy in the array. Assume this strategy has highest APR.
  function investAll() external override {
    _onlyVault();

    if (strategies.length != 0) {
      (address strategy, uint strategyLoss, uint strategyBalanceAfterInvest) = _investToTopStrategy(true);
      if (strategyLoss > 0) {
        _coverLoss(msg.sender, strategyLoss, INVEST_LOSS_TOLERANCE, strategyBalanceAfterInvest);
        emit Loss(strategy, strategyLoss);
      }
    }
  }

  /// @dev Try to withdraw all from all strategies. May be too expensive to handle in one tx.
  function withdrawAllToVault() external override {
    _onlyVault();

    address _vault = vault;
    address _asset = asset;
    uint length = strategies.length;
    for (uint i = 0; i < length; i++) {
      uint strategyBalance = IStrategyV2(strategies[i]).totalAssets();
      if (strategyBalance != 0) {
        uint strategyLoss = IStrategyV2(strategies[i]).withdrawAllToSplitter();
        emit WithdrawFromStrategy(strategies[i]);

        // register possible loses
        if (strategyLoss != 0) {
          _coverLoss(_vault, strategyLoss, WITHDRAW_LOSS_TOLERANCE, strategyBalance);
          emit Loss(strategies[i], strategyLoss);
        }
      }
    }

    uint balanceAfter = IERC20(_asset).balanceOf(address(this));
    if (balanceAfter > 0) {
      IERC20(_asset).safeTransfer(_vault, balanceAfter);
    }
  }

  /// @dev Cascade withdraw from strategies start from lower APR until reach the target amount.
  ///      For large amounts with multiple strategies may not be possible to process this function.
  function withdrawToVault(uint256 amount) external override {
    _onlyVault();

    address _asset = asset;
    address _vault = vault;
    uint balance = IERC20(_asset).balanceOf(address(this));
    if (balance < amount) {
      uint remainingAmount = amount - balance;
      uint length = strategies.length;
      for (uint i = length; i > 0; i--) {
        IStrategyV2 strategy = IStrategyV2(strategies[i - 1]);

        uint strategyBalance = strategy.totalAssets();

        if (strategyBalance != 0) {
          // withdraw from strategy
          uint strategyLoss = (strategyBalance <= remainingAmount)
            ? strategy.withdrawAllToSplitter()
            : strategy.withdrawToSplitter(remainingAmount);
          emit WithdrawFromStrategy(address(strategy));

          uint currentBalance = IERC20(_asset).balanceOf(address(this));
          // assume that we can not decrease splitter balance during withdraw process
          uint withdrew = currentBalance - balance;
          balance = currentBalance;

          remainingAmount = withdrew < remainingAmount ? remainingAmount - withdrew : 0;

          // if we withdrew less than expected try to cover loss from vault insurance
          if (strategyLoss != 0) {
            _coverLoss(_vault, strategyLoss, WITHDRAW_LOSS_TOLERANCE, strategyBalance);
            emit Loss(address(strategy), strategyLoss);
          }

          if (balance >= amount) {
            break;
          }
        }
      }
    }

    if (balance != 0) {
      IERC20(_asset).safeTransfer(_vault, Math.min(amount, balance));
    }
  }

  // *********************************************
  //                HARD WORKS
  // *********************************************

  /// @dev Register profit/loss data for the strategy.
  ///      Sender assume to be a registered strategy.
  ///      Suppose to be used in actions where we updated assets price and need to cover the price diff gap.
  function coverPossibleStrategyLoss(uint earned, uint lost) external override {
    address strategy = msg.sender;
    require(isValidStrategy[strategy], "SS: Invalid strategy");

    uint tvl = IStrategyV2(strategy).totalAssets();
    _declareStrategyIncomeAndCoverLoss(strategy, tvl, 0, earned, lost, false);
  }

  /// @dev Call hard works for all strategies.
  function doHardWork() external override {
    _onlyOperatorsOrVault();

    // prevent recursion
    require(!isHardWorking, "SS: Already in hard work");
    isHardWorking = true;
    uint length = strategies.length;
    bool needReorder;
    for (uint i = 0; i < length; i++) {
      if (pausedStrategies[strategies[i]]) {
        continue;
      }
      bool result = _doHardWorkForStrategy(strategies[i], false);
      if (result) {
        needReorder = true;
      }
    }
    if (needReorder) {
      _sortStrategiesByAPR();
    }
    isHardWorking = false;
  }

  /// @dev Call hard work for given strategy.
  function doHardWorkForStrategy(address strategy, bool push) external {
    _onlyOperators();
    require(isValidStrategy[strategy], "SS: Invalid strategy");

    // prevent recursion
    require(!isHardWorking, "SS: Already in hard work");
    isHardWorking = true;
    bool result = _doHardWorkForStrategy(strategy, push);
    if (result) {
      _sortStrategiesByAPR();
    }
    isHardWorking = false;
  }

  function _doHardWorkForStrategy(address strategy, bool push) internal returns (bool) {
    uint lastHardWork = lastHardWorks[strategy];

    if (
      (
        lastHardWork + HARDWORK_DELAY < block.timestamp
        && IStrategyV2(strategy).isReadyToHardWork()
        && !pausedStrategies[strategy]
      )
      || push
    ) {
      uint sinceLastHardWork = block.timestamp - lastHardWork;
      uint tvl = IStrategyV2(strategy).totalAssets();
      if (tvl != 0) {
        (uint earned, uint lost) = IStrategyV2(strategy).doHardWork();

        (uint apr, uint avgApr) = _declareStrategyIncomeAndCoverLoss(strategy, tvl, sinceLastHardWork, earned, lost, true);

        lastHardWorks[strategy] = block.timestamp;

        emit HardWork(
          msg.sender,
          strategy,
          tvl,
          earned,
          lost,
          apr,
          avgApr
        );
        return true;
      }
    }
    return false;
  }

  function _declareStrategyIncomeAndCoverLoss(
    address strategy,
    uint strategyTvl,
    uint sinceLastHardWork,
    uint earned,
    uint lost,
    bool registerApr
  ) internal returns (uint apr, uint avgApr) {
    apr = 0;
    avgApr = 0;
    uint lostForCovering = lost > earned ? lost - earned : 0;
    if (lostForCovering > 0) {
      _coverLoss(vault, lostForCovering, HARDWORK_LOSS_TOLERANCE, strategyTvl);
    }

    if (registerApr) {
      if (earned > lost) {
        apr = computeApr(strategyTvl, earned - lost, sinceLastHardWork);
      }
      strategiesAPRHistory[strategy].push(apr);
      avgApr = averageApr(strategy);
      strategiesAPR[strategy] = avgApr;
    }
  }

  function averageApr(address strategy) public view returns (uint) {
    uint[] storage history = strategiesAPRHistory[strategy];
    uint aprSum;
    uint length = history.length;
    uint count = Math.min(HISTORY_DEEP, length);
    if (count != 0) {
      for (uint i; i < count; i++) {
        aprSum += history[length - i - 1];
      }
      return aprSum / count;
    }
    return 0;
  }

  /// @dev https://www.investopedia.com/terms/a/apr.asp
  ///      TVL and rewards should be in the same currency and with the same decimals
  function computeApr(uint tvl, uint earned, uint duration) public pure returns (uint) {
    if (tvl == 0 || duration == 0) {
      return 0;
    }
    return earned * 1e18 * APR_DENOMINATOR * uint(365) / tvl / (duration * 1e18 / 1 days);
  }

  /// @dev Insertion sorting algorithm for using with arrays fewer than 10 elements.
  ///      Based on https://medium.com/coinmonks/sorting-in-solidity-without-comparison-4eb47e04ff0d
  ///      Sort strategies array by APR values from strategiesAPR map. Highest to lowest.
  function _sortStrategiesByAPR() internal {
    unchecked {
      uint length = strategies.length;
      for (uint i = 1; i < length; i++) {
        address key = strategies[i];
        uint j = i - 1;
        while ((int(j) >= 0) && strategiesAPR[strategies[j]] < strategiesAPR[key]) {
          strategies[j + 1] = strategies[j];
          j--;
        }
        strategies[j + 1] = key;
      }
    }
  }

  /// @dev Return true if given item found in address array
  function _contains(address[] memory array, address _item) internal pure returns (bool) {
    for (uint256 i = 0; i < array.length; i++) {
      if (array[i] == _item) {
        return true;
      }
    }
    return false;
  }

  function _setStrategyAPR(address strategy, uint apr) internal {
    strategiesAPR[strategy] = apr;
    // need to override last values of history for properly calculate average apr
    for (uint i; i < HISTORY_DEEP; i++) {
      strategiesAPRHistory[strategy].push(apr);
    }
  }

  /// @param updateTotalAssetsBeforeInvest TotalAssets of strategy should be updated before investing.
  /// @return strategy Selected strategy or zero
  /// @return strategyLoss Loss should be covered from Insurance
  function _investToTopStrategy(
    bool updateTotalAssetsBeforeInvest
  ) internal returns (
    address strategy,
    uint strategyLoss,
    uint strategyBalanceAfterInvest
  ) {
    address _asset = asset;
    uint balance = IERC20(_asset).balanceOf(address(this));
    // no actions for zero balance, return empty strategy
    if (balance != 0) {
      uint length = strategies.length;
      for (uint i; i < length; ++i) {
        strategy = strategies[i];
        if (pausedStrategies[strategy]) {
          continue;
        }

        uint capacity = getStrategyCapacity(strategy);

        uint strategyBalance = IStrategyV2(strategy).totalAssets();
        uint toInvest;
        if (capacity > strategyBalance) {
          toInvest = Math.min(capacity - strategyBalance, balance);
        } else {
          continue;
        }

        if (toInvest != 0) {
          IERC20(_asset).safeTransfer(strategy, toInvest);
          strategyLoss = IStrategyV2(strategy).investAll(toInvest, updateTotalAssetsBeforeInvest);
          strategyBalanceAfterInvest = strategyBalance + toInvest;
          emit Invested(strategy, toInvest);
          break;
        }
      }
    }

    return (strategy, strategyLoss, strategyBalanceAfterInvest);
  }

  function _coverLoss(address _vault, uint amount, uint lossTolerance, uint strategyBalance) internal {
    if (amount != 0) {
      require(strategyBalance != 0, "SS: Strategy balance should not be zero");
      require(amount * 100_000 / strategyBalance <= lossTolerance, "SS: Loss too high");
      ITetuVaultV2(_vault).coverLoss(amount);
    }
  }

}

// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "../openzeppelin/Math.sol";
import "../interfaces/ISplitter.sol";
import "../interfaces/ITetuVaultV2.sol";
import "../interfaces/IGauge.sol";
import "../proxy/ControllableV3.sol";
import "./ERC4626Upgradeable.sol";

/// @title Vault for storing underlying tokens and managing them with strategy splitter.
/// @author belbix
contract TetuVaultV2 is ERC4626Upgradeable, ControllableV3, ITetuVaultV2 {
  using SafeERC20 for IERC20;
  using FixedPointMathLib for uint;

  // *************************************************************
  //                        CONSTANTS
  // *************************************************************

  /// @dev Version of this contract. Adjust manually on each code modification.
  string public constant VAULT_VERSION = "2.1.4";
  /// @dev Denominator for buffer calculation. 100% of the buffer amount.
  uint constant public BUFFER_DENOMINATOR = 100_000;
  /// @dev Denominator for fee calculation.
  uint constant public FEE_DENOMINATOR = 100_000;
  /// @dev Max 1% fee.
  uint constant public MAX_FEE = FEE_DENOMINATOR / 100;

  // *************************************************************
  //                        VARIABLES
  //                Keep names and ordering!
  //                 Add only in the bottom.
  // *************************************************************

  /// @dev Strategy splitter. Should be setup after deploy.
  ISplitter public splitter;
  /// @dev Connected gauge for stakeless rewards
  IGauge public gauge;
  /// @dev Dedicated contract for holding insurance for covering share price loss.
  IVaultInsurance public override insurance;
  /// @dev Percent of assets that will always stay in this vault.
  uint public buffer;

  /// @dev Maximum amount for withdraw. Max UINT256 by default.
  uint public maxWithdrawAssets;
  /// @dev Maximum amount for redeem. Max UINT256 by default.
  uint public maxRedeemShares;
  /// @dev Maximum amount for deposit. Max UINT256 by default.
  uint public maxDepositAssets;
  /// @dev Maximum amount for mint. Max UINT256 by default.
  uint public maxMintShares;
  /// @dev Fee for deposit/mint actions. Zero by default.
  uint public override depositFee;
  /// @dev Fee for withdraw/redeem actions. Zero by default.
  uint public override withdrawFee;

  /// @dev Trigger doHardwork on invest action. Enabled by default.
  bool public doHardWorkOnInvest;

  /// @dev msg.sender => block when request sent. Should be cleared on deposit/withdraw action
  ///      For simplification we are setup new withdraw request on each deposit/transfer
  mapping(address => uint) public withdrawRequests;

  /// @dev A user should wait this block amounts before able to withdraw.
  uint public withdrawRequestBlocks;

  // *************************************************************
  //                        EVENTS
  // *************************************************************

  event Init(
    address controller,
    address asset,
    string name,
    string symbol,
    address gauge,
    uint buffer
  );
  event SplitterSetup(address splitter);
  event BufferChanged(uint oldValue, uint newValue);
  event Invest(address splitter, uint amount);
  event MaxWithdrawChanged(uint maxAssets, uint maxShares);
  event MaxDepositChanged(uint maxAssets, uint maxShares);
  event FeeChanged(uint depositFee, uint withdrawFee);
  event DoHardWorkOnInvestChanged(bool oldValue, bool newValue);
  event FeeTransfer(uint amount);
  event LossCovered(uint amount);
  event WithdrawRequested(address sender, uint startBlock);
  event WithdrawRequestBlocks(uint blocks);

  // *************************************************************
  //                        INIT
  // *************************************************************

  /// @dev Proxy initialization. Call it after contract deploy.
  function init(
    address controller_,
    IERC20 asset_,
    string memory _name,
    string memory _symbol,
    address _gauge,
    uint _buffer
  ) external initializer override {
    require(_buffer <= BUFFER_DENOMINATOR, "!BUFFER");
    require(_gauge != address(0), "!GAUGE");
    require(IControllable(_gauge).isController(controller_), "!GAUGE_CONTROLLER");

    _requireERC20(address(asset_));
    __ERC4626_init(asset_, _name, _symbol);
    __Controllable_init(controller_);

    _requireInterface(_gauge, InterfaceIds.I_GAUGE);
    gauge = IGauge(_gauge);
    buffer = _buffer;

    // set defaults
    maxWithdrawAssets = type(uint).max;
    maxRedeemShares = type(uint).max;
    maxDepositAssets = type(uint).max - 1;
    maxMintShares = type(uint).max - 1;
    doHardWorkOnInvest = true;
    withdrawRequestBlocks = 5;
    emit WithdrawRequestBlocks(5);

    emit Init(
      controller_,
      address(asset_),
      _name,
      _symbol,
      _gauge,
      _buffer
    );
  }

  function initInsurance(IVaultInsurance _insurance) external override {
    require(address(insurance) == address(0), "INITED");
    _requireInterface(address(_insurance), InterfaceIds.I_VAULT_INSURANCE);

    require(_insurance.vault() == address(this), "!VAULT");
    require(_insurance.asset() == address(_asset), "!ASSET");
    insurance = _insurance;
  }

  // *************************************************************
  //                      GOV ACTIONS
  // *************************************************************

  /// @dev Set block amount before user will able to withdraw after a request.
  function setWithdrawRequestBlocks(uint blocks) external {
    require(isGovernance(msg.sender), "DENIED");
    withdrawRequestBlocks = blocks;
    emit WithdrawRequestBlocks(blocks);
  }

  /// @dev Set new buffer value. Should be lower than denominator.
  function setBuffer(uint _buffer) external {
    require(isGovernance(msg.sender), "DENIED");
    require(_buffer <= BUFFER_DENOMINATOR, "BUFFER");

    emit BufferChanged(buffer, _buffer);
    buffer = _buffer;
  }

  /// @dev Set maximum available to deposit amounts.
  ///      Could be zero values in emergency case when need to pause malicious actions.
  function setMaxDeposit(uint maxAssets, uint maxShares) external {
    require(isGovernance(msg.sender), "DENIED");

    maxDepositAssets = maxAssets;
    maxMintShares = maxShares;
    emit MaxDepositChanged(maxAssets, maxShares);
  }

  /// @dev Set maximum available to withdraw amounts.
  ///      Could be zero values in emergency case when need to pause malicious actions.
  function setMaxWithdraw(uint maxAssets, uint maxShares) external {
    require(isGovernance(msg.sender), "DENIED");

    maxWithdrawAssets = maxAssets;
    maxRedeemShares = maxShares;
    emit MaxWithdrawChanged(maxAssets, maxShares);
  }

  /// @dev Set deposit/withdraw fees
  function setFees(uint _depositFee, uint _withdrawFee) external {
    require(isGovernance(msg.sender), "DENIED");
    require(_depositFee <= MAX_FEE && _withdrawFee <= MAX_FEE, "TOO_HIGH");

    depositFee = _depositFee;
    withdrawFee = _withdrawFee;
    emit FeeChanged(_depositFee, _withdrawFee);
  }

  /// @dev If activated will call doHardWork on splitter on each invest action.
  function setDoHardWorkOnInvest(bool value) external {
    require(isGovernance(msg.sender), "DENIED");
    emit DoHardWorkOnInvestChanged(doHardWorkOnInvest, value);
    doHardWorkOnInvest = value;
  }

  /// @dev Set splitter address. Can not change exist splitter.
  function setSplitter(address _splitter) external override {
    IERC20 asset_ = _asset;
    require(address(splitter) == address(0), "DENIED");
    _requireInterface(_splitter, InterfaceIds.I_SPLITTER);
    require(ISplitter(_splitter).asset() == address(asset_), "WRONG_UNDERLYING");
    require(ISplitter(_splitter).vault() == address(this), "WRONG_VAULT");
    require(IControllable(_splitter).isController(controller()), "WRONG_CONTROLLER");
    asset_.approve(_splitter, type(uint).max);
    splitter = ISplitter(_splitter);
    emit SplitterSetup(_splitter);
  }

  // *************************************************************
  //                        VIEWS
  // *************************************************************

  /// @dev Total amount of the underlying asset that is “managed” by Vault
  function totalAssets() public view override returns (uint) {
    return _asset.balanceOf(address(this)) + splitter.totalAssets();
  }

  /// @dev Amount of assets under control of strategy splitter.
  function splitterAssets() external view returns (uint) {
    return splitter.totalAssets();
  }

  /// @dev Price of 1 full share
  function sharePrice() external view returns (uint) {
    uint units = 10 ** uint256(decimals());
    uint totalSupply_ = totalSupply();
    return totalSupply_ == 0
      ? units
      : units * totalAssets() / totalSupply_;
  }

  /// @dev See {IERC165-supportsInterface}.
  function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
    return interfaceId == InterfaceIds.I_TETU_VAULT_V2 || super.supportsInterface(interfaceId);
  }

  // *************************************************************
  //                 DEPOSIT LOGIC
  // *************************************************************

  function previewDeposit(uint assets) public view virtual override returns (uint) {
    uint shares = convertToShares(assets);
    return shares - (shares * depositFee / FEE_DENOMINATOR);
  }

  function previewMint(uint shares) public view virtual override returns (uint) {
    uint supply = totalSupply();
    if (supply != 0) {
      uint assets = shares.mulDivUp(totalAssets(), supply);
      return assets * FEE_DENOMINATOR / (FEE_DENOMINATOR - depositFee);
    } else {
      return shares * FEE_DENOMINATOR / (FEE_DENOMINATOR - depositFee);
    }
  }

  /// @dev Calculate available to invest amount and send this amount to splitter
  function afterDeposit(uint assets, uint /*shares*/, address receiver) internal override {
    // reset withdraw request if necessary
    if (withdrawRequestBlocks != 0) {
      withdrawRequests[receiver] = block.number;
    }

    address _splitter = address(splitter);
    IERC20 asset_ = _asset;
    uint _depositFee = depositFee;
    // send fee to insurance contract
    if (_depositFee != 0) {
      uint toFees = assets * _depositFee / FEE_DENOMINATOR;
      asset_.safeTransfer(address(insurance), toFees);
      emit FeeTransfer(toFees);
    }
    uint256 toInvest = _availableToInvest(_splitter, asset_);
    // invest only when buffer is filled
    if (toInvest > 0) {

      // need to check recursive hardworks
      if (doHardWorkOnInvest && !ISplitter(_splitter).isHardWorking()) {
        ISplitter(_splitter).doHardWork();
      }

      asset_.safeTransfer(_splitter, toInvest);
      ISplitter(_splitter).investAll();
      emit Invest(_splitter, toInvest);
    }
  }

  /// @notice Returns amount of assets ready to invest to the splitter
  function _availableToInvest(address _splitter, IERC20 asset_) internal view returns (uint) {
    uint _buffer = buffer;
    if (_splitter == address(0) || _buffer == BUFFER_DENOMINATOR) {
      return 0;
    }
    uint assetsInVault = asset_.balanceOf(address(this));
    uint assetsInSplitter = ISplitter(_splitter).totalAssets();
    uint wantInvestTotal = (assetsInVault + assetsInSplitter)
    * (BUFFER_DENOMINATOR - _buffer) / BUFFER_DENOMINATOR;
    if (assetsInSplitter >= wantInvestTotal) {
      return 0;
    } else {
      uint remainingToInvest = wantInvestTotal - assetsInSplitter;
      return remainingToInvest <= assetsInVault ? remainingToInvest : assetsInVault;
    }
  }

  // *************************************************************
  //                 WITHDRAW LOGIC
  // *************************************************************

  function requestWithdraw() external {
    withdrawRequests[msg.sender] = block.number;
    emit WithdrawRequested(msg.sender, block.number);
  }

  /// @dev Withdraw all available shares for tx sender.
  ///      The revert is expected if the balance is higher than `maxRedeem`
  function withdrawAll() external {
    redeem(balanceOf(msg.sender), msg.sender, msg.sender);
  }

  function previewWithdraw(uint assets) public view virtual override returns (uint) {
    uint supply = totalSupply();
    uint _totalAssets = totalAssets();
    if (_totalAssets == 0) {
      return assets;
    }
    uint shares = assets.mulDivUp(supply, _totalAssets);
    shares = shares * FEE_DENOMINATOR / (FEE_DENOMINATOR - withdrawFee);
    return supply == 0 ? assets : shares;
  }

  function previewRedeem(uint shares) public view virtual override returns (uint) {
    shares = shares - (shares * withdrawFee / FEE_DENOMINATOR);
    return convertToAssets(shares);
  }

  function maxDeposit(address) public view override returns (uint) {
    return maxDepositAssets;
  }

  function maxMint(address) public view override returns (uint) {
    return maxMintShares;
  }

  function maxWithdraw(address owner) public view override returns (uint) {
    uint assets = convertToAssets(balanceOf(owner));
    assets -= assets.mulDivUp(withdrawFee, FEE_DENOMINATOR);
    return Math.min(maxWithdrawAssets, assets);
  }

  function maxRedeem(address owner) public view override returns (uint) {
    return Math.min(maxRedeemShares, balanceOf(owner));
  }

  /// @dev Internal hook for getting necessary assets from splitter.
  function beforeWithdraw(uint assets, uint shares, address /*receiver*/, address owner_) internal override {
    // check withdraw request if necessary
    uint _withdrawRequestBlocks = withdrawRequestBlocks;
    if (_withdrawRequestBlocks != 0) {
      // ensure that at least {_withdrawRequestBlocks} blocks were passed since last deposit/withdraw of the owner
      uint wr = withdrawRequests[owner_];
      require(wr != 0 && wr + _withdrawRequestBlocks < block.number, "NOT_REQUESTED");
      withdrawRequests[owner_] = block.number;
    }

    uint _withdrawFee = withdrawFee;
    uint fromSplitter;
    if (_withdrawFee != 0) {
      // add fee amount
      fromSplitter = assets * FEE_DENOMINATOR / (FEE_DENOMINATOR - _withdrawFee);
    } else {
      fromSplitter = assets;
    }

    IERC20 asset_ = _asset;
    uint balance = asset_.balanceOf(address(this));
    // if not enough balance in the vault withdraw from strategies
    if (balance < fromSplitter) {
      _processWithdrawFromSplitter(
        fromSplitter,
        shares,
        totalSupply(),
        buffer,
        splitter,
        balance
      );
    }
    balance = asset_.balanceOf(address(this));
    require(assets <= balance, "SLIPPAGE");

    // send fee amount to insurance for keep correct calculations
    // in case of compensation it will lead to double transfer
    // but we assume that it will be rare case
    if (_withdrawFee != 0) {
      // we should compensate possible slippage from user fee too
      uint toFees = Math.min(fromSplitter - assets, balance - assets);
      if (toFees != 0) {
        asset_.safeTransfer(address(insurance), toFees);
        emit FeeTransfer(toFees);
      }
    }
  }

  /// @dev Do necessary calculation for withdrawing from splitter and move assets to vault.
  ///      If splitter not defined must not be called.
  function _processWithdrawFromSplitter(
    uint assetsNeed,
    uint shares,
    uint totalSupply_,
    uint _buffer,
    ISplitter _splitter,
    uint assetsInVault
  ) internal {
    // withdraw everything from the splitter to accurately check the share value
    if (shares == totalSupply_) {
      _splitter.withdrawAllToVault();
    } else {
      uint assetsInSplitter = _splitter.totalAssets();

      // we should always have buffer amount inside the vault
      // assume `assetsNeed` can not be higher than entire balance
      uint expectedBuffer = (assetsInSplitter + assetsInVault - assetsNeed) * _buffer / BUFFER_DENOMINATOR;

      // this code should not be called if `assetsInVault` higher than `assetsNeed`
      uint missing = Math.min(expectedBuffer + assetsNeed - assetsInVault, assetsInSplitter);
      // if zero should be resolved on splitter side
      _splitter.withdrawToVault(missing);
    }
  }

  // *************************************************************
  //                 INSURANCE LOGIC
  // *************************************************************

  function coverLoss(uint amount) external override {
    require(msg.sender == address(splitter), "!SPLITTER");
    IVaultInsurance _insurance = insurance;
    uint balance = _asset.balanceOf(address(_insurance));
    uint toRecover = Math.min(amount, balance);
    _insurance.transferToVault(toRecover);
    emit LossCovered(toRecover);
  }

  // *************************************************************
  //                 GAUGE HOOK
  // *************************************************************

  /// @dev Connect this vault to the gauge for non-contract addresses.
  function _afterTokenTransfer(
    address from,
    address to,
    uint
  ) internal override {
    // refresh withdraw request if necessary
    if (withdrawRequestBlocks != 0) {
      withdrawRequests[from] = block.number;
      withdrawRequests[to] = block.number;
    }
    gauge.handleBalanceChange(from);
    gauge.handleBalanceChange(to);
  }

}

// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "../tools/TetuERC165.sol";
import "../interfaces/IController.sol";
import "../interfaces/IERC20.sol";
import "../interfaces/ITetuVaultV2.sol";
import "../interfaces/ISplitter.sol";
import "../proxy/ProxyControlled.sol";
import "./VaultInsurance.sol";
import "../lib/InterfaceIds.sol";

/// @title Factory for vaults.
/// @author belbix
contract VaultFactory is TetuERC165 {

  // *************************************************************
  //                        VARIABLES
  // *************************************************************

  /// @dev Platform controller, need for restrictions.
  address public immutable controller;

  /// @dev TetuVaultV2 contract address
  address public vaultImpl;
  /// @dev VaultInsurance contract address
  address public vaultInsuranceImpl;
  /// @dev StrategySplitterV2 contract address
  address public splitterImpl;

  /// @dev Array of deployed vaults.
  address[] public deployedVaults;

  // *************************************************************
  //                        EVENTS
  // *************************************************************

  event VaultDeployed(
    address sender,
    address asset,
    string name,
    string symbol,
    address gauge,
    uint buffer,
    address vaultProxy,
    address vaultLogic,
    address insurance,
    address splitterProxy,
    address splitterLogic
  );
  event VaultImplChanged(address value);
  event VaultInsuranceImplChanged(address value);
  event SplitterImplChanged(address value);

  constructor(
    address _controller,
    address _vaultImpl,
    address _vaultInsuranceImpl,
    address _splitterImpl
  ) {
    _requireInterface(_controller, InterfaceIds.I_CONTROLLER);
    _requireInterface(_vaultImpl, InterfaceIds.I_TETU_VAULT_V2);
    _requireInterface(_vaultInsuranceImpl, InterfaceIds.I_VAULT_INSURANCE);
    _requireInterface(_splitterImpl, InterfaceIds.I_SPLITTER);

    controller = _controller;
    vaultImpl = _vaultImpl;
    vaultInsuranceImpl = _vaultInsuranceImpl;
    splitterImpl = _splitterImpl;
  }

  function deployedVaultsLength() external view returns (uint) {
    return deployedVaults.length;
  }

  // *************************************************************
  //                        RESTRICTIONS
  // *************************************************************

  /// @dev Only governance
  modifier onlyGov() {
    require(msg.sender == IController(controller).governance(), "!GOV");
    _;
  }

  /// @dev Only platform operators
  modifier onlyOperator() {
    require(IController(controller).isOperator(msg.sender), "!OPERATOR");
    _;
  }

  // *************************************************************
  //                        GOV ACTIONS
  // *************************************************************

  /// @dev Set TetuVaultV2 contract address
  function setVaultImpl(address value) external onlyGov {
    _requireInterface(value, InterfaceIds.I_TETU_VAULT_V2);
    vaultImpl = value;
    emit VaultImplChanged(value);
  }

  /// @dev Set VaultInsurance contract address
  function setVaultInsuranceImpl(address value) external onlyGov {
    _requireInterface(value, InterfaceIds.I_VAULT_INSURANCE);
    vaultInsuranceImpl = value;
    emit VaultInsuranceImplChanged(value);
  }

  /// @dev Set StrategySplitterV2 contract address
  function setSplitterImpl(address value) external onlyGov {
    _requireInterface(value, InterfaceIds.I_SPLITTER);
    splitterImpl = value;
    emit SplitterImplChanged(value);
  }

  // *************************************************************
  //                    OPERATOR ACTIONS
  // *************************************************************

  /// @dev Create and init vault with given attributes.
  function createVault(
    IERC20 asset,
    string memory name,
    string memory symbol,
    address gauge,
    uint buffer
  ) external onlyOperator {
    // clone vault implementations
    address vaultProxy = address(new ProxyControlled());
    address vaultLogic = vaultImpl;
    // init proxy
    IProxyControlled(vaultProxy).initProxy(vaultLogic);
    // init vault
    ITetuVaultV2(vaultProxy).init(
      controller,
      asset,
      name,
      symbol,
      gauge,
      buffer
    );
    // clone insurance
    VaultInsurance insurance = new VaultInsurance();
    // init insurance
    insurance.init(vaultProxy, address(asset));
    // set insurance to vault
    ITetuVaultV2(vaultProxy).initInsurance(insurance);

    // clone splitter
    address splitterProxy = address(new ProxyControlled());
    address splitterLogic = splitterImpl;
    // init proxy
    IProxyControlled(splitterProxy).initProxy(splitterLogic);
    // init splitter
    ISplitter(splitterProxy).init(controller, address(asset), vaultProxy);
    // set splitter to vault
    ITetuVaultV2(vaultProxy).setSplitter(splitterProxy);

    deployedVaults.push(vaultProxy);

    emit VaultDeployed(
      msg.sender,
      address(asset),
      name,
      symbol,
      gauge,
      buffer,
      vaultProxy,
      vaultLogic,
      address(insurance),
      splitterProxy,
      splitterLogic
    );
  }

}

