contract HfPoolAdapter is IPoolAdapter, IPoolAdapterInitializerWithAP, Initializable {
  using SafeERC20 for IERC20;

  //-----------------------------------------------------
  ///    Data types
  //-----------------------------------------------------

  /// @notice To avoid stack too deep
  struct LocalRepayVars {
