  /// @notice Max allowed value of (sumCollateralSafe - sumBorrowPlusEffects) / liquidity, decimals 18
  uint private constant MAX_DIVISION18 = 1e10;
  string public constant POOL_ADAPTER_VERSION = "1.0.2";

