// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

library KyberStrategyErrors {

  string public constant NEED_REBALANCE = "KS-1 Need rebalance";
  string public constant WRONG_BALANCE = "KS-2 Wrong balance";
  string public constant INCORRECT_TICK_RANGE = "KS-3 Incorrect tickRange";
  string public constant INCORRECT_REBALANCE_TICK_RANGE = "KS-4 Incorrect rebalanceTickRange";
  string public constant INCORRECT_ASSET = "KS-5 Incorrect asset";
  string public constant WRONG_FEE = "KS-6 Wrong fee";
  string public constant WRONG_LIQUIDITY = "KS-7 Wrong liquidity";
  string public constant NO_REBALANCE_NEEDED = "KS-9 No rebalance needed";
  string public constant BALANCE_LOWER_THAN_FEE = "KS-10 Balance lower than fee";
  string public constant NOT_CALLBACK_CALLER = "KS-11 Not callback caller";
  string public constant ZERO_PROFIT_HOLDER = "KS-13 Zero strategy profit holder";
  string public constant NOT_UNSTAKED = "KS-14 Liquidity must be unstaked";
  string public constant FUSE_IS_ACTIVE = "KS-14 Fuse is active";
}

contracts/strategies/pair/PairBasedStrat.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@tetu_io/tetu-converter/contracts/interfaces/ITetuConverter.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/ITetuLiquidator.sol";
import "../ConverterStrategyBaseLib.sol";
import "../../interfaces/IPoolProportionsProvider.sol";
import "../../libs/BorrowLib.sol";

/// @notice Library for the UniV3-like strategies with two tokens in the pool
/// @dev The library contains quoteWithdrawStep/withdrawStep-related logic
library PairBasedStrategyLib {
  //region ------------------------------------------------ Constants
  uint internal constant _ASSET_LIQUIDATION_SLIPPAGE = 300;
  /// @notice In all functions below array {token} contains underlying at the first position
  uint internal constant IDX_ASSET = 0;
  /// @notice In all functions below array {token} contains not-underlying at the second position
  uint internal constant IDX_TOKEN = 1;

  uint internal constant IDX_SWAP_1 = 0;
  uint internal constant IDX_REPAY_1 = 1;
  uint internal constant IDX_SWAP_2 = 2;
  uint internal constant IDX_REPAY_2 = 3;

  /// @notice A gap to reduce AmountToSwap calculated inside quoteWithdrawByAgg, [0...100_000]
  uint public constant GAP_AMOUNT_TO_SWAP = 100;

  /// @notice Enter to the pool at the end of withdrawByAggStep
  uint public constant ENTRY_TO_POOL_IS_ALLOWED = 1;
  /// @notice Enter to the pool at the end of withdrawByAggStep only if full withdrawing has been completed
  uint public constant ENTRY_TO_POOL_IS_ALLOWED_IF_COMPLETED = 2;

  /// @notice Fuse thresholds are set as array: [LOWER_LIMIT_ON, LOWER_LIMIT_OFF, UPPER_LIMIT_ON, UPPER_LIMIT_OFF]
  ///         If the price falls below LOWER_LIMIT_ON the fuse is turned ON
  ///         When the prices raises back and reaches LOWER_LIMIT_OFF, the fuse is turned OFF
  ///         In the same way, if the price raises above UPPER_LIMIT_ON the fuse is turned ON
  ///         When the prices falls back and reaches UPPER_LIMIT_OFF, the fuse is turned OFF
  ///
  ///         Example: [0.9, 0.92, 1.08, 1.1]
  ///         Price falls below 0.9 - fuse is ON. Price rises back up to 0.92 - fuse is OFF.
  ///         Price raises more and reaches 1.1 - fuse is ON again. Price falls back and reaches 1.08 - fuse OFF again.
  uint public constant FUSE_IDX_LOWER_LIMIT_ON = 0;
  uint public constant FUSE_IDX_LOWER_LIMIT_OFF = 1;
  uint public constant FUSE_IDX_UPPER_LIMIT_ON = 2;
  uint public constant FUSE_IDX_UPPER_LIMIT_OFF = 3;

  uint public constant IDX_ADDR_DEFAULT_STATE_TOKEN_A = 0;
  uint public constant IDX_ADDR_DEFAULT_STATE_TOKEN_B = 1;
  uint public constant IDX_ADDR_DEFAULT_STATE_POOL = 2;
  uint public constant IDX_ADDR_DEFAULT_STATE_PROFIT_HOLDER = 3;

  uint public constant IDX_TICK_DEFAULT_STATE_TICK_SPACING = 0;
  uint public constant IDX_TICK_DEFAULT_STATE_LOWER_TICK = 1;
  uint public constant IDX_TICK_DEFAULT_STATE_UPPER_TICK = 2;
  uint public constant IDX_TICK_DEFAULT_STATE_REBALANCE_TICK_RANGE = 3;

  uint public constant IDX_NUMS_DEFAULT_STATE_TOTAL_LIQUIDITY = 0;
  uint public constant IDX_NUMS_DEFAULT_STATE_FUSE_STATUS_A = 1;
  uint public constant IDX_NUMS_DEFAULT_STATE_FUSE_STATUS_B = 2;
  uint public constant IDX_NUMS_DEFAULT_STATE_WITHDRAW_DONE = 3;
  uint public constant IDX_NUMS_DEFAULT_STATE_THRESHOLD_A_0 = 4;
  uint public constant IDX_NUMS_DEFAULT_STATE_THRESHOLD_A_1 = 5;
  uint public constant IDX_NUMS_DEFAULT_STATE_THRESHOLD_A_2 = 6;
  uint public constant IDX_NUMS_DEFAULT_STATE_THRESHOLD_A_3 = 7;
  uint public constant IDX_NUMS_DEFAULT_STATE_THRESHOLD_B_0 = 8;
  uint public constant IDX_NUMS_DEFAULT_STATE_THRESHOLD_B_1 = 9;
  uint public constant IDX_NUMS_DEFAULT_STATE_THRESHOLD_B_2 = 10;
  uint public constant IDX_NUMS_DEFAULT_STATE_THRESHOLD_B_3 = 11;
  uint public constant IDX_NUMS_DEFAULT_STATE_LAST_REBALANCE_NO_SWAP = 12;

  uint public constant IDX_BOOL_VALUES_DEFAULT_STATE_IS_STABLE_POOL = 0;
  uint public constant IDX_BOOL_VALUES_DEFAULT_STATE_DEPOSITOR_SWAP_TOKENS = 1;

  /// @notice 1inch router V5
  address internal constant ONEINCH = 0x1111111254EEB25477B68fb85Ed929f73A960582;
  /// @notice OpenOceanExchangeProxy
  address internal constant OPENOCEAN = 0x6352a56caadC4F1E25CD6c75970Fa768A3304e64;

  string public constant UNKNOWN_SWAP_ROUTER = "PBS-1 Unknown router";
  string public constant INCORRECT_TICK_RANGE = "PBS-3 Incorrect tickRange";
  string public constant INCORRECT_REBALANCE_TICK_RANGE = "PBS-4 Incorrect rebalanceTickRange";
  string public constant INCORRECT_ASSET = "PBS-5 Incorrect asset";

  //endregion ------------------------------------------------ Constants

  //region ------------------------------------------------ Data types
  /// @notice The fuse is triggered when the price rises above or falls below the limit 1.
  ///         If the fuse was triggered, all assets are withdrawn from the pool on the strategy balance.
  ///         Then all debts should be closed and all assets should be converted to underlying.
  ///         The fuse is turned off automatically when the price falls below or rises above the limit 2
  ///         and all assets are deposited back to the pool.
  enum FuseStatus {
    /// @notice Fuse is not used at all
    FUSE_DISABLED_0,
    /// @notice Fuse is not triggered, assets are deposited to the pool
    FUSE_OFF_1,
    /// @notice Fuse was triggered by lower limit, assets was withdrawn from the pool, but active debts can exist
    FUSE_ON_LOWER_LIMIT_2,
    /// @notice Fuse was triggered by upper limit, assets was withdrawn from the pool, but active debts can exist
    FUSE_ON_UPPER_LIMIT_3
  }

  struct SwapByAggParams {
    bool useLiquidator;
    address tokenToSwap;
    /// @notice Aggregator to make swap
    ///         It is 0 if useLiquidator is true
    ///         It can be equal to address of liquidator if we use liquidator as aggregator (in tests)
    address aggregator;
    uint amountToSwap;
    /// @notice Swap-data prepared off-chain (route, amounts, etc). 0 - use liquidator to make swap
    bytes swapData;
  }

  struct GetAmountToRepay2Local {
    uint x;
    uint y;
    uint c0;
    uint b0;
    uint alpha;
    int b;
  }

  struct FuseStateParams {
    FuseStatus status;
    /// @notice Price thresholds [LOWER_LIMIT_ON, LOWER_LIMIT_OFF, UPPER_LIMIT_ON, UPPER_LIMIT_OFF]
    /// @dev see PairBasedStrategyLib.FUSE_IDX_XXX
    uint[4] thresholds;
  }
  //endregion ------------------------------------------------ Data types

  //region ------------------------------------------------ Events
  event FuseStatusChanged(uint fuseStatus);
  event NewFuseThresholds(uint[4] newFuseThresholds);
  //endregion ------------------------------------------------ Events

  //region ------------------------------------------------ External withdraw functions

  /// @notice Get info for the swap that will be made on the next call of {withdrawStep}
  /// @param converterLiquidator_ [TetuConverter, TetuLiquidator]
  /// @param tokens Tokens used by depositor (length == 2: underlying and not-underlying)
  /// @param liquidationThresholds Liquidation thresholds for the {tokens}
  /// @param propNotUnderlying18 Required proportion of not-underlying for the final swap of leftovers, [0...1e18].
  ///                            The leftovers should be swapped to get following result proportions of the assets:
  ///                            not-underlying : underlying === propNotUnderlying18 : 1e18 - propNotUnderlying18
  ///                            Value type(uint).max means that the proportions should be read from the pool.
  /// @param amountsFromPool Amounts of {tokens} that will be received from the pool before calling withdraw
  /// @return tokenToSwap Address of the token that will be swapped on the next swap. 0 - no swap
  /// @return amountToSwap Amount that will be swapped on the next swap. 0 - no swap
  ///                      This amount is NOT reduced on {GAP_AMOUNT_TO_SWAP}, it should be reduced after the call if necessary.
  function quoteWithdrawStep(
    address[2] memory converterLiquidator_,
    address[] memory tokens,
    uint[] memory liquidationThresholds,
    uint[] memory amountsFromPool,
    uint planKind,
    uint propNotUnderlying18
  ) external returns (
    address tokenToSwap,
    uint amountToSwap
  ){
    (uint[] memory prices,
     uint[] memory decs
    ) = AppLib._getPricesAndDecs(AppLib._getPriceOracle(ITetuConverter(converterLiquidator_[0])), tokens, 2);
    IterationPlanLib.SwapRepayPlanParams memory p = IterationPlanLib.SwapRepayPlanParams({
      converter: ITetuConverter(converterLiquidator_[0]),
      liquidator: ITetuLiquidator(converterLiquidator_[1]),
      tokens: tokens,
      liquidationThresholds: liquidationThresholds,
      propNotUnderlying18: propNotUnderlying18 == type(uint).max
        ? IPoolProportionsProvider(address(this)).getPropNotUnderlying18()
        : propNotUnderlying18,
      prices: prices,
      decs: decs,
      balanceAdditions: amountsFromPool,
      planKind: planKind,
      usePoolProportions: propNotUnderlying18 == type(uint).max
    });
    return _quoteWithdrawStep(p);
  }

  /// @notice Make withdraw step with 0 or 1 swap only. The step can make one of the following actions:
  ///         1) repay direct debt 2) repay reverse debt 3) final swap leftovers of not-underlying asset
  /// @param converterLiquidator_ [TetuConverter, TetuLiquidator]
  /// @param tokens Tokens used by depositor (length == 2: underlying and not-underlying)
  /// @param liquidationThresholds Liquidation thresholds for the {tokens}
  /// @param tokenToSwap_ Address of the token that will be swapped on the next swap. 0 - no swap
  /// @param amountToSwap_ Amount that will be swapped on the next swap. 0 - no swap
  /// @param aggregator_ Aggregator that should be used for the next swap. 0 - no swap
  /// @param swapData_ Swap data to be passed to the aggregator on the next swap.
  ///                  Swap data contains swap-route, amount and all other required info for the swap.
  ///                  Swap data should be prepared on-chain on the base of data received by {quoteWithdrawStep}
  /// @param useLiquidator_ Use liquidator instead of aggregator.
  ///                       Aggregator swaps amount reduced on {GAP_AMOUNT_TO_SWAP}.
  ///                       Liquidator doesn't use {GAP_AMOUNT_TO_SWAP}.
  ///                       It's allowed to pass liquidator address in {aggregator_} and set {useLiquidator_} to false -
  ///                       the liquidator will be used in same way as aggregator in this case.
  /// @param planKind One of IterationPlanLib.PLAN_XXX
  /// @param propNotUnderlying18 Required proportion of not-underlying for the final swap of leftovers, [0...1e18].
  ///                           The leftovers should be swapped to get following result proportions of the assets:
  ///                           not-underlying : underlying === propNotUnderlying18 : 1e18 - propNotUnderlying18
  /// @return completed All debts were closed, leftovers were swapped to the required proportions
  function withdrawStep(
    address[2] memory converterLiquidator_,
    address[] memory tokens,
    uint[] memory liquidationThresholds,
    address tokenToSwap_,
    uint amountToSwap_,
    address aggregator_,
    bytes memory swapData_,
    bool useLiquidator_,
    uint planKind,
    uint propNotUnderlying18
  ) external returns (
    bool completed
  ){
    (uint[] memory prices,
     uint[] memory decs
    ) = AppLib._getPricesAndDecs(AppLib._getPriceOracle(ITetuConverter(converterLiquidator_[0])), tokens, 2);

    IterationPlanLib.SwapRepayPlanParams memory p = IterationPlanLib.SwapRepayPlanParams({
      converter: ITetuConverter(converterLiquidator_[0]),
      liquidator: ITetuLiquidator(converterLiquidator_[1]),
      tokens: tokens,
      liquidationThresholds: liquidationThresholds,
      propNotUnderlying18: propNotUnderlying18 == type(uint).max
        ? IPoolProportionsProvider(address(this)).getPropNotUnderlying18()
        : propNotUnderlying18,
      prices: prices,
      decs: decs,
      balanceAdditions: new uint[](2), // 2 = tokens.length
      planKind: planKind,
      usePoolProportions: propNotUnderlying18 == type(uint).max
    });
    SwapByAggParams memory aggParams = SwapByAggParams({
      tokenToSwap: tokenToSwap_,
      amountToSwap: amountToSwap_,
      useLiquidator: useLiquidator_,
      aggregator: aggregator_,
      swapData: swapData_
    });
    return _withdrawStep(p, aggParams);
  }
  //endregion ------------------------------------------------ External withdraw functions

  //region ------------------------------------------------ Fuse functions
  function setFuseStatus(FuseStateParams storage fuse, FuseStatus status) external {
    fuse.status = status;
    emit FuseStatusChanged(uint(status));
  }

  function setFuseThresholds(FuseStateParams storage state, uint[4] memory values) external {
    require(
      (values[FUSE_IDX_LOWER_LIMIT_ON] == 0 && values[FUSE_IDX_LOWER_LIMIT_OFF] == 0)
      || (values[FUSE_IDX_LOWER_LIMIT_ON] <= values[FUSE_IDX_LOWER_LIMIT_OFF]),
      AppErrors.INVALID_VALUE
    );
    require(
      (values[FUSE_IDX_UPPER_LIMIT_ON] == 0 && values[FUSE_IDX_UPPER_LIMIT_OFF] == 0)
      || (values[FUSE_IDX_UPPER_LIMIT_ON] >= values[FUSE_IDX_UPPER_LIMIT_OFF]),
      AppErrors.INVALID_VALUE
    );
    if (values[FUSE_IDX_LOWER_LIMIT_ON] != 0 && values[FUSE_IDX_UPPER_LIMIT_ON] != 0) {
      require(
        values[FUSE_IDX_UPPER_LIMIT_ON] > values[FUSE_IDX_LOWER_LIMIT_ON],
        AppErrors.INVALID_VALUE
      );
    }
    state.thresholds = values;
    emit NewFuseThresholds(values);
  }

  function isFuseTriggeredOn(PairBasedStrategyLib.FuseStatus fuseStatus) internal pure returns (bool) {
    return uint(fuseStatus) > uint(PairBasedStrategyLib.FuseStatus.FUSE_OFF_1);
  }

  /// @notice Check if the fuse should be turned ON/OFF
  /// @param price Current price
  /// @return needToChange A boolean indicating if the fuse status should be changed
  /// @return status Exist fuse status or new fuse status (if needToChange is true)
  function needChangeFuseStatus(FuseStateParams memory fuse, uint price) internal pure returns (
    bool needToChange,
    FuseStatus status
  ) {
    if (fuse.status != FuseStatus.FUSE_DISABLED_0) {
      if (fuse.status == FuseStatus.FUSE_OFF_1) {
        // currently fuse is OFF
        if (price <= fuse.thresholds[FUSE_IDX_LOWER_LIMIT_ON]) {
          needToChange = true;
          status = FuseStatus.FUSE_ON_LOWER_LIMIT_2;
        } else if (price >= fuse.thresholds[FUSE_IDX_UPPER_LIMIT_ON]) {
          needToChange = true;
          status = FuseStatus.FUSE_ON_UPPER_LIMIT_3;
        }
      } else {
        if (fuse.status == FuseStatus.FUSE_ON_LOWER_LIMIT_2) {
          // currently fuse is triggered ON by lower limit
          if (price >= fuse.thresholds[FUSE_IDX_LOWER_LIMIT_OFF]) {
            needToChange = true;
            if (price >= fuse.thresholds[FUSE_IDX_UPPER_LIMIT_ON]) {
              status = FuseStatus.FUSE_ON_UPPER_LIMIT_3;
            } else {
              status = FuseStatus.FUSE_OFF_1;
            }
          }
        } else {
          // currently fuse is triggered ON by upper limit
          if (price <= fuse.thresholds[FUSE_IDX_UPPER_LIMIT_OFF]) {
            needToChange = true;
            if (price <= fuse.thresholds[FUSE_IDX_LOWER_LIMIT_OFF]) {
              status = FuseStatus.FUSE_ON_LOWER_LIMIT_2;
            } else {
              status = FuseStatus.FUSE_OFF_1;
            }
          }
        }
      }
    }

    return (needToChange, needToChange ? status : fuse.status);
  }
  //endregion ------------------------------------------------ Fuse functions

  //region ------------------------------------------------ Internal helper functions
  /// @notice Quote amount of the next swap if any.
  ///         Swaps are required if direct-borrow exists OR reverse-borrow exists or not underlying leftovers exist
  ///         Function returns info for first swap only.
  /// @return tokenToSwap What token should be swapped. Zero address if no swap is required
  /// @return amountToSwap Amount to swap. Zero if no swap is required.
  function _quoteWithdrawStep(IterationPlanLib.SwapRepayPlanParams memory p) internal returns (
    address tokenToSwap,
    uint amountToSwap
  ) {
    uint indexTokenToSwapPlus1;
    (indexTokenToSwapPlus1, amountToSwap,) = IterationPlanLib.buildIterationPlan(
      [address(p.converter), address(p.liquidator)],
      p.tokens,
      p.liquidationThresholds,
      p.prices,
      p.decs,
      p.balanceAdditions,
      [
        p.usePoolProportions ? 1 : 0,
        p.planKind,
        p.propNotUnderlying18,
        type(uint).max,
        IDX_ASSET,
        IDX_TOKEN
      ]
    );
    if (indexTokenToSwapPlus1 != 0) {
      tokenToSwap = p.tokens[indexTokenToSwapPlus1 - 1];
    }
    return (tokenToSwap, amountToSwap);
  }

  /// @notice Make one iteration of withdraw. Each iteration can make 0 or 1 swap only
  ///         We can make only 1 of the following 3 operations per single call:
  ///         1) repay direct debt 2) repay reverse debt 3) swap leftovers to underlying
  function _withdrawStep(IterationPlanLib.SwapRepayPlanParams memory p, SwapByAggParams memory aggParams) internal returns (
    bool completed
  ) {
    (uint idxToSwap1, uint amountToSwap, uint idxToRepay1) = IterationPlanLib.buildIterationPlan(
      [address(p.converter), address(p.liquidator)],
      p.tokens,
      p.liquidationThresholds,
      p.prices,
      p.decs,
      p.balanceAdditions,
      [
        p.usePoolProportions ? 1 : 0,
        p.planKind,
        p.propNotUnderlying18,
        type(uint).max,
        IDX_ASSET,
        IDX_TOKEN
      ]
    );

    bool[4] memory actions = [
      p.planKind == IterationPlanLib.PLAN_SWAP_ONLY || p.planKind == IterationPlanLib.PLAN_SWAP_REPAY, // swap 1
      p.planKind == IterationPlanLib.PLAN_REPAY_SWAP_REPAY || p.planKind == IterationPlanLib.PLAN_SWAP_REPAY, // repay 1
      p.planKind == IterationPlanLib.PLAN_REPAY_SWAP_REPAY, // swap 2
      p.planKind == IterationPlanLib.PLAN_REPAY_SWAP_REPAY // repay 2
    ];

    if (idxToSwap1 != 0 && actions[IDX_SWAP_1]) {
      (, p.propNotUnderlying18) = _swap(p, aggParams, idxToSwap1 - 1, idxToSwap1 - 1 == IDX_ASSET ? IDX_TOKEN : IDX_ASSET, amountToSwap);
    }

    if (idxToRepay1 != 0 && actions[IDX_REPAY_1]) {
      ConverterStrategyBaseLib._repayDebt(
        p.converter,
        p.tokens[idxToRepay1 - 1 == IDX_ASSET ? IDX_TOKEN : IDX_ASSET],
        p.tokens[idxToRepay1 - 1],
        IERC20(p.tokens[idxToRepay1 - 1]).balanceOf(address(this))
      );
    }

    if (idxToSwap1 != 0) {
      if (actions[IDX_SWAP_2]) {
        (, p.propNotUnderlying18) = _swap(p, aggParams, idxToSwap1 - 1, idxToSwap1 - 1 == IDX_ASSET ? IDX_TOKEN : IDX_ASSET, amountToSwap);

        if (actions[IDX_REPAY_2] && idxToRepay1 != 0) {
          // see calculations inside estimateSwapAmountForRepaySwapRepay
          // There are two possibilities here:
          // 1) All collateral asset available on balance was swapped. We need additional repay to get assets in right proportions
          // 2) Only part of collateral asset was swapped, so assets are already in right proportions. Repay 2 is not needed
          (uint amountToRepay2, bool borrowInsteadRepay) = _getAmountToRepay2(
            p,
            idxToRepay1 - 1 == IDX_ASSET ? IDX_TOKEN : IDX_ASSET,
            idxToRepay1 - 1
          );

          if (borrowInsteadRepay) {
            borrowToProportions(p, idxToRepay1 - 1, idxToRepay1 - 1 == IDX_ASSET ? IDX_TOKEN : IDX_ASSET);
          } else if (amountToRepay2 > p.liquidationThresholds[idxToRepay1 - 1]) {
            // we need to know repaidAmount
            // we cannot relay on the value returned by _repayDebt because of SCB-710, we need to check balances
            // temporary save current balance to repaidAmount
            uint repaidAmount = IERC20(p.tokens[idxToRepay1 - 1]).balanceOf(address(this));

            ConverterStrategyBaseLib._repayDebt(
              p.converter,
              p.tokens[idxToRepay1 - 1 == IDX_ASSET ? IDX_TOKEN : IDX_ASSET],
              p.tokens[idxToRepay1 - 1],
              amountToRepay2
            );
            uint balanceAfter = IERC20(p.tokens[idxToRepay1 - 1]).balanceOf(address(this));
            repaidAmount = repaidAmount > balanceAfter
              ? repaidAmount - balanceAfter
              : 0;

            if (repaidAmount < amountToRepay2 && amountToRepay2 - repaidAmount > p.liquidationThresholds[idxToRepay1 - 1]) {
              borrowToProportions(p, idxToRepay1 - 1, idxToRepay1 - 1 == IDX_ASSET ? IDX_TOKEN : IDX_ASSET);
            }
          }
        }
      } else {
        // leftovers were swapped, there are no debts anymore
        // the swap can change pool proportions, so probably it's necessary to make additional borrow here
        if (
          idxToRepay1 == 0 // there are no debts anymore
          && p.usePoolProportions  // we use proportions from the pool
          && p.propNotUnderlying18 != 0 && p.propNotUnderlying18 != 1e18 // BorrowLib doesn't allow prop=0
        ) {
          _fixLeftoversProportions(p);
        }
      }
    }

    // Withdraw is completed on last iteration (no debts, swapping leftovers)
    return idxToRepay1 == 0;
  }

  /// @notice Set balances to right proportions using borrow
  ///         (it can be necessary if propNotUnderlying18 was changed after swap)
  function _fixLeftoversProportions(IterationPlanLib.SwapRepayPlanParams memory p) internal {
    uint balanceAsset = IERC20(p.tokens[IDX_ASSET]).balanceOf(address(this));
    uint balanceToken = IERC20(p.tokens[IDX_TOKEN]).balanceOf(address(this));
    (uint targetAssets,
      uint targetTokens
    ) = IterationPlanLib._getTargetAmounts(p.prices, p.decs, balanceAsset, balanceToken, p.propNotUnderlying18, IDX_ASSET, IDX_TOKEN);

    if (balanceAsset > targetAssets) {
      if (balanceAsset - targetAssets > p.liquidationThresholds[IDX_ASSET]) {
        _borrowToProportions(p, IDX_ASSET, IDX_TOKEN, balanceAsset, balanceToken);
      }
    } else if (balanceToken > targetTokens) {
      if (balanceToken - targetTokens > p.liquidationThresholds[IDX_ASSET]) {
        _borrowToProportions(p, IDX_TOKEN, IDX_ASSET, balanceToken, balanceAsset);
      }
    }
  }

  /// @notice borrow borrow-asset under collateral-asset, result balances should match to propNotUnderlying18
  function borrowToProportions(
    IterationPlanLib.SwapRepayPlanParams memory p,
    uint indexCollateral,
    uint indexBorrow
  ) internal {
    _borrowToProportions(
      p,
      indexCollateral,
      indexBorrow,
      IERC20(p.tokens[indexCollateral]).balanceOf(address(this)),
      IERC20(p.tokens[indexBorrow]).balanceOf(address(this))
    );
  }

  /// @notice borrow borrow-asset under collateral-asset, result balances should match to propNotUnderlying18
  function _borrowToProportions(
    IterationPlanLib.SwapRepayPlanParams memory p,
    uint indexCollateral,
    uint indexBorrow,
    uint balanceCollateral,
    uint balanceBorrow
  ) internal {
    BorrowLib.RebalanceAssetsCore memory cac = BorrowLib.RebalanceAssetsCore({
      converterLiquidator: BorrowLib.ConverterLiquidator(p.converter, p.liquidator),
      assetA: p.tokens[indexCollateral],
      assetB: p.tokens[indexBorrow],
      propA: indexCollateral == IDX_ASSET ? 1e18 - p.propNotUnderlying18 : p.propNotUnderlying18,
      propB: indexCollateral == IDX_ASSET ? p.propNotUnderlying18 : 1e18 - p.propNotUnderlying18,
      // {assetA} to {assetB} ratio; {amountB} * {alpha} => {amountA}, decimals 18
      alpha18: 1e18 * p.prices[indexBorrow] * p.decs[indexCollateral] / p.prices[indexCollateral] / p.decs[indexBorrow],
      thresholdA: p.liquidationThresholds[indexCollateral],
      addonA: 0,
      addonB: 0,
      indexA: indexCollateral,
      indexB: indexBorrow
    });

    // we are going to change direction of the borrow
    // let's ensure that there is no debt in opposite direction
    (uint needToRepay,) = p.converter.getDebtAmountStored(address(this), p.tokens[indexBorrow],  p.tokens[indexCollateral], false);
    require(needToRepay < AppLib.DUST_AMOUNT_TOKENS, AppErrors.OPPOSITE_DEBT_EXISTS);

    BorrowLib.openPosition(
      cac,
      BorrowLib.PricesDecs({
        prices: p.prices,
        decs: p.decs
      }),
      balanceCollateral,
      balanceBorrow
    );
  }

  /// @notice Calculate amount that should be repaid to get right proportions of assets on balance
  ///         Analyse only single borrow-direction: indexCollateral => indexBorrow
  /// @return amountToRepay Amount that should be repaid
  /// @return borrowInsteadRepay true if repay is not necessary at all and borrow is required instead
  ///                            if we need both repay and borrow then false is returned
  function _getAmountToRepay2(
    IterationPlanLib.SwapRepayPlanParams memory p,
    uint indexCollateral,
    uint indexBorrow
  ) internal view returns (
    uint amountToRepay,
    bool borrowInsteadRepay
  ) {
    GetAmountToRepay2Local memory v;
    v.c0 = IERC20(p.tokens[indexCollateral]).balanceOf(address(this)) * p.prices[indexCollateral] / p.decs[indexCollateral];
    v.b0 = IERC20(p.tokens[indexBorrow]).balanceOf(address(this)) * p.prices[indexBorrow] / p.decs[indexBorrow];

    v.x = indexCollateral == IDX_ASSET ? 1e18 - p.propNotUnderlying18 : p.propNotUnderlying18;
    v.y = indexCollateral == IDX_ASSET ? p.propNotUnderlying18 : 1e18 - p.propNotUnderlying18;
    v.alpha = p.prices[indexCollateral] * p.decs[indexBorrow] * 1e18 / p.prices[indexBorrow] / p.decs[indexCollateral];

    (uint needToRepay, uint collateralAmountOut) = p.converter.getDebtAmountStored(
      address(this),
      p.tokens[indexCollateral],
      p.tokens[indexBorrow],
      true
    );

    if (needToRepay == 0) {
      // check if we need to make reverse borrow to fit to proportions: borrow collateral-asset under borrow-asset
      uint targetCollateral = (v.c0 + v.b0) * v.x / (v.x + v.y);
      borrowInsteadRepay = targetCollateral > v.c0
        && targetCollateral - v.c0
        > (p.liquidationThresholds[indexCollateral] * p.prices[indexCollateral] / p.decs[indexCollateral]);
    } else {
      // initial balances: c0, b0
      // we are going to repay amount b and receive (betta * b, b), where betta ~ alpha * totalCollateral / totalBorrow
      // we should have x/y = (c0 + betta * b) / (b0 - b)
      // so b = (x * b0 - y * c0) / (betta * y + x)
      v.b = (int(v.x * v.b0) - int(v.y * v.c0)) / (int(v.y * v.alpha * collateralAmountOut / needToRepay / 1e18) + int(v.x));
      if (v.b > 0) {
        amountToRepay = uint(v.b);
      }
    }

    return (amountToRepay * p.decs[indexBorrow] / p.prices[indexBorrow], borrowInsteadRepay);
  }

  /// @notice Swap {aggParams.amountToSwap} using either liquidator or aggregator
  /// @dev You can use liquidator as aggregator, so aggregator's logic will be used for the liquidator
  /// @param amountIn Calculated amount to be swapped. It can be different from {aggParams.amountToSwap} a bit,
  ///                 but aggregators require exact value {aggParams.amountToSwap}, so amountIn is not used with agg.
  function _swap(
    IterationPlanLib.SwapRepayPlanParams memory p,
    SwapByAggParams memory aggParams,
    uint indexIn,
    uint indexOut,
    uint amountIn
  ) internal returns (
    uint spentAmountIn,
    uint updatedPropNotUnderlying18
  ) {
    // liquidator and aggregator have different logic here:
    // - liquidator uses amountIn to swap
    // - Aggregator uses amountToSwap for which a route was built off-chain before the call of the swap()
    // It's allowed to use aggregator == liquidator, so in this way liquidator will use aggregator's logic (for tests)

    if (!aggParams.useLiquidator) {
      // aggregator requires exact input amount - aggParams.amountToSwap
      // actual amount can be a bit different because the quote function was called in different block
      amountIn = aggParams.amountToSwap;
    }
    address aggregator = aggParams.useLiquidator
      ? address(p.liquidator)
      : aggParams.aggregator;

    require(amountIn <= IERC20(p.tokens[indexIn]).balanceOf(address(this)), AppErrors.NOT_ENOUGH_BALANCE);
    // let's ensure that "next swap" is made using correct token
    require(aggParams.tokenToSwap == p.tokens[indexIn], AppErrors.INCORRECT_SWAP_BY_AGG_PARAM);

    if (amountIn > AppLib._getLiquidationThreshold(p.liquidationThresholds[indexIn])) {
      AppLib.approveIfNeeded(p.tokens[indexIn], amountIn, aggregator);

      uint balanceTokenOutBefore = AppLib.balance(p.tokens[indexOut]);

      if (aggParams.useLiquidator) {
        (spentAmountIn,) = ConverterStrategyBaseLib._liquidate(
          p.converter,
          ITetuLiquidator(aggregator),
          p.tokens[indexIn],
          p.tokens[indexOut],
          amountIn,
          _ASSET_LIQUIDATION_SLIPPAGE,
          p.liquidationThresholds[indexIn],
          true
        );
      } else {
        if (aggregator != address(p.liquidator)) {
          _checkSwapRouter(aggregator);
        }

        (bool success, bytes memory result) = aggregator.call(aggParams.swapData);
        require(success, string(result));

        spentAmountIn = amountIn;
      }

      require(
        p.converter.isConversionValid(
          p.tokens[indexIn],
          amountIn,
          p.tokens[indexOut],
          AppLib.balance(p.tokens[indexOut]) - balanceTokenOutBefore,
          _ASSET_LIQUIDATION_SLIPPAGE
        ), AppErrors.PRICE_IMPACT);
    }

    return (
      spentAmountIn,
    // p.propNotUnderlying18 contains original proportions that were valid before the swap
    // after swap() we need to re-read new values from the pool
      p.usePoolProportions
        ? IPoolProportionsProvider(address(this)).getPropNotUnderlying18()
      : p.propNotUnderlying18
    );
  }
  //endregion ------------------------------------------------ Internal helper functions

  //region ----------------------------------------- Utils
  function _checkSwapRouter(address router) internal pure {
    require(router == ONEINCH || router == OPENOCEAN, UNKNOWN_SWAP_ROUTER);
  }

  /// @notice Extract propNotUnderlying18 from {planEntryData} of the given {planKind}
  function _extractProp(uint planKind, bytes memory planEntryData) internal pure returns(uint propNotUnderlying18) {
    require(planKind == IterationPlanLib.PLAN_SWAP_REPAY
    || planKind == IterationPlanLib.PLAN_REPAY_SWAP_REPAY
    || planKind == IterationPlanLib.PLAN_SWAP_ONLY,
      AppErrors.WRONG_VALUE
    );
    (, propNotUnderlying18) = abi.decode(planEntryData, (uint, uint));
    require(propNotUnderlying18 <= 1e18 || propNotUnderlying18 == type(uint).max, AppErrors.INVALID_VALUE); // 0 is allowed
  }
  //endregion ------------------------------------------ Utils
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@tetu_io/tetu-converter/contracts/interfaces/ITetuConverter.sol";
import "../ConverterStrategyBaseLib.sol";
import "./PairBasedStrategyLib.sol";
import "../ConverterStrategyBaseLib2.sol";

/// @notice Library for the UniV3-like strategies with two tokens in the pool
library PairBasedStrategyLogicLib {
  //region ------------------------------------------------------- Data types
  /// @notice Local variables required inside withdrawByAggStep and quoteWithdrawByAgg
  struct WithdrawLocal {
    /// [underlying, not-underlying]
    address[] tokens;
    address controller;
    /// liquidationThresholds for the {tokens}
    uint[] liquidationThresholds;
    uint planKind;
    uint propNotUnderlying18;
  }

  /// @notice Common part of all XXXXConverterStrategyLogicLib.State
  struct PairState {
    address pool;
    address strategyProfitHolder;
    /// @notice This is underlying
    address tokenA;
    /// @notice This is not underlying
    address tokenB;

    bool isStablePool;
    /// @notice Tokens are swapped in the pool (pool.tokenB is underlying, pool.tokenA is not-underlying)
    bool depositorSwapTokens;

    int24 tickSpacing;
    int24 lowerTick;
    int24 upperTick;
    int24 rebalanceTickRange;
    uint128 totalLiquidity;

    /// @notice Fuse for token A and token B
    PairBasedStrategyLib.FuseStateParams[2] fuseAB;
    /// @notice 1 means that the fuse was triggered ON and then all debts were closed
    ///         and assets were converter to underlying using withdrawStepByAgg.
    ///         This flag is automatically cleared to 0 if fuse is triggered OFF.
    uint withdrawDone;

    /// @notice Timestamp of last call of rebalanceNoSwaps() or zero if withdrawByAggStep() was called last
    uint lastRebalanceNoSwap;
  }

  struct RebalanceNoSwapsLocal {
    address tokenA;
    address tokenB;
    bool depositorSwapTokens;
    int24 newLowerTick;
    int24 newUpperTick;
    uint prop0;
    uint prop1;
  }

  struct WithdrawByAggStepLocal {
    PairBasedStrategyLogicLib.WithdrawLocal w;
    address tokenToSwap;
    address aggregator;
    address controller;
    address converter;
    address splitter;
    uint amountToSwap;
    uint profitToCover;
    uint oldTotalAssets;
    uint entryToPool;
  }
  //endregion ------------------------------------------------------- Data types

  //region ------------------------------------------------------- Helpers
  /// @notice Prepare array of amounts ready to deposit, borrow missed amounts
  function _beforeDeposit(
    ITetuConverter tetuConverter_,
    uint amount_,
    address tokenA,
    address tokenB,
    bytes memory entryData,
    mapping(address => uint) storage liquidationThresholds
  ) external returns (
    uint[] memory tokenAmounts
  ) {
    tokenAmounts = new uint[](2);
    uint spentCollateral;

    AppLib.approveIfNeeded(tokenA, amount_, address(tetuConverter_));
    (spentCollateral, tokenAmounts[1]) = ConverterStrategyBaseLib.openPosition(
      tetuConverter_,
      entryData,
      tokenA,
      tokenB,
      amount_,
      liquidationThresholds[tokenA] // amount_ is set in terms of collateral asset
    );

    tokenAmounts[0] = amount_ > spentCollateral
      ? amount_ - spentCollateral
      : 0;
  }

  /// @notice Initialize {dest} in place. Underlying is always first in {dest.tokens}.
  /// @param tokens_ [underlying, not-underlying]
  function initWithdrawLocal(
    WithdrawLocal memory dest,
    address[2] memory tokens_,
    mapping(address => uint) storage liquidationThresholds,
    bytes memory planEntryData,
    address controller
  ) internal view { // it's internal because it initializes {dest}
    dest.controller = controller;
    StrategyLib2.onlyOperators(dest.controller);

    dest.planKind = IterationPlanLib.getEntryKind(planEntryData);
    dest.propNotUnderlying18 = PairBasedStrategyLib._extractProp(dest.planKind, planEntryData);

    dest.tokens = new address[](2);
    (dest.tokens[0], dest.tokens[1]) = (tokens_[0], tokens_[1]);

    dest.liquidationThresholds = new uint[](2);
    dest.liquidationThresholds[0] = liquidationThresholds[dest.tokens[0]];
    dest.liquidationThresholds[1] = liquidationThresholds[dest.tokens[1]];
  }

  function calcTickRange(int24 tick, int24 tickRange, int24 tickSpacing) public pure returns (
    int24 lowerTick,
    int24 upperTick
  ) {
    if (tick < 0 && tick / tickSpacing * tickSpacing != tick) {
      lowerTick = ((tick - tickRange) / tickSpacing - 1) * tickSpacing;
    } else {
      lowerTick = (tick - tickRange) / tickSpacing * tickSpacing;
    }
    upperTick = tickRange == 0 ? lowerTick + tickSpacing : lowerTick + tickRange * 2;
  }
  //endregion ------------------------------------------------------- Helpers

  //region ------------------------------------------------------- PairState-helpers
  /// @notice Set the initial values to PairState instance
  /// @param pairState Depositor storage state struct to be initialized
  /// @param addr [pool, asset, pool.token0(), pool.token1()]
  ///        asset: Underlying asset of the depositor.
  /// @param tickData [tickSpacing, lowerTick, upperTick, rebalanceTickRange]
  /// @param fuseThresholdsA Fuse thresholds for token A (stable pool only)
  /// @param fuseThresholdsB Fuse thresholds for token B (stable pool only)
  function setInitialDepositorValues(
    PairState storage pairState,
    address[4] calldata addr,
    int24[4] calldata tickData,
    bool isStablePool_,
    uint[4] calldata fuseThresholdsA,
    uint[4] calldata fuseThresholdsB
  ) external {
    pairState.pool = addr[0];
    address asset = addr[1];
    address token0 = addr[2];
    address token1 = addr[3];

    pairState.tickSpacing = tickData[0];
    pairState.lowerTick = tickData[1];
    pairState.upperTick = tickData[2];
    pairState.rebalanceTickRange = tickData[3];

    require(asset == token0 || asset == token1, PairBasedStrategyLib.INCORRECT_ASSET);
    if (asset == token0) {
      pairState.tokenA = token0;
      pairState.tokenB = token1;
      pairState.depositorSwapTokens = false;
    } else {
      pairState.tokenA = token1;
      pairState.tokenB = token0;
      pairState.depositorSwapTokens = true;
    }

    if (isStablePool_) {
      /// for stable pools fuse can be enabled
      pairState.isStablePool = true;
      PairBasedStrategyLib.setFuseStatus(pairState.fuseAB[0], PairBasedStrategyLib.FuseStatus.FUSE_OFF_1);
      PairBasedStrategyLib.setFuseThresholds(pairState.fuseAB[0], fuseThresholdsA);
      PairBasedStrategyLib.setFuseStatus(pairState.fuseAB[1], PairBasedStrategyLib.FuseStatus.FUSE_OFF_1);
      PairBasedStrategyLib.setFuseThresholds(pairState.fuseAB[1], fuseThresholdsB);
    }

    // totalLiquidity is 0, no need to initialize
    // withdrawDone is 0, no need to initialize
  }

  function updateFuseStatus(
    PairBasedStrategyLogicLib.PairState storage pairState,
    bool[2] calldata fuseStatusChangedAB,
    PairBasedStrategyLib.FuseStatus[2] calldata fuseStatusAB
  ) external {
    bool updated;
    for (uint i = 0; i < 2; i = AppLib.uncheckedInc(i)) {
      if (fuseStatusChangedAB[i]) {
        PairBasedStrategyLib.setFuseStatus(pairState.fuseAB[i], fuseStatusAB[i]);
        updated = true;
      }
    }

    if (updated) {
      // if fuse is triggered ON, full-withdraw is required
      // if fuse is triggered OFF, the assets will be deposited back to pool
      // in both cases withdrawDone should be reset
      pairState.withdrawDone = 0;
    }
  }

  /// @notice Returns the current state of the contract
  /// @return addr [tokenA, tokenB, pool, profitHolder]
  /// @return tickData [tickSpacing, lowerTick, upperTick, rebalanceTickRange]
  /// @return nums [totalLiquidity, fuse-status-tokenA, fuse-status-tokenB, withdrawDone, 4 thresholds of token A, 4 thresholds of token B, lastRebalanceNoSwap]
  /// @return boolValues [isStablePool, depositorSwapTokens]
  function getDefaultState(PairBasedStrategyLogicLib.PairState storage pairState) external view returns (
    address[] memory addr,
    int24[] memory tickData,
    uint[] memory nums,
    bool[] memory boolValues
  ) {
    addr = new address[](4);
    tickData = new int24[](4);
    nums = new uint[](13);
    boolValues = new bool[](2);

    addr[PairBasedStrategyLib.IDX_ADDR_DEFAULT_STATE_TOKEN_A] = pairState.tokenA;
    addr[PairBasedStrategyLib.IDX_ADDR_DEFAULT_STATE_TOKEN_B] = pairState.tokenB;
    addr[PairBasedStrategyLib.IDX_ADDR_DEFAULT_STATE_POOL] = pairState.pool;
    addr[PairBasedStrategyLib.IDX_ADDR_DEFAULT_STATE_PROFIT_HOLDER] = pairState.strategyProfitHolder;

    tickData[PairBasedStrategyLib.IDX_TICK_DEFAULT_STATE_TICK_SPACING] = pairState.tickSpacing;
    tickData[PairBasedStrategyLib.IDX_TICK_DEFAULT_STATE_LOWER_TICK] = pairState.lowerTick;
    tickData[PairBasedStrategyLib.IDX_TICK_DEFAULT_STATE_UPPER_TICK] = pairState.upperTick;
    tickData[PairBasedStrategyLib.IDX_TICK_DEFAULT_STATE_REBALANCE_TICK_RANGE] = pairState.rebalanceTickRange;

    nums[PairBasedStrategyLib.IDX_NUMS_DEFAULT_STATE_TOTAL_LIQUIDITY] = uint(pairState.totalLiquidity);
    nums[PairBasedStrategyLib.IDX_NUMS_DEFAULT_STATE_FUSE_STATUS_A] = uint(pairState.fuseAB[0].status);
    nums[PairBasedStrategyLib.IDX_NUMS_DEFAULT_STATE_FUSE_STATUS_B] = uint(pairState.fuseAB[1].status);
    nums[PairBasedStrategyLib.IDX_NUMS_DEFAULT_STATE_WITHDRAW_DONE] = pairState.withdrawDone;
    for (uint i = 0; i < 4; ++i) {
      nums[PairBasedStrategyLib.IDX_NUMS_DEFAULT_STATE_THRESHOLD_A_0 + i] = pairState.fuseAB[0].thresholds[i];
      nums[PairBasedStrategyLib.IDX_NUMS_DEFAULT_STATE_THRESHOLD_B_0 + i] = pairState.fuseAB[1].thresholds[i];
    }
    nums[PairBasedStrategyLib.IDX_NUMS_DEFAULT_STATE_LAST_REBALANCE_NO_SWAP] = pairState.lastRebalanceNoSwap;

    boolValues[PairBasedStrategyLib.IDX_BOOL_VALUES_DEFAULT_STATE_IS_STABLE_POOL] = pairState.isStablePool;
    boolValues[PairBasedStrategyLib.IDX_BOOL_VALUES_DEFAULT_STATE_DEPOSITOR_SWAP_TOKENS] = pairState.depositorSwapTokens;
  }

  /// @notice Get info about a swap required by next call of {withdrawByAggStep} within the given plan
  /// @param amounts_ Amounts of [underlying, not-underlying] that will be received from the pool before withdrawing
  function quoteWithdrawByAgg(
    PairBasedStrategyLogicLib.PairState storage pairState,
    bytes memory planEntryData,
    uint[] memory amounts_,
    address controller_,
    ITetuConverter converter_,
    mapping(address => uint) storage liquidationThresholds
  ) external returns (
    address tokenToSwap,
    uint amountToSwap
  ) {
    // check operator-only, initialize w
    WithdrawLocal memory w;
    initWithdrawLocal(
      w,
      [pairState.tokenA, pairState.tokenB],
      liquidationThresholds,
      planEntryData,
      controller_
    );

    (tokenToSwap, amountToSwap) = PairBasedStrategyLib.quoteWithdrawStep(
      [address(converter_), address(AppLib._getLiquidator(w.controller))],
      w.tokens,
      w.liquidationThresholds,
      amounts_,
      w.planKind,
      w.propNotUnderlying18
    );

    if (amountToSwap != 0) {
      // withdrawByAggStep will execute REPAY1 - SWAP - REPAY2
      // but quoteWithdrawByAgg and withdrawByAggStep are executed in different blocks
      // so, REPAY1 can return less collateral than quoteWithdrawByAgg expected
      // As result, we can have less amount on balance than required amountToSwap
      // So, we need to reduce amountToSwap on small gap amount
      amountToSwap -= amountToSwap * PairBasedStrategyLib.GAP_AMOUNT_TO_SWAP / 100_000;
    }
  }

  /// @notice Calculate amounts to be deposited to pool, calculate loss, fix profitToCover
  /// @param addr_ [tokenToSwap, aggregator, controller, converter, splitter]
  /// @param values_ [amountToSwap_, profitToCover, oldTotalAssets, not used here]
  /// @param tokens [underlying, not-underlying] (values been read from pairBase)
  /// @return completed All debts were closed, leftovers were swapped to proper proportions
  /// @return tokenAmounts Amounts to be deposited to pool. If {tokenAmounts} contains zero amount return empty array.
  /// @return loss Loss to cover
  function withdrawByAggStep(
    address[5] calldata addr_,
    uint[4] calldata values_,
    bytes memory swapData,
    bytes memory planEntryData,
    address[2] memory tokens,
    mapping(address => uint) storage liquidationThresholds
  ) external returns (
    bool completed,
    uint[] memory tokenAmounts,
    uint loss
  ) {
    WithdrawByAggStepLocal memory v;

    v.tokenToSwap = addr_[0];
    v.aggregator = addr_[1];
    v.controller = addr_[2];
    v.converter = addr_[3];
    v.splitter = addr_[4];

    v.amountToSwap = values_[0];
    v.profitToCover = values_[1];
    v.oldTotalAssets = values_[2];

    // initialize v
    PairBasedStrategyLogicLib.initWithdrawLocal(v.w, tokens, liquidationThresholds, planEntryData, v.controller);

    // make withdraw iteration according to the selected plan
    completed = PairBasedStrategyLib.withdrawStep(
      [v.converter, address(AppLib._getLiquidator(v.w.controller))],
      v.w.tokens,
      v.w.liquidationThresholds,
      v.tokenToSwap,
      v.amountToSwap,
      v.aggregator,
      swapData,
      v.aggregator == address(0),
      v.w.planKind,
      v.w.propNotUnderlying18
    );

    // fix loss / profitToCover
    if (v.profitToCover > 0) {
      uint profitToSend = Math.min(v.profitToCover, IERC20(v.w.tokens[0]).balanceOf(address(this)));
      ConverterStrategyBaseLib2.sendToInsurance(v.w.tokens[0], profitToSend, v.splitter, v.oldTotalAssets);
    }

    (loss, tokenAmounts) = ConverterStrategyBaseLib2.getTokenAmountsPair(
      ITetuConverter(v.converter),
      v.oldTotalAssets,
      v.w.tokens[0],
      v.w.tokens[1],
      [v.w.liquidationThresholds[0], v.w.liquidationThresholds[1]]
    );
  }

  /// @notice Rebalance asset to proportions {propTokenA}:{1e18-propTokenA}, fix profitToCover
  /// @param propTokenA Proportion of {tokenA}, > 0. Proportion of {tokenB} is calculates as 1e18 - prop0
  /// @param liquidationThresholdsAB [liquidityThreshold of token A, liquidityThreshold of tokenB]
  function rebalanceNoSwaps(
    address[2] calldata converterLiquidator,
    PairBasedStrategyLogicLib.PairState storage pairState,
    uint profitToCover,
    uint totalAssets,
    address splitter,
    uint[2] calldata liquidationThresholdsAB,
    uint propTokenA
  ) internal {
    address tokenA = pairState.tokenA;
    address tokenB = pairState.tokenB;

    BorrowLib.rebalanceAssets(
      ITetuConverter(converterLiquidator[0]),
      ITetuLiquidator(converterLiquidator[1]),
      tokenA,
      tokenB,
      propTokenA,
      liquidationThresholdsAB[0], // liquidityThreshold of token A
      liquidationThresholdsAB[1], // liquidityThreshold of token B
      profitToCover
    );

    // we assume here, that rebalanceAssets provides profitToCover on balance and set leftovers to right proportions
    if (profitToCover != 0) {
      uint profitToSend = Math.min(profitToCover, IERC20(tokenA).balanceOf(address(this)));
      ConverterStrategyBaseLib2.sendToInsurance(tokenA, profitToSend, splitter, totalAssets);
    }
  }
  //endregion ------------------------------------------------------- PairState-helpers

  //region ------------------------------------------------------- needStrategyRebalance
  /// @notice Determine if the strategy needs to be rebalanced.
  /// @return needRebalance A boolean indicating if {rebalanceNoSwaps} should be called
  function needStrategyRebalance(
    PairBasedStrategyLogicLib.PairState storage pairState,
    ITetuConverter converter_,
    int24 tick
  ) external view returns (
    bool needRebalance,
    bool[2] memory fuseStatusChangedAB,
    PairBasedStrategyLib.FuseStatus[2] memory fuseStatusAB
  ) {
    if (pairState.isStablePool) {
      uint[2] memory prices;
      (prices[0], prices[1]) = ConverterStrategyBaseLib2.getOracleAssetsPrices(converter_, pairState.tokenA, pairState.tokenB);
      for (uint i = 0; i < 2; i = AppLib.uncheckedInc(i)) {
        (fuseStatusChangedAB[i], fuseStatusAB[i]) = PairBasedStrategyLib.needChangeFuseStatus(pairState.fuseAB[i], prices[i]);
      }
      needRebalance = fuseStatusChangedAB[0]
        || fuseStatusChangedAB[1]
        || (
          !PairBasedStrategyLib.isFuseTriggeredOn(fuseStatusAB[0])
          && !PairBasedStrategyLib.isFuseTriggeredOn(fuseStatusAB[1])
          && _needPoolRebalance(pairState, tick)
        );
    } else {
      needRebalance = _needPoolRebalance(pairState, tick);
    }

    return (needRebalance, fuseStatusChangedAB, fuseStatusAB); // hide warning
  }

  /// @notice Determine if the pool needs to be rebalanced.
  /// @return A boolean indicating if the pool needs to be rebalanced.
  function _needPoolRebalance(
    int24 tick,
    int24 lowerTick,
    int24 upperTick,
    int24 tickSpacing,
    int24 rebalanceTickRange
  ) internal pure returns (bool) {
    if (upperTick - lowerTick == tickSpacing) {
      return tick < lowerTick || tick >= upperTick;
    } else {
      int24 halfRange = (upperTick - lowerTick) / 2;
      int24 oldMedianTick = lowerTick + halfRange;
      return (tick > oldMedianTick)
        ? tick - oldMedianTick >= rebalanceTickRange
        : oldMedianTick - tick > rebalanceTickRange;
    }
  }

  function _needPoolRebalance(PairBasedStrategyLogicLib.PairState storage pairState, int24 tick) internal view returns (bool) {
    return _needPoolRebalance(
      tick,
      pairState.lowerTick,
      pairState.upperTick,
      pairState.tickSpacing,
      pairState.rebalanceTickRange
    );
  }
  //endregion ------------------------------------------------------- needStrategyRebalance
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@tetu_io/tetu-contracts-v2/contracts/interfaces/ISplitter.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/IConverterController.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/ITetuConverter.sol";
import "@tetu_io/tetu-converter/contracts/interfaces/IPriceOracle.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/IERC20Metadata.sol";
import "../../interfaces/IPairBasedStrategyReaderAccess.sol";
import "../../libs/AppLib.sol";
import "../ConverterStrategyBaseLib.sol";
import "./PairBasedStrategyLib.sol";

/// @notice Read raw values and calculate complex values related to UniswapV3ConverterStrategy
contract PairBasedStrategyReader {

  /// @notice Possible results of isWithdrawByAggCallRequired:
  ///         full withdraw is required (with propNotUnderlying = 0)
  uint constant public FULL_WITHDRAW_IS_REQUIRED = 1;
  /// @notice Possible results of isWithdrawByAggCallRequired:
  ///         rebalance of the debts is required with pool proportions (propNotUnderlying = type(uint).max)
  uint constant public DEBTS_REBALANCE_IS_REQUIRED = 2;

  struct GetLockedUnderlyingAmountLocal {
    ITetuConverter converter;
    address[] tokens;
    uint[] prices;
    uint[] decs;
    uint directDebt;
    uint directCollateral;
    uint reverseDebt;
    uint reverseCollateral;
    uint directDebtCost;
    uint reverseCollateralCost;
  }

  /// @notice Estimate amount of underlying locked in the strategy by TetuConverter
  /// @dev We cannot call strategy.getState() because of stack too deep problem
  /// @param strategy_ Instance of UniswapV3ConverterStrategy
  /// @return estimatedUnderlyingAmount Total locked amount recalculated to the underlying
  /// @return totalAssets strategy.totalAssets() - in terms of underlying
  function getLockedUnderlyingAmount(address strategy_) public view returns (
    uint estimatedUnderlyingAmount,
    uint totalAssets
  ) {
    GetLockedUnderlyingAmountLocal memory v;
    IPairBasedStrategyReaderAccess strategy = IPairBasedStrategyReaderAccess(strategy_);

    (address[] memory addr, , , ) = strategy.getDefaultState();
    address tokenA = addr[PairBasedStrategyLib.IDX_ADDR_DEFAULT_STATE_TOKEN_A];
    address tokenB = addr[PairBasedStrategyLib.IDX_ADDR_DEFAULT_STATE_TOKEN_B];

    v.converter = ITetuConverter(strategy.converter());

    v.tokens = new address[](2);
    v.tokens[0] = ISplitter(strategy.splitter()).asset(); // underlying
    v.tokens[1] = tokenA == v.tokens[0] ? tokenB : tokenA; // not underlying

    IPriceOracle priceOracle = AppLib._getPriceOracle(v.converter);
    (v.prices, v.decs) =  AppLib._getPricesAndDecs(priceOracle, v.tokens, 2);

    // direct borrow: underlying is collateral
    (v.directDebt, v.directCollateral) = v.converter.getDebtAmountStored(strategy_, v.tokens[0], v.tokens[1], true);

    // reverse borrow: underlying is borrowed asset
    (v.reverseDebt, v.reverseCollateral) = v.converter.getDebtAmountStored(strategy_, v.tokens[1], v.tokens[0], true);

    v.directDebtCost = v.directDebt * v.prices[1] * v.decs[0] / v.decs[1] / v.prices[0];
    v.reverseCollateralCost = v.reverseCollateral * v.prices[1] * v.decs[0] / v.decs[1] / v.prices[0];

    return (
      v.directCollateral + v.reverseCollateralCost > (v.directDebtCost + v.reverseDebt)
        ? v.directCollateral + v.reverseCollateralCost - v.directDebtCost - v.reverseDebt
        : 0,
      strategy.totalAssets()
    );
  }

  /// @notice Check if a call of withdrawByAgg is required
  /// @param strategy_ instance of IPairBasedStrategyReaderAccess
  /// @param allowedLockedAmountPercent [0...100]
  /// @return 0: it's not necessary to call withdrawByAgg
  ///         1: full withdraw is required (with propNotUnderlying = 0)
  ///         2: rebalance of the debts is required with pool proportions (propNotUnderlying = type(uint).max)
  function isWithdrawByAggCallRequired(address strategy_, uint allowedLockedAmountPercent) external view returns (
    uint
  ) {
    IPairBasedStrategyReaderAccess strategy = IPairBasedStrategyReaderAccess(strategy_);

    (, , uint[] memory nums, ) = strategy.getDefaultState();

    if (
      PairBasedStrategyLib.isFuseTriggeredOn(
        PairBasedStrategyLib.FuseStatus(nums[PairBasedStrategyLib.IDX_NUMS_DEFAULT_STATE_FUSE_STATUS_A])
      ) || PairBasedStrategyLib.isFuseTriggeredOn(
        PairBasedStrategyLib.FuseStatus(nums[PairBasedStrategyLib.IDX_NUMS_DEFAULT_STATE_FUSE_STATUS_B])
      )
    ) {
      // fuse is enabled: full withdraw to underlying is required
      if (nums[PairBasedStrategyLib.IDX_NUMS_DEFAULT_STATE_WITHDRAW_DONE] == 0) {
        return FULL_WITHDRAW_IS_REQUIRED;
      }
    } else {
      // locked amount is too high: partial withdraw  (with pool proportions) is required
      (uint estimatedUnderlyingAmount, uint totalAssets) = getLockedUnderlyingAmount(strategy_);
      uint percent = estimatedUnderlyingAmount * 100 / totalAssets;

      if (percent > allowedLockedAmountPercent) {
        return DEBTS_REBALANCE_IS_REQUIRED;
      }
    }

    return 0;
  }
}

contracts/strategies/StrategyProfitHolde.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@tetu_io/tetu-contracts-v2/contracts/interfaces/IERC20.sol";
import "@tetu_io/tetu-contracts-v2/contracts/openzeppelin/SafeERC20.sol";

contract StrategyProfitHolder {
  using SafeERC20 for IERC20;

  address public immutable strategy;
  address[] public tokens;

  constructor(address strategy_, address[] memory tokens_) {
    strategy = strategy_;
    tokens = tokens_;
    uint len = tokens_.length;
    for (uint i; i < len; ++i) {
      IERC20(tokens_[i]).safeApprove(strategy_, 2 ** 255);
    }
  }

  function addToken(address token) external {
    address _strategy = strategy;
    require(msg.sender == _strategy, "SPH: denied");
    uint len = tokens.length;
    for (uint i; i < len; ++i) {
      require(tokens[i] != token, "SPH: token exists");
    }
    tokens.push(token);
    IERC20(token).safeApprove(_strategy, 2 ** 255);
  }

  function tokensLength() external view returns (uint) {
    return tokens.length;
  }
}

