    // Keep the flywheel moving
    updateCompSupplyIndex(cTokenCollateral);
    distributeSupplierComp(cTokenCollateral, borrower);
    distributeSupplierComp(cTokenCollateral, liquidator);

    return uint(Error.NO_ERROR);
  }

