    uint planKind,
    uint propNotUnderlying18
  ) external returns (
    bool completed
  ) {
    return PairBasedStrategyLib.withdrawStep(
      converterLiquidator_,
      tokens,
      liquidationThresholds,
      tokenToSwap_,
      amountToSwap_,
      aggregator_,
      swapData_,
      useLiquidator_,
      planKind,
      propNotUnderlying18
    );
  }

  function _swap(
    IterationPlanLib.SwapRepayPlanParams memory p,
    PairBasedStrategyLib.SwapByAggParams memory aggParams,
    uint indexIn,
    uint indexOut,
    uint amountIn
  ) external returns (
    uint spentAmountIn,
    uint updatedPropNotUnderlying18
  ) {
    return PairBasedStrategyLib._swap(p, aggParams, indexIn, indexOut, amountIn);
  }

  function _getAmountToRepay2(
    IterationPlanLib.SwapRepayPlanParams memory p,
    uint indexCollateral,
    uint indexBorrow
  ) external view returns (
    uint amountToRepay,
    bool borrowInsteadRepay
  ) {
    return PairBasedStrategyLib._getAmountToRepay2(p, indexCollateral, indexBorrow);
  }

  function borrowToProportions(
    IterationPlanLib.SwapRepayPlanParams memory p,
    uint indexCollateral,
    uint indexBorrow
  ) external {
    return PairBasedStrategyLib.borrowToProportions(p, indexCollateral, indexBorrow);
  }


  //region ---------------------------------------------- IPoolProportionsProvider implementation

  uint[] internal valuesPropNotUnderlying18;
  /// @notice getPropNotUnderlying18() uses a value of balance of this token to detect what value should be returned
  address switchToken;
  /// @notice getPropNotUnderlying18 should return second value if balance of the switch token is equal to the given value
  uint switchTokenBalanceToSwitch;
  function setPropNotUnderlying18(
    uint[] memory valuesPropNotUnderlying18_,
    address switchToken_,
    uint switchTokenBalanceToSwitch_
  ) external {
    require(valuesPropNotUnderlying18_.length == 2, "Incorrect length array in setPropNotUnderlying18");
    valuesPropNotUnderlying18 = valuesPropNotUnderlying18_;
    switchToken = switchToken_;
    switchTokenBalanceToSwitch = switchTokenBalanceToSwitch_;
  }

  /// @notice Take next proportions of not-underlying from array
  ///         First call should return valuesPropNotUnderlying18[0], second call - valuesPropNotUnderlying18[1]
  ///         The function is view, so we cannot use counter. So we detect required value implicitly
  ///         through the value of balance of the {switchToken} of the sender.
  /// @return Proportion of the not-underlying [0...1e18]
  function getPropNotUnderlying18() external view returns (uint) {
    console.log("getPropNotUnderlying18.IERC20(switchToken).balanceOf(msg.sender)", IERC20(switchToken).balanceOf(msg.sender));
    uint ret = IERC20(switchToken).balanceOf(msg.sender) == switchTokenBalanceToSwitch
      ? valuesPropNotUnderlying18[1]
      : valuesPropNotUnderlying18[0];
    console.log("getPropNotUnderlying18.ret", ret);
    return ret;
  }
  //endregion ---------------------------------------------- IPoolProportionsProvider implementation

  function _extractProp(uint planKind, bytes memory planEntryData) external pure returns(uint propNotUnderlying18) {
    return PairBasedStrategyLib._extractProp(planKind, planEntryData);
  }

  //region ------------------------------------------------ Fuse functions
  PairBasedStrategyLib.FuseStateParams internal _fuse;
  function setUpFuse(uint status, uint[4] memory thresholds) external {
    _fuse.status = PairBasedStrategyLib.FuseStatus(status);
    _fuse.thresholds = thresholds;
  }
  function getFuseData() external view returns (uint status, uint[4] memory thresholds) {
    return (uint(_fuse.status), _fuse.thresholds);
  }

  function setFuseStatus(uint status) external {
    PairBasedStrategyLib.setFuseStatus(_fuse, PairBasedStrategyLib.FuseStatus(status));
  }

  function setFuseThresholds(uint[4] memory values) external {
    PairBasedStrategyLib.setFuseThresholds(_fuse, values);
  }

  function needChangeFuseStatus(PairBasedStrategyLib.FuseStateParams memory fuse_, uint price) external pure returns (
    bool needToChange,
    PairBasedStrategyLib.FuseStatus status
  ) {
    return PairBasedStrategyLib.needChangeFuseStatus(fuse_, price);
  }
  //endregion ------------------------------------------------ Fuse functions
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "../../strategies/pair/PairBasedStrategyLogicLib.sol";

contract PairBasedStrategyLogicLibFacade {
  mapping(address => uint) public liquidationThresholds;
  PairBasedStrategyLogicLib.PairState internal pairState;

  //region Auxiliary functions
  function setLiquidationThreshold(address asset, uint threshold) external {
    liquidationThresholds[asset] = threshold;
  }

  /// @param tickParams [tickSpacing, lowerTick, upperTick, rebalanceTickRange]
  function setPairState(
    address[2] memory tokensAB,
    address pool,
    bool isStablePool,
    int24[4] memory tickParams,
    bool depositorSwapTokens,
    uint128 totalLiquidity,
    address strategyProfitHolder,
    PairBasedStrategyLib.FuseStateParams[2] memory fuseAB,
    uint withdrawDone,
    uint lastRebalanceNoSwap
  ) external {
    pairState.tokenA = tokensAB[0];
    pairState.tokenB = tokensAB[1];

    pairState.pool = address(pool);
    pairState.isStablePool = isStablePool;

    pairState.tickSpacing = tickParams[0];
    pairState.lowerTick = tickParams[1];
    pairState.upperTick = tickParams[2];
    pairState.rebalanceTickRange = tickParams[3];

    pairState.depositorSwapTokens = depositorSwapTokens;
    pairState.totalLiquidity = totalLiquidity;
    pairState.strategyProfitHolder = strategyProfitHolder;
    pairState.fuseAB[0] = fuseAB[0];
    pairState.fuseAB[1] = fuseAB[1];

    pairState.withdrawDone = withdrawDone;
    pairState.lastRebalanceNoSwap = lastRebalanceNoSwap;
  }

  function getPairState() external view returns(
    address[2] memory tokensAB,
    address pool,
    bool isStablePool,
    int24[4] memory tickParams,
    bool depositorSwapTokens,
    uint128 totalLiquidity,
    address strategyProfitHolder,
    uint[10] memory fuseParams, // [fuse A status, 4 thresholds of fuse A, fuse B status, 4 thresholds of fuse B]
    uint withdrawDone
  ) {
    return (
      [pairState.tokenA, pairState.tokenB],
      pairState.pool,
      pairState.isStablePool,
      [pairState.tickSpacing, pairState.lowerTick, pairState.upperTick, pairState.rebalanceTickRange],
      pairState.depositorSwapTokens,
      pairState.totalLiquidity,
      pairState.strategyProfitHolder,
      [
        uint(pairState.fuseAB[0].status),
        pairState.fuseAB[0].thresholds[0],
        pairState.fuseAB[0].thresholds[1],
        pairState.fuseAB[0].thresholds[2],
        pairState.fuseAB[0].thresholds[3],
        uint(pairState.fuseAB[1].status),
        pairState.fuseAB[1].thresholds[0],
        pairState.fuseAB[1].thresholds[1],
        pairState.fuseAB[1].thresholds[2],
        pairState.fuseAB[1].thresholds[3]
      ],
      pairState.withdrawDone
    );
  }
  //endregion Auxiliary functions

  function _beforeDeposit(
    ITetuConverter tetuConverter_,
    uint amount_,
    address tokenA,
    address tokenB,
    bytes memory entryData
  ) external returns (uint[] memory tokenAmounts) {
    return PairBasedStrategyLogicLib._beforeDeposit(tetuConverter_, amount_, tokenA, tokenB, entryData, liquidationThresholds);
  }

  function initWithdrawLocal(
    address[2] calldata tokens,
    bytes memory planEntryData,
    address controller
  ) external view returns (
    PairBasedStrategyLogicLib.WithdrawLocal memory dest // for tests it's ok to return a struct
  ) {
    PairBasedStrategyLogicLib.initWithdrawLocal(dest, tokens, liquidationThresholds, planEntryData, controller);
    return dest;
  }

  function _needPoolRebalance(
    int24 tick,
    int24 lowerTick,
    int24 upperTick,
    int24 tickSpacing,
    int24 rebalanceTickRange
  ) external pure returns (bool) {
    return PairBasedStrategyLogicLib._needPoolRebalance(tick, lowerTick, upperTick, tickSpacing, rebalanceTickRange);
  }

  function needStrategyRebalance(
    ITetuConverter converter_,
    int24 tick
  ) external view returns (
    bool needRebalance,
    bool[2] memory fuseStatusChangedAB,
    PairBasedStrategyLib.FuseStatus[2] memory fuseStatusAB
  ) {
    return PairBasedStrategyLogicLib.needStrategyRebalance(pairState, converter_, tick);
  }

  function setInitialDepositorValues(
    address[4] calldata addr,
    int24[4] calldata tickData,
    bool isStablePool_,
    uint[4] calldata fuseThresholdsA,
    uint[4] calldata fuseThresholdsB
  ) external {
    PairBasedStrategyLogicLib.setInitialDepositorValues(pairState, addr, tickData, isStablePool_, fuseThresholdsA, fuseThresholdsB);
  }

  function updateFuseStatus(
    bool[2] calldata fuseStatusChangedAB,
    PairBasedStrategyLib.FuseStatus[2] calldata fuseStatusAB
  ) external {
    PairBasedStrategyLogicLib.updateFuseStatus(pairState, fuseStatusChangedAB, fuseStatusAB);
  }

  function getDefaultState() external view returns (
    address[] memory addr,
    int24[] memory tickData,
    uint[] memory nums,
    bool[] memory boolValues
  ) {
    return PairBasedStrategyLogicLib.getDefaultState(pairState);
  }
}

