// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@tetu_io/tetu-converter/contracts/interfaces/IPriceOracle.sol";
// import "hardhat/console.sol";

contract PriceOracleMock is IPriceOracle {
  /// how much 1 token costs in USD, decimals 18
  mapping(address => uint256) public prices;

  constructor(address[] memory assets, uint[] memory pricesInUSD) {
    _changePrices(assets, pricesInUSD);
  }
  ///////////////////////////////////////////////////////
  ///           Set up
  ///////////////////////////////////////////////////////
  function changePrices(address[] memory assets, uint[] memory pricesInUSD) external {
    _changePrices(assets, pricesInUSD);
  }

  function _changePrices(address[] memory assets, uint[] memory pricesInUSD) internal {
    require(assets.length == pricesInUSD.length, "wrong lengths");
    for (uint i = 0; i < assets.length; ++i) {
      prices[assets[i]] = pricesInUSD[i];
      // console.log("Price for %d is %d USD", assets[i], pricesInUSD[i]);
    }
  }

  ///////////////////////////////////////////////////////
  ///           IPriceOracle
  ///////////////////////////////////////////////////////

  /// @notice Return asset price in USD, decimals 18
  function getAssetPrice(address asset) external view override returns (uint256) {
    // console.log("PriceOracleMock.getAssetPrice", asset, prices[asset]);
    return prices[asset];
  }
}

contracts/test/PriceOracleImitator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@tetu_io/tetu-converter/contracts/interfaces/IPriceOracle.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/ITetuLiquidator.sol";
import "@tetu_io/tetu-contracts-v2/contracts/interfaces/IERC20Metadata.sol";

contract PriceOracleImitator is IPriceOracle {
  address public usdc;
  ITetuLiquidator liquidator;

  constructor(address usdc_, address liquidator_) {
    usdc = usdc_;
    liquidator = ITetuLiquidator(liquidator_);
  }

  /// @notice Return asset price in USD, decimals 18
  function getAssetPrice(address asset) external view override returns (uint256) {
    if (asset == usdc) {
      return 1e18 * 10000;
    }
    uint tokenInDecimals = IERC20Metadata(asset).decimals();
    uint tokenOutDecimals = IERC20Metadata(usdc).decimals();
    uint price = liquidator.getPrice(asset, usdc, 10 ** tokenInDecimals);

    if (tokenOutDecimals > 18) {
      price = price / 10 ** (tokenOutDecimals - 18);
    } else if (tokenOutDecimals < 18) {
      price = price * 10 ** (18 - tokenOutDecimals);
    }

    return price * 10000;
  }

  function setUsdc(address asset) external {
    usdc = asset;
  }
}

